INSERT INTO examples (id, title, meaning) VALUES (2079, 'you will answer to your director for any damage caused by your own men', 'anh phải chịu trách nhiệm với giám đốc về mọi thiệt hại do người của anh gây ra');
INSERT INTO examples (id, title, meaning) VALUES (2080, 'the bulldozer answered smoothly to the controls', 'chiếc xe ủi đất ngoan ngoãn tuân theo sự điều khiển của tài xế');
INSERT INTO examples (id, title, meaning) VALUES (2119, 'do you anticipate (meeting) any trouble?', 'anh có lường trước (là sẽ gặp) khó khăn hay không?');
INSERT INTO examples (id, title, meaning) VALUES (2120, 'we anticipate that demand is likely to increase', 'chúng tôi dự kiến là nhu cầu sẽ tăng lên');
INSERT INTO examples (id, title, meaning) VALUES (2121, 'she anticipates all her mother''s needs', 'cô ta đoán trước được tất cả những gì mẹ cần');
INSERT INTO examples (id, title, meaning) VALUES (2122, 'anticipating that it would soon be dark, they all took torches', 'biết trước là trời sắp tối, tất cả bọn họ đều mang theo đèn bấm');
INSERT INTO examples (id, title, meaning) VALUES (2123, 'a good general can anticipate what the enemy will do', 'một vị tướng tài có thể biết trước quân địch sẽ hành động như thế nào');
INSERT INTO examples (id, title, meaning) VALUES (2124, 'When Scott reached the South Pole, he found Amundsen had anticipated him', 'Khi Scott tiến đến Nam Cực, ông nhận ra rằng Amundsen đã đến trước mình rồi');
INSERT INTO examples (id, title, meaning) VALUES (2125, 'earlier explorers probably anticipated Columbus'' discovery of America', 'Khi Columbus phát hiện châu Mỹ, có lẽ đã có những nhà thám hiểm khác đến đó từ trước rồi');
INSERT INTO examples (id, title, meaning) VALUES (2126, 'we anticipated their (making a) complaint by writing a full report', 'Chúng tôi đã ngừa trước những lời khiếu nại của họ bằng cách viết một bản báo cáo đầy đủ');
INSERT INTO examples (id, title, meaning) VALUES (2127, 'to anticipate one''s income', 'dùng trước khoản thu nhập của mình (tiêu tiền trước khi được lãnh)');
INSERT INTO examples (id, title, meaning) VALUES (2273, 'to apologize to someone for something', 'xin lỗi ai về việc gì');
INSERT INTO examples (id, title, meaning) VALUES (2274, 'I must apologize for my late arrival', 'Tôi phải xin lỗi vì đến muộn');
INSERT INTO examples (id, title, meaning) VALUES (2275, 'apologize to your teacher!', 'hãy xin lỗi thầy anh đi!');
INSERT INTO examples (id, title, meaning) VALUES (2292, 'an appeal for help, food, extra staff', 'việc kêu gọi cứu giúp, cung cấp thức ăn, thêm nhân viên');
INSERT INTO examples (id, title, meaning) VALUES (2293, 'a charity appeal', 'lời kêu gọi cứu tế');
INSERT INTO examples (id, title, meaning) VALUES (2294, 'her eyes held a look of silent appeal', 'đôi mắt cô ấy chất chứa một lời kêu cứu thầm lặng');
INSERT INTO examples (id, title, meaning) VALUES (2295, 'does jazz hold any appeal for you?', 'nhạc jazz có hấp dẫn anh chút nào không?');
INSERT INTO examples (id, title, meaning) VALUES (2296, 'the new fashion soon lost its appeal', 'mốt mới chẳng bao lâu đã mất sức hấp dẫn');
INSERT INTO examples (id, title, meaning) VALUES (2297, 'to lodge an appeal', 'gửi đơn kháng cáo');
INSERT INTO examples (id, title, meaning) VALUES (2298, 'to have the right of appeal', 'có quyền chống án');
INSERT INTO examples (id, title, meaning) VALUES (2299, 'appeal court', 'tòa phúc thẩm');
INSERT INTO examples (id, title, meaning) VALUES (2300, 'I''m appealing on behalf of the famine victims', 'tôi khẩn khoản kêu gọi các ngài nhân danh các nạn nhân của vụ đói kém');
INSERT INTO examples (id, title, meaning) VALUES (2301, 'the police appealed to the crowd not to panic', 'cảnh sát kêu gọi đám đông đừng hoảng hốt');
INSERT INTO examples (id, title, meaning) VALUES (2302, 'the idea of camping has never appealed to me', 'ý nghĩ cắm trại chưa bao giờ là điều hấp dẫn tôi');
INSERT INTO examples (id, title, meaning) VALUES (2303, 'her sense of humour appealed to him enormously', 'sự nhạy bén của cô ta đối với chất trào phúng đã lôi cuốn anh ta hết sức mạnh mẽ');
INSERT INTO examples (id, title, meaning) VALUES (2304, 'I''ve decided not to appeal', 'tôi đã quyết định không chống án');
INSERT INTO examples (id, title, meaning) VALUES (2305, 'she appealed to the higher court against her sentence', 'bà ấy chống án lên tòa trên để phản đối quyết định kết tội bà ấy');
INSERT INTO examples (id, title, meaning) VALUES (2306, 'the whole side appealed for a catch', 'cả một bên yêu cầu cho bắt bóng');
INSERT INTO examples (id, title, meaning) VALUES (2307, 'the captain appealed against the light', 'người đội trưởng phản đối về ánh sáng (cho rằng ánh sáng không bảo đảm cho trận đấu tiếp tục)');
INSERT INTO examples (id, title, meaning) VALUES (2311, 'a ship appeared on the horizon', 'một con tàu xuất hiện ở chân trời');
INSERT INTO examples (id, title, meaning) VALUES (2312, 'a light appeared at the end of the tunnel', 'anh sáng lóe lên ở cuối đường hầm');
INSERT INTO examples (id, title, meaning) VALUES (2313, 'a rash has appeared on his body', 'chứng phát ban đã xuất hiện trên người cậu ấy');
INSERT INTO examples (id, title, meaning) VALUES (2314, 'he promised to be here at 4 o''clock but didn''t appear until six', 'anh ấy hứa có mặt ở đây lúc bốn giờ, nhưng mãi đến sáu giờ mới thấy đến');
INSERT INTO examples (id, title, meaning) VALUES (2315, 'I''ve to appear in court on a charge of drunken driving', 'tôi phải ra tòa vì bị cáo buộc là lái xe trong lúc say rượu');
INSERT INTO examples (id, title, meaning) VALUES (2316, 'to appear on the stage', 'ra sân khấu (diễn viên)');
INSERT INTO examples (id, title, meaning) VALUES (2317, 'to appear for the defendant / prosecution', 'ra tòa cãi cho bên bị/bên nguyên');
INSERT INTO examples (id, title, meaning) VALUES (2318, 'the book will appear in a week', 'một tuần nữa cuốn sách sẽ được xuất bản');
INSERT INTO examples (id, title, meaning) VALUES (2319, 'the news appeared next day on the front page', 'tin tức về việc này được in trên trang nhất ngày hôm sau');
INSERT INTO examples (id, title, meaning) VALUES (2320, 'the streets appear deserted', 'phố xá trông vắng tanh');
INSERT INTO examples (id, title, meaning) VALUES (2321, 'she appeared very confident', 'cô ấy có vẻ rất tự tin');
INSERT INTO examples (id, title, meaning) VALUES (2322, 'Are they reliable? - It appears not', 'Họ có đáng tin cậy hay không? - Hình như không');
INSERT INTO examples (id, title, meaning) VALUES (2323, 'don''t make him appear a fool', 'đừng để anh ta biến thành thằng ngốc');
INSERT INTO examples (id, title, meaning) VALUES (2324, 'she appears to have many friends', 'hóa ra cô ấy có nhiều bạn');
INSERT INTO examples (id, title, meaning) VALUES (2325, 'There appears to have / It appears that there has been a mistake', 'Hình như/té ra đã có sự nhầm lẫn');
INSERT INTO examples (id, title, meaning) VALUES (2326, 'You appear to have made / It appears that you have made a mistake', 'Xem ra anh đã nhầm');
INSERT INTO examples (id, title, meaning) VALUES (2327, 'Has he been found guilty? - It appears so / not', 'Có đúng là anh ta đã bị phát hiện là phạm tội hay không? - Hình như đúng/không');
INSERT INTO examples (id, title, meaning) VALUES (2328, 'he appears to have resigned', 'có lẽ/hình như ông ấy đã từ chức');
INSERT INTO examples (id, title, meaning) VALUES (2360, 'a kitchen full of electrical appliances', 'một nhà bếp đầy những đồ dùng bằng điện (máy giặt, máy rửa bát...)');
INSERT INTO examples (id, title, meaning) VALUES (2362, 'as the wages were low, there were few applicants for the job', 'do lương thấp, nên ít có người nộp đơn xin việc này');
INSERT INTO examples (id, title, meaning) VALUES (2363, 'the application of a plaster to a wound', 'sự đắp thuốc vào vết thương');
INSERT INTO examples (id, title, meaning) VALUES (2364, 'medicine for external application', 'thuốc dùng ngoài da');
INSERT INTO examples (id, title, meaning) VALUES (2365, 'a man of close application', 'một người rất chuyên cần');
INSERT INTO examples (id, title, meaning) VALUES (2366, 'application for a job', 'đơn xin việc làm');
INSERT INTO examples (id, title, meaning) VALUES (2367, 'to make an application to someone for something', 'gửi đơn cho ai để xin việc gì');
INSERT INTO examples (id, title, meaning) VALUES (2368, 'to put in an application', 'đệ đơn xin, gửi đơn xin');
INSERT INTO examples (id, title, meaning) VALUES (2369, 'application software', 'phần mềm ứng dụng');
INSERT INTO examples (id, title, meaning) VALUES (2372, 'to apply plaster to the wound', 'đắp thuốc vào vết thương');
INSERT INTO examples (id, title, meaning) VALUES (2373, 'to apply ointment sparingly', 'bôi thuốc mỡ một cách dè xẻn');
INSERT INTO examples (id, title, meaning) VALUES (2374, 'to apply the glue to both surfaces', 'phết hồ dán lên cả hai mặt');
INSERT INTO examples (id, title, meaning) VALUES (2375, 'to apply one''s ear to the wall', 'áp tai vào tường');
INSERT INTO examples (id, title, meaning) VALUES (2376, 'to apply the brakes hard', 'bóp phanh mạnh; đạp phanh mạnh');
INSERT INTO examples (id, title, meaning) VALUES (2377, 'to apply force, pressure, heat', 'gây ra một lực, sức ép, sức nóng');
INSERT INTO examples (id, title, meaning) VALUES (2378, 'to apply a new method', 'áp dụng một phương pháp mới');
INSERT INTO examples (id, title, meaning) VALUES (2379, 'to apply a law / rule / precept', 'áp dụng một đạo luật/quy tắc/lệnh');
INSERT INTO examples (id, title, meaning) VALUES (2380, 'to apply economic sanctions', 'áp dụng những chế tài về kinh tế');
INSERT INTO examples (id, title, meaning) VALUES (2381, 'I''d never apply the word " readable " to any of his books', 'tôi chẳng bao giờ dám dùng hai chữ "đọc được" cho bất cứ quyển sách nào của ông ấy');
INSERT INTO examples (id, title, meaning) VALUES (2382, 'the results of this research can be applied to new developments in technology', 'kết quả của công trình nghiên cứu này có thể áp dụng cho những phát triển mới về công nghệ');
INSERT INTO examples (id, title, meaning) VALUES (2383, 'to apply oneself to a task', 'chuyên tâm vào nhiệm vụ; hết lòng hết sức làm nhiệm vụ');
INSERT INTO examples (id, title, meaning) VALUES (2384, 'to apply one''s minds to finding a solution', 'chuyên tâm vào việc tìm ra một giải pháp');
INSERT INTO examples (id, title, meaning) VALUES (2385, 'you will only pass your exams if you really apply yourself (to your work)', 'anh chỉ có thể thi đỗ nếu thật sự chuyên tâm (vào việc học tập của mình)');
INSERT INTO examples (id, title, meaning) VALUES (2386, 'to apply for a post / job / passport / visa', 'xin một chức vụ/việc làm/hộ chiếu/thị thực');
INSERT INTO examples (id, title, meaning) VALUES (2387, 'you should apply immediately, in person or by letter', 'bạn nên đề nghị ngay, trực tiếp đến hoặc bằng thư');
INSERT INTO examples (id, title, meaning) VALUES (2388, 'to apply to the publishers for permission to reprint an extract', 'xin phép nhà xuất bản in lại một đoạn trích');
INSERT INTO examples (id, title, meaning) VALUES (2389, 'you must apply to the secretary', 'anh phải hỏi người thư ký');
INSERT INTO examples (id, title, meaning) VALUES (2390, 'these rules don''t always apply', 'những quy tắc này không phải lúc nào cũng ứng dụng được');
INSERT INTO examples (id, title, meaning) VALUES (2391, 'what I''ve said applies only to some of you', 'những điều tôi nói chỉ có hiệu lực đối với một số người trong các anh mà thôi');
INSERT INTO examples (id, title, meaning) VALUES (2392, 'they have appointed Mr Smith/ a new manager', 'họ đã bổ nhiệm ông Smith/một người quản lý mới');
INSERT INTO examples (id, title, meaning) VALUES (2393, 'he was appointed to the vacant post', 'ông ta được bổ nhiệm vào chức vụ đang bỏ trống');
INSERT INTO examples (id, title, meaning) VALUES (2394, 'who shall we appoint (as) chairperson?', 'chúng ta sẽ cử ai làm chủ toạ?');
INSERT INTO examples (id, title, meaning) VALUES (2395, 'we must appoint somebody to act as secretary', 'chúng ta phải cử người nào đó làm thư ký');
INSERT INTO examples (id, title, meaning) VALUES (2396, 'to appoint a committee', 'cử một ủy ban');
INSERT INTO examples (id, title, meaning) VALUES (2397, 'to appoint a date to meet / for a meeting', 'định ngày để gặp/để họp');
INSERT INTO examples (id, title, meaning) VALUES (2398, 'the time appointed for the meeting was 10.30', 'thời gian họp được ấn định vào 10 giờ 30');
INSERT INTO examples (id, title, meaning) VALUES (2399, 'the troops were badly appointed', 'quân đội được trang bị tồi');
INSERT INTO examples (id, title, meaning) VALUES (2401, 'appointment of someone to a post', 'sự bổ nhiệm ai vào một chức vụ');
INSERT INTO examples (id, title, meaning) VALUES (2402, 'his promotion to manager was a popular appointment', 'ông ấy được đề bạt làm giám đốc là một sự bổ nhiệm được lòng công chúng');
INSERT INTO examples (id, title, meaning) VALUES (2403, 'I''m looking for a permanent appointment', 'tôi đang tìm kiếm một công việc làm thường xuyên');
INSERT INTO examples (id, title, meaning) VALUES (2404, 'to make / fix an appointment with someone', 'hẹn gặp ai');
INSERT INTO examples (id, title, meaning) VALUES (2405, 'to break an appointment', 'thất hẹn, sai hẹn');
INSERT INTO examples (id, title, meaning) VALUES (2406, 'to keep an appointment', 'đúng hẹn');
INSERT INTO examples (id, title, meaning) VALUES (2407, 'I''ve a dental appointment at 3 pm', 'tôi có hẹn chữa răng vào ba giờ chiều');
INSERT INTO examples (id, title, meaning) VALUES (2408, 'interviews are by appointment only', 'ai có hẹn trước thì mới được phỏng vấn');
INSERT INTO examples (id, title, meaning) VALUES (2416, 'you can''t fully appreciate foreign literature in translation', 'anh không thể thưởng thức đầy đủ văn học nước ngoài qua bản dịch');
INSERT INTO examples (id, title, meaning) VALUES (2417, 'I really appreciate a good cup of tea', 'tôi thực sự thưởng thức một tách trà ngon');
INSERT INTO examples (id, title, meaning) VALUES (2418, 'to appreciate the value of something', 'đánh giá đúng cái gì; nhận thức được giá trị của cái gì');
INSERT INTO examples (id, title, meaning) VALUES (2419, 'to appreciate the difficulties of the situation', 'thấy rõ hoàn cảnh khó khăn, nhận thức được những khó khăn của tình hình');
INSERT INTO examples (id, title, meaning) VALUES (2420, 'to appreciate the necessity', 'thấy rõ được sự cần thiết');
INSERT INTO examples (id, title, meaning) VALUES (2421, 'I appreciate your problem, but I don''t think I can help you', 'tôi thông cảm với vấn đề của bạn, nhưng tôi không nghĩ rằng tôi giúp được bạn');
INSERT INTO examples (id, title, meaning) VALUES (2422, 'you don''t seem to appreciate how busy I am', 'hình như bạn không thông cảm tôi bận như thế nào');
INSERT INTO examples (id, title, meaning) VALUES (2423, 'your help was highly appreciated', 'sự giúp đỡ của bạn được đánh giá cao (nghĩa là chúng tôi biết ơn điều đó)');
INSERT INTO examples (id, title, meaning) VALUES (2424, 'I greatly appreciate your kindness', 'tôi rất cảm kích lòng tốt của anh');
INSERT INTO examples (id, title, meaning) VALUES (2425, 'local property has appreciated (in value) since they built the motorway nearby', 'tài sản ở địa phương đã tăng giá trị từ khi người ta làm xa lộ gần đó');
INSERT INTO examples (id, title, meaning) VALUES (2445, 'easy of approach', '(địa điểm) dễ đến; (người) dễ nói chuyện thân thiện, dễ gần gũi');
INSERT INTO examples (id, title, meaning) VALUES (2446, 'difficult of approach', '(địa điểm) khó đến; (người) khó nói chuyện thân thiện, khó gần gũi');
INSERT INTO examples (id, title, meaning) VALUES (2447, 'heavy footsteps signalled the teacher''s approach', 'những bước chân nặng nề báo hiệu là thầy giáo đang đến gần');
INSERT INTO examples (id, title, meaning) VALUES (2448, 'at her approach, the children ran off', 'thấy bà ta đến gần, lũ trẻ bỏ chạy mất');
INSERT INTO examples (id, title, meaning) VALUES (2449, 'some approach to truth', 'một cái gì gần như là chân lý');
INSERT INTO examples (id, title, meaning) VALUES (2450, 'that''s the nearest approach to a smile he ever makes', 'đó là cái nhêch mép gần giống nhất với nụ cười mà anh ta từng có');
INSERT INTO examples (id, title, meaning) VALUES (2451, 'the approach to a city', 'lối vào thành phố');
INSERT INTO examples (id, title, meaning) VALUES (2452, 'all the approaches to the palace were guarded by troops', 'mọi ngả đường dẫn đến lâu đài đều có quân lính canh gác');
INSERT INTO examples (id, title, meaning) VALUES (2453, 'a new approach to language teaching', 'phương pháp mới để giảng dạy ngôn ngữ');
INSERT INTO examples (id, title, meaning) VALUES (2454, 'approach velocity', 'vận tốc gần đúng');
INSERT INTO examples (id, title, meaning) VALUES (2455, 'the club has made an approach to a local business firm for sponsorship', 'câu lạc bộ đã tiếp xúc với một công ty kinh doanh địa phương để xin bảo trợ');
INSERT INTO examples (id, title, meaning) VALUES (2456, 'she resented his persistent approaches', 'cô ấy bực bội vì sự đeo đuổi dai dẳng của cậu ta');
INSERT INTO examples (id, title, meaning) VALUES (2457, 'winter is approaching', 'mùa đông đang đến');
INSERT INTO examples (id, title, meaning) VALUES (2458, 'the time is approaching when we must think about buying a new house', 'đã sắp đến lúc chúng tôi phải nghĩ đến việc mua một ngôi nhà mới');
INSERT INTO examples (id, title, meaning) VALUES (2459, 'as you approach the city, the first building you see is the church', 'khi bạn đến gần thành phố, tòa nhà đầu tiên bạn thấy là nhà thờ');
INSERT INTO examples (id, title, meaning) VALUES (2460, 'his eccentricity approaches madness', 'tính lập dị của anh ta gần như là điên');
INSERT INTO examples (id, title, meaning) VALUES (2461, 'to approach someone on some matter', 'thăm dò ý kiến ai về vấn đề gì');
INSERT INTO examples (id, title, meaning) VALUES (2462, 'to approach one''s bank manager for a loan', 'tiếp xúc với người quản lý ngân hàng để vay tiền');
INSERT INTO examples (id, title, meaning) VALUES (2463, 'to approach a witness with a bribe', 'đến người làm chứng kèm theo đồ đút lót');
INSERT INTO examples (id, title, meaning) VALUES (2464, 'I find him difficult to approach', 'tôi thấy anh ta là người khó gần gũi');
INSERT INTO examples (id, title, meaning) VALUES (2465, 'before trying to solve the puzzle, let''s consider the way to approach it', 'trước khi tìm cách giải câu đố, ta hãy xem xét cách tiếp cận nó đã');
INSERT INTO examples (id, title, meaning) VALUES (2467, 'it''s necessary for the homicide to incur a punishment appropriate to his cruelties', 'kẻ sát nhân cần phải nhận một hình phạt thích đáng với hành vi man rợ của y');
INSERT INTO examples (id, title, meaning) VALUES (2468, 'you''ll be informed of the details at the appropriate time', 'ông sẽ được thông báo chi tiết vào một thời điểm thích hợp');
INSERT INTO examples (id, title, meaning) VALUES (2469, 'he who appropriates others'' fortune in bad faith will be accused of theft', 'người nào có gian ý chiếm đoạt của người khác làm của riêng mình thì bị kết tội trộm');
INSERT INTO examples (id, title, meaning) VALUES (2475, 'that peace treaty has been approved by the national assembly', 'hòa ước ấy đã được sự phê chuẩn của quốc hội');
INSERT INTO examples (id, title, meaning) VALUES (2476, 'the auditors approved the company''s accounts', 'các kiểm định viên đã chấp thuận các chứng từ thanh toán của công ty');
