INSERT INTO examples (id, title, meaning) VALUES (2479, 'to approve of the proposal', 'tán thành lời đề nghị');
INSERT INTO examples (id, title, meaning) VALUES (2480, 'Lan doesn''t want to take her new boyfriend home in case her parents don''t approve of him', 'Lan không muốn đưa anh bạn mới quen về nhà nếu bố mẹ không ưng thuận anh ta');
