INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (1, '1', 'Cấu trúc chung của một câu trong tiếng anh', '{}', 'Cấu trúc chung của một câu trong tiếng anh, Cấu trúc chung của một câu trong tiếng anh', '{2, 3, 4, 5}', '[{"sub_title": "Cấu trúc chung của một câu trong tiếng Anh", "content": [{"c": "", "f": ["SUBJECT + VERB + COMPLEMENT(OBJECT: DIRECT | INDIRECT) + MODIFIER"], "e": [{"e": "John and I ate a pizza last night."}, {"e": "We studied \"present perfect\" last week."}, {"e": "He runs very fast."}, {"e": "I like walking."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (2, '1', 'Subject (chủ ngữ)', '{"Cấu trúc chung của một câu trong tiếng anh"}', 'Cấu trúc chung của một câu trong tiếng anh, Subject (chủ ngữ)', '{1, 3, 4, 5}', '[{"sub_title": "", "content": [{"c": "Chủ ngữ là chủ thể của hành động trong câu, thường đứng trước động từ (verb). Chủ ngữ thường là một danh từ (noun) hoặc một ngữ danh từ (noun phrase - một nhóm từ kết thúc bằng một danh từ, trong trường hợp này ngữ danh từ không được bắt đầu bằng một giới từ). Chủ ngữ thường đứng ở đầu câu và quyết định việc chia động từ.", "f": [], "e": []}, {"c": "Chú ý rằng mọi câu trong tiếng Anh đều có chủ ngữ (Trong câu mệnh lệnh, chủ ngữ được ngầm hiểu là người nghe. Ví dụ: “Don''t move!” = Đứng im!).", "f": [], "e": [{"e": "<span>Milk</span> is delicious. (một danh từ)"}, {"e": "<span>That new, red car</span> is mine. (một ngữ danh từ)"}]}, {"c": "Đôi khi câu không có chủ ngữ thật sự, trong trường hợp đó, It hoặc There đóng vai trò chủ ngữ giả.", "f": [], "e": [{"e": "<span>It</span> is a nice day today."}, {"e": "<span>There</span> is a fire in that building."}, {"e": "<span>There</span> were many students in the room."}, {"e": "<span>It</span> is the fact that the earth goes around the sun."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (3, '1', 'Verb (động từ)', '{"Cấu trúc chung của một câu trong tiếng anh"}', 'Cấu trúc chung của một câu trong tiếng anh, Verb (động từ)', '{1, 2, 4, 5}', '[{"sub_title": "", "content": [{"c": "Động từ là từ chỉ hành động hoặc trạng thái của chủ ngữ. Mọi câu đều phải có động từ. Nó có thể là một từ đơn hoặc một ngữ động từ. Ngữ động từ (verb phrase) là một nhóm từ gồm một hoặc nhiều trợ động từ (auxiliary) và một động từ chính.", "f": [], "e": [{"e": "I <span>love</span> you. (chỉ hành động)"}, {"e": "Chilli <span>is</span> hot. (chỉ trạng thái)"}, {"e": "I <span>have seen</span> the movie three times before. (auxiliary: have; main verb: seen)"}, {"e": "I <span>am going</span> to Sai Gon tomorrow. (auxiliary: am; main verb: going)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (4, '1', 'Complement (vị ngữ)', '{"Cấu trúc chung của một câu trong tiếng anh"}', 'Cấu trúc chung của một câu trong tiếng anh, Complement (vị ngữ)', '{1, 2, 3, 5}', '[{"sub_title": "", "content": [{"c": "Vị ngữ là từ hoặc cụm từ chỉ đối tượng tác động của chủ ngữ. Cũng giống như chủ ngữ, vị ngữ thường là danh từ hoặc ngữ danh từ không bắt đầu bằng giới từ, tuy nhiên vị ngữ thường đứng sau động từ. Không phải câu nào cũng có complement. Vị ngữ trả lời cho câu hỏi What? hoặc Whom?", "f": [], "e": [{"e": "John bought <span>a car</span> yesterday. (What did John buy?)"}, {"e": "Jill wants to drink <span>some water</span>. (What does he want to drink?)"}, {"e": "She saw <span>John</span> at the movie last night. (Whom did she see at the movie?)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (5, '1', 'Modifier (trạng từ)', '{"Cấu trúc chung của một câu trong tiếng anh"}', 'Cấu trúc chung của một câu trong tiếng anh, Modifier (trạng từ)', '{1, 2, 3, 4}', '[{"sub_title": "", "content": [{"c": "Trạng từ là từ hoặc cụm từ chỉ thời gian, địa điểm hoặc cách thức của hành động. Không phải câu nào cũng có trạng từ. Chúng thường là các cụm giới từ (prepositional phrase), phó từ (adverb) hoặc một cụm phó từ (adverbial phrase). Chúng trả lời câu hỏi When?, Where? hoặc How? Một cụm giới từ là một cụm từ bắt đầu bằng một giới từ và kết thúc bằng một danh từ (VD: in the morning, on the table,...). Nếu có nhiều trạng từ trong câu thì trạng từ chỉ thời gian thường đi sau cùng.", "f": [], "e": [{"e": "John bought a book <span>at the bookstore</span>. (Where did John buy a book?)"}, {"e": "She saw John <span>at the movie</span> <span>last night</span>. (Where did she see John? When did she see him?)"}, {"e": "She drives very fast. (How does she drive?)"}]}, {"c": "Chú ý rằng trạng từ thường đi sau vị ngữ nhưng không nhất thiết. Tuy nhiên trạng từ là cụm giới từ không được nằm giữa động từ và vị ngữ.", "f": [], "e": [{"e": "She <span>drove</span> on the street <span>her new car</span>. (Sai)"}, {"e": "She <span>drove</span> <span>her new car</span> on the street. (Đúng)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (6, '1', 'Danh từ đếm được và không đếm được (count noun/ non-count noun)', '{"Noun phrase (ngữ danh từ)"}', 'Noun phrase (ngữ danh từ), Danh từ đếm được và không đếm được (count noun/ non-count noun)', '{7, 8, 9, 10, 11}', '[{"sub_title": "Danh từ đếm được", "content": [{"c": "Là danh từ có thể dùng được với số đếm, do đó nó có 2 hình thái số ít và số nhiều. Nó dùng được với a hay với the. VD: one book, two books, ...", "f": [], "e": []}, {"c": "Một số danh từ đếm được có hình thái số nhiều đặc biệt. VD: person - people; child - children; tooth – teeth; foot – feet; mouse – mice ...", "f": [], "e": []}, {"c": "Một số danh từ đếm được có dạng số ít/ số nhiều như nhau chỉ phân biệt bằng có \"a\" và không có \"a\": an aircraft/ aircraft; a sheep/ sheep; a fish/ fish.", "f": [], "e": []}]}, {"sub_title": "Danh từ không đếm được", "content": [{"c": "Không dùng được với số đếm, do đó nó không có hình thái số ít, số nhiều. Nó không thể dùng được với a, còn the chỉ trong một số trường hợp đặc biệt. VD: milk (sữa). Bạn không thể nói \"one milk\", \"two milks\" ... (Một số vật chất không đếm được có thể được chứa trong các bình đựng, bao bì... đếm được. VD: one glass of milk - một cốc sữa).", "f": [], "e": []}, {"c": "Một số các danh từ không đếm được như food, meat, money, sand, water ... đôi khi được dùng như các danh từ số nhiều để chỉ các dạng, loại khác nhau của vật liệu đó.", "f": [], "e": [{"e": "This is <span>one of the foods</span> that my doctor wants me to eat."}]}, {"c": "Danh từ \"time\" nếu dùng với nghĩa là \"thời gian\" là không đếm được nhưng khi dùng với nghĩa là \"thời đại\" hay \"số lần\" là danh từ đếm được.", "f": [], "e": [{"e": "You have spent too much <span>time</span> on that homework. (thời gian, không đếm được)"}, {"e": "I have seen that movie three <span>times</span> before. (số lần, đếm được)"}]}]}, {"sub_title": "Các định ngữ dùng được với các danh từ đếm được", "content": [{"c": "a(n), the, some, any", "f": [], "e": []}, {"c": "this, that, these, those", "f": [], "e": []}, {"c": "none, one, two, three,...", "f": [], "e": []}, {"c": "many", "f": [], "e": []}, {"c": "a lot of", "f": [], "e": []}, {"c": "a [large / great] number of", "f": [], "e": []}, {"c": "(a) few", "f": [], "e": []}, {"c": "fewer... than", "f": [], "e": []}, {"c": "more\tthan", "f": [], "e": []}]}, {"sub_title": "Các định ngữ dùng được với các danh từ không đếm được", "content": [{"c": "the, some, any", "f": [], "e": []}, {"c": "this, that", "f": [], "e": []}, {"c": "None", "f": [], "e": []}, {"c": "much (thường dùng trong câu phủ định, câu hỏi)", "f": [], "e": []}, {"c": "a lot of", "f": [], "e": []}, {"c": "a large amount of", "f": [], "e": []}, {"c": "(a) little", "f": [], "e": []}, {"c": "less\tthan", "f": [], "e": []}, {"c": "more\tthan", "f": [], "e": []}]}, {"sub_title": "Một số từ không đếm được nên biết", "content": [{"c": "sand, food, meat, water, money, news, measles (bệnh sởi), soap, information, air, mumps (bệnh quai bị), economics, physics, mathematics, politics, homework", "f": [], "e": []}, {"c": "Note: <span>advertising</span> là danh từ không đếm được nhưng <span>advertisement</span> là danh từ đếm được, chỉ một quảng cáo cụ thể nào đó.", "f": [], "e": [{"e": "There are too many <span>advertisements</span> during TV shows."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (7, '1', 'Cách dùng quán từ không xác định "a" và "an"', '{"Noun phrase (ngữ danh từ)"}', 'Noun phrase (ngữ danh từ), Cách dùng quán từ không xác định "a" và "an"', '{6, 8, 9, 10, 11}', '[{"sub_title": "", "content": [{"c": "Dùng a hoặc an trước một danh từ số ít đếm được. Chúng có nghĩa là một. Chúng được dùng trong câu có tính khái quát hoặc đề cập đến một chủ thể chưa được đề cập từ trước.", "f": [], "e": [{"e": "<span>A ball</span> is round. (nghĩa chung, khái quát, chỉ tất cả các quả bóng)"}, {"e": "I saw <span>a boy</span> in the street. (chúng ta không biết cậu bé nào, chưa được đề cập trước đó)"}]}]}, {"sub_title": "Dùng “an” với", "content": [{"c": "Quán từ an được dùng trước từ bắt đầu bằng nguyên âm (trong cách phát âm, chứ không phải trong cách viết)", "f": [], "e": []}, {"c": "Các từ bắt đầu bằng các nguyên âm a, e, i, o: an aircraft, an empty glass, an object", "f": [], "e": []}, {"c": "Một số từ bắt đầu bằng u, y: an uncle, an umbrella", "f": [], "e": []}, {"c": "Một số từ bắt đầu bằng h câm: an heir, haft an hour", "f": [], "e": []}, {"c": "Các từ mở đầu bằng một chữ viết tắt: an S.O.S/ an M.P", "f": [], "e": []}]}, {"sub_title": "Dùng “a” với", "content": [{"c": "Dùng a trước các từ bắt đầu bằng một phụ âm. Chúng bao gồm các chữ cái còn lại và một số trường hợp bắt đầu bằng u, y, h. VD: a house, a university, a home party, a heavy load, a uniform, a union, a year income,...", "f": [], "e": []}, {"c": "Đứng trước một danh từ mở đầu bằng \"uni...\" phải dùng \"a\" (a university/ a uniform/ universal/ union) (Europe, eulogy (lời ca ngợi), euphemism (lối nói trại), eucalyptus (cây khuynh diệp)", "f": [], "e": []}, {"c": "Dùng trong các thành ngữ chỉ số lượng nhất định như: a lot of/a great deal of/a couple/a dozen.", "f": [], "e": []}, {"c": "Dùng trước những số đếm nhất định thường là hàng ngàn, hàng trăm như a/one hundred - a/one thousand.", "f": [], "e": []}, {"c": "Dùng trước \"half\" (một nửa) khi nó theo sau một đơn vị nguyên vẹn: a kilo and a half, hay khi nó đi ghép với một danh từ khác để chỉ nửa phần (khi viết có dấu gạch nối): a half - share, a half - holiday (ngày lễ chỉ nghỉ nửa ngày).", "f": [], "e": []}, {"c": "Dùng với các đơn vị phân số như 1/3 a/one third - 1/5 a /one fifth.", "f": [], "e": []}, {"c": "Dùng trong các thành ngữ chỉ giá cả, tốc độ, tỉ lệ: $5 a kilo, 60 kilometers an hour, 4 times a day.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (8, '1', 'Cách dùng quán từ xác định "the"', '{"Noun phrase (ngữ danh từ)"}', 'Noun phrase (ngữ danh từ), Cách dùng quán từ xác định "the"', '{6, 7, 9, 10, 11}', '[{"sub_title": "", "content": [{"c": "Dùng the trước một danh từ đã được xác định cụ thể về mặt tính chất, đặc điểm, vị trí hoặc đã được đề cập đến trước đó, hoặc những khái niệm phổ thông, ai cũng biết.", "f": [], "e": [{"e": "<span>The boy</span> in the corner is my friend. (Cả người nói và người nghe đều biết đó là cậu bé nào)"}, {"e": "<span>The earth</span> is round. (Chỉ có một trái đất, ai cũng biết)"}]}, {"c": "Với danh từ không đếm được, dùng the nếu nói đến một vật cụ thể, không dùng the nếu nói chung.", "f": [], "e": [{"e": "Sugar is sweet. (Chỉ các loại đường nói chung)"}, {"e": "<span>The sugar</span> on the table is from Cuba. (Cụ thể là đường ở trên bàn)"}]}, {"c": "Với danh từ đếm được số nhiều, khi chúng có nghĩa đại diện chung cho một lớp các vật cùng loại thì cũng không dùng the.", "f": [], "e": [{"e": "<span>Oranges</span> are green until they ripen. (Cam nói chung)"}, {"e": "<span>Athletes</span> should follow a well-balanced diet. (Vận động viên nói chung)"}]}]}, {"sub_title": "Sau đây là một số trường hợp thông dụng dùng The theo quy tắc trên", "content": [{"c": "", "f": ["The + danh từ + giới từ + danh từ"], "e": [{"e": "The girl in blue"}, {"e": "The Gulf of Mexico"}]}, {"c": "", "f": ["Dùng trước những tính từ so sánh bậc nhất hoặc only"], "e": [{"e": "The only way"}, {"e": "The best day"}]}, {"c": "", "f": ["Dùng cho những khoảng thời gian xác định (thập niên)"], "e": [{"e": "In the 1990s"}]}, {"c": "", "f": ["The + danh từ + đại từ quan hệ + mệnh đề phụ"], "e": [{"e": "The man to whom you have just spoken is the chairman."}]}, {"c": "", "f": ["The + danh từ số ít tượng trưng cho một nhóm thú vật hoặc đồ vật"], "e": [{"e": "The whale = whales (loài cá voi)"}, {"e": "the deep-freeze (thức ăn đông lạnh)"}]}, {"c": "", "f": ["Đối với man khi mang nghĩa \"loài người\" tuyệt đối không được dùng the"], "e": [{"e": "Since man lived on the earth ... (kể từ khi loài người sinh sống trên trái đất này)"}]}, {"c": "", "f": ["Dùng trước một danh từ số ít để chỉ một nhóm, một hạng người nhất định trong xã hội"], "e": [{"e": "The small shopkeeper (Giới chủ tiệm nhỏ)"}, {"e": "The top offcial (Giới quan chức cao cấp)"}]}, {"c": "", "f": ["The + adj: Tượng trưng cho một nhóm người, chúng không bao giờ được phép ở số nhiều nhưng được xem là các danh từ số nhiều. Do vậy động từ và đại từ đi cùng với chúng phải ở ngôi thứ 3 số nhiều: The old = The old people;"], "e": [{"e": "The old are often very hard in their moving"}]}, {"c": "", "f": ["The + tên gọi các đội hợp xướng/ dàn nhạc cổ điển/ ban nhạc phổ thông"], "e": [{"e": "The Back Choir/ The Philharmonique Philadelphia Orchestra/ The Beatles."}]}, {"c": "", "f": ["The + tên gọi các tờ báo (không tạp chí)/ tàu biển/ các khinh khí cầu"], "e": [{"e": "The Times/ The Titanic/ The Hindenberg"}]}, {"c": "", "f": ["The + họ của một gia đình ở số nhiều = gia đình nhà"], "e": [{"e": "The Smiths = Mr/ Mrs Smith and children"}]}, {"c": "", "f": ["Thông thường không dùng the trước tên riêng trừ trường hợp có nhiều người hoặc vật cùng tên và người nói muốn ám chỉ một người cụ thể trong số đó"], "e": [{"e": "There are three Sunsan Parkers in the telephone directory."}, {"e": "The Sunsan Parker that I know lives on the First Avenue."}]}, {"c": "", "f": ["Tương tự, không dùng \"the\" trước bữa ăn: breakfast, lunch, dinner"], "e": [{"e": "We ate breakfast at 8 am this morning."}]}, {"c": "", "f": ["Trừ khi muốn ám chỉ một bữa ăn cụ thể"], "e": [{"e": "The dinner that you invited me last week were delecious."}]}, {"c": "", "f": ["Không dùng \"the\" trước một số danh từ như home, bed, church, court, jail, prison, hospital, school, class, college, university v.v... khi nó đi với các động từ và giới từ chỉ chuyển động chỉ đi đến đó là mục đích chính hoặc ra khỏi đó cũng vì mục đích chính"], "e": [{"e": "Students go to school everyday"}, {"e": "The patient was released from hospital"}]}, {"c": "", "f": ["Nhưng nếu đến đó hoặc ra khỏi đó không vì mục đích chính thì dùng \"the\"."], "e": [{"e": "Students go to the school for a class party. The doctor left the hospital for lunch."}]}]}, {"sub_title": "Sử dụng \"the\" trong một số trường hợp điển hình", "content": [{"c": "Dùng trước tên các đại dương, sông ngòi, biển, vịnh và các cụm hồ (số nhiều)", "f": [], "e": [{"e": "The Red Sea, the Atlantic Ocean, the Persian Gufl, the Great Lakes"}]}, {"c": "Trước tên các dãy núi", "f": [], "e": [{"e": "The Rocky Mountains"}]}, {"c": "Trước tên những vật thể duy nhất trong vũ trụ hoặc trên thế giới:", "f": [], "e": [{"e": "The earth, the moon"}]}, {"c": "The schools, colleges, universities + of + danh từ riêng", "f": [], "e": [{"e": "The University of Florida"}]}, {"c": "The + số thứ tự + danh từ ", "f": [], "e": [{"e": "The third chapter."}]}, {"c": "Trước tên các cuộc chiến tranh khu vực với điều kiện tên khu vực đó phải được tính từ hoá", "f": [], "e": [{"e": "The Korean War (=> The Vietnamese economy)"}]}, {"c": "Trước tên các nước có hai từ trở lên (ngoại trừ Great Britain)", "f": [], "e": [{"e": "The United States, The Central African Republic"}]}, {"c": "Trước tên các nước được coi là một quần đảo hoặc một quần đảo", "f": [], "e": [{"e": "The Philipines, The Virgin Islands, The Hawaii"}]}, {"c": "Trước tên các tài liệu hoặc sự kiện lịch sử", "f": [], "e": [{"e": "The Constitution, The Magna Carta"}]}, {"c": "Trước tên các nhóm dân tộc thiểu số", "f": [], "e": [{"e": "the Indians"}]}, {"c": "Trước tên các môn học cụ thể", "f": [], "e": [{"e": "The Solid matter Physics"}]}, {"c": "Trước tên các nhạc cụ khi đề cập đến các nhạc cụ đó nói chung hoặc khi chơi các nhạc cụ đó.", "f": [], "e": [{"e": "The violin is difficult to play Who is that on the piano"}]}]}, {"sub_title": "Không sử dụng \"the\" trong một số trường hợp điển hình", "content": [{"c": "Trước tên một hồ", "f": [], "e": [{"e": "Lake Geneva"}]}, {"c": "Trước tên một ngọn núi", "f": [], "e": [{"e": "Mount Vesuvius"}]}, {"c": "Trước tên các hành tinh hoặc các chòm sao", "f": [], "e": [{"e": "Venus, Mars"}]}, {"c": "Trước tên các trường này nếu trước nó là một tên riêng", "f": [], "e": [{"e": "Stetson University"}]}, {"c": "Trước các danh từ đi cùng với một số đếm ", "f": [], "e": [{"e": "Chapter three, Word War One"}]}, {"c": "Trước tên các nước chỉ có một từ:", "f": [], "e": [{"e": "China, France, Venezuela, Vietnam"}]}, {"c": "Trước tên các nước mở đầu bằng New, một tính từ chỉ hướng:", "f": [], "e": [{"e": "New Zealand, North Korean, France"}]}, {"c": "Trước tên các lục địa, tỉnh, tiểu bang, thành phố, quận, huyện:", "f": [], "e": [{"e": "Europe, Florida"}]}, {"c": "Trước tên bất kì môn thể thao nào", "f": [], "e": [{"e": "baseball, basketball"}]}, {"c": "Trước các danh từ trừu tượng (trừ một số trường hợp đặc biệt):", "f": [], "e": [{"e": "freedom, happiness"}]}, {"c": "Trước tên các môn học nói chung ", "f": [], "e": [{"e": "mathematics"}]}, {"c": "Trước tên các ngày lễ, tết ", "f": [], "e": [{"e": "Christmas, Thanksgiving"}]}, {"c": "Trước tên các loại hình nhạc cụ trong các hình thức âm nhạc cụ thể (Jazz, Rock, classical music..)", "f": [], "e": [{"e": "To perform jazz on trumpet and piano"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (9, '1', 'Cách sử dụng another và other', '{"Noun phrase (ngữ danh từ)"}', 'Noun phrase (ngữ danh từ), Cách sử dụng another và other', '{6, 7, 8, 10, 11}', '[{"sub_title": "Hai từ này thường gây nhầm lẫn", "content": []}, {"sub_title": "Dùng với danh từ đếm được", "content": [{"c": "", "f": ["an + other + danh từ đếm được số ít = một cái nữa, một cái khác, một người nữa, một người khác (= one more)."], "e": [{"e": "another pencil = one more pencil"}]}, {"c": "", "f": ["the other + danh từ đếm được số ít = cái cuối cùng còn lại (của một bộ), người còn lại (của một nhóm), = last of the set."], "e": [{"e": "the other pencil = the last pencil present"}]}, {"c": "", "f": ["Other + danh từ đếm được số nhiều = mấy cái nữa, mấy cái khác, mấy người nữa, mấy người khác (= more of the set)."], "e": [{"e": "other pencils = some more pencils"}]}, {"c": "", "f": ["The other + danh từ đếm được số nhiều = những cái còn lại (của một bộ), những người còn lại (của một nhóm), = the rest of the set."], "e": [{"e": "the other pencils = all remaining pencils"}]}]}, {"sub_title": "Dùng với danh từ không đếm được", "content": [{"c": "", "f": ["Other + danh từ không đếm được = một chút nữa (= more of the set)."], "e": [{"e": "other water = some more water"}]}, {"c": "", "f": ["The other + danh từ không đếm được = chỗ còn sót lại."], "e": [{"e": "the other water = the remaining water"}]}, {"c": "Another và other là không xác định trong khi the other là xác định; nếu chủ ngữ là đã biết (được nhắc đến trước đó) thì ta có thể bỏ danh từ đi sau another hoặc other, chỉ cần dùng another hoặc other như một đại từ là đủ. Khi danh từ số nhiều bị lược bớt (trong cách nói tắt nêu trên) thì other trở thành others. Không bao giờ được dùng others + danh từ số nhiều", "f": [], "e": [{"e": "I Don ''t want this book. Please give me another. (another = any other book - not specific)"}, {"e": "I Don ''t want this book. Please give me the other. (the other = the other book, specific)"}, {"e": "This chemical is poisonous. Others are poisonous too. (others = the other chemicals, not specific)"}, {"e": "I Don ''t want these books. Please give me the others. (the others = the other books, specific)"}]}, {"c": "Trong một số trường hợp người ta dùng one hoặc ones đằng sau another hoặc other thay cho danh từ: ", "f": [], "e": [{"e": "I Don ''t want this book. Please give me another one."}, {"e": "I don''t want this book. Please give me the other one."}, {"e": "This chemical is poisonous. Other ones are poisonous too."}, {"e": "I don''t want these books. Please give me the other ones."}]}, {"c": "This hoặc that có thể dùng với one nhưng these và those không được dùng với ones, mặc dù cả 4 từ này đều có thể dùng thay cho danh từ (với vai trò là đại từ) khi không đi với one hoặc ones", "f": [], "e": [{"e": "I don''t want this book. I want that."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (10, '1', 'Cách sử dụng little, a little, few, a few', '{"Noun phrase (ngữ danh từ)"}', 'Noun phrase (ngữ danh từ), Cách sử dụng little, a little, few, a few', '{6, 7, 8, 9, 11}', '[{"sub_title": "", "content": [{"c": "Little + danh từ không đếm được: rất ít, không đủ để (có khuynh hướng phủ định)", "f": [], "e": [{"e": "I have little money, not enough to buy groceries."}]}, {"c": "A little + danh từ không đếm được: có một chút, đủ để", "f": [], "e": [{"e": "I have a little money, enough to buy groceries"}]}, {"c": "Few + danh từ đếm được số nhiều: có rất ít, không đủ để (có tính phủ định) ", "f": [], "e": [{"e": "I have few books, not enough for reference reading"}]}, {"c": "A few + danh từ đếm được số nhiều: có một chút, đủ để", "f": [], "e": [{"e": "I have a few records, enough for listening."}]}, {"c": "Trong một số trường hợp khi danh từ ở trên đã được nhắc đến thì ở phía dưới chỉ cần dùng little hoặc few như một đại từ là đủ (cũng giống như đối với other/another; this/that).", "f": [], "e": [{"e": "Are you ready in money. Yes, a little."}]}, {"c": "Quite a few + đếm được = Quite a bit + không đếm được = Quite a lot of + noun = rất nhiều.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (11, '1', 'Sở hữu cách', '{"Noun phrase (ngữ danh từ)"}', 'Noun phrase (ngữ danh từ), Sở hữu cách', '{6, 7, 8, 9, 10}', '[{"sub_title": "", "content": [{"c": "The noun''s + noun: Chỉ được dùng cho những danh từ chỉ người hoặc động vật, không dùng cho các đồ vật.", "f": [], "e": [{"e": "The student''s book"}, {"e": "The cat''s legs."}]}, {"c": "Đối với danh từ số nhiều đã có sẵn \"s\" ở đuôi chỉ cần dùng dấu phẩy", "f": [], "e": [{"e": "The students'' book."}]}, {"c": "Nhưng đối với những danh từ đổi số nhiều đặc biệt không \"s\" ở đuôi vẫn phải dùng đầy đủ dấu sở hữu cách.", "f": [], "e": [{"e": "The children''s toys"}, {"e": "The people''s willing"}]}, {"c": "Nếu có hai danh từ cùng đứng ở sở hữu cách thì danh từ nào đứng gần danh từ bị sở hữu nhất sẽ mang dấu sở hữu.", "f": [], "e": [{"e": "Paul and Peter''s room."}]}, {"c": "Đối với những tên riêng hoặc danh từ đã có sẵn \"s\" ở đuôi có thể chỉ cần dùng dấu phẩy và nhấn mạnh đuôi khi đọc hoặc dùng sở hữu cách và phải thay đổi cách đọc. Tên riêng không dùng \"the\" đằng trước.", "f": [], "e": [{"e": "The boss'' car = the boss ''s car [bosiz]"}, {"e": "Agnes'' house = Agnes ''s [siz] house."}]}, {"c": "Sở hữu cách cũng được dùng cho thời gian (năm, tháng, thập niên, thế kỉ) ", "f": [], "e": [{"e": "The 1990s'' events (những sự kiện của thập niên 90)"}, {"e": "The 21st century''s prospects."}]}, {"c": "Dùng cho các mùa trong năm trừ mùa xuân và mùa thu. Nếu dùng sở hữu cách cho hai mùa này thì người viết đã nhân cách hoá chúng. Ngày nay người ta dùng các mùa trong năm như một tính từ cho các danh từ đằng sau, ít dùng sở hữu cách.", "f": [], "e": [{"e": "The Autumn''s leaf (chiếc lá của nàng thu)"}]}, {"c": "Dùng cho tên các công ty lớn, các quốc gia The Rockerfeller''s oil products.", "f": [], "e": [{"e": "China''s food."}]}, {"c": "Đối với các cửa hiệu có nghề nghiệp đặc trưng chỉ cần dùng danh từ với dấu sở hữu. ", "f": [], "e": [{"e": "In a florist''s"}, {"e": "At a hairdresser''s"}]}, {"c": "Đặc biệt là các tiệm ăn với tên riêng", "f": [], "e": [{"e": "The Antonio''s"}]}, {"c": "Dùng trước một số danh từ bất động vật chỉ trong một số thành ngữ a stone''s throw from ...(Cách nơi đâu một tầm đá ném).", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (12, '1', 'Verb phrase (ngữ động từ)', '{}', 'Verb phrase (ngữ động từ), Verb phrase (ngữ động từ)', '{}', '[{"sub_title": "", "content": [{"c": "Như đã đề cập ở phần cấu trúc chung của câu, ngữ động từ tiếng Anh gồm có một động từ chính và một hoặc nhiêu trợ động từ. Động từ trong tiếng Anh chia làm 3 thì chính:", "f": [], "e": []}]}, {"sub_title": "Quá khứ (Past)", "content": [{"c": "", "f": [], "e": []}]}, {"sub_title": "Hiện tại (Present)", "content": [{"c": "", "f": [], "e": []}]}, {"sub_title": "Tương lai (Future)", "content": [{"c": "", "f": [], "e": []}, {"c": "Mỗi thì chính lại chia thành nhiều thì nhỏ để diễn đạt tính chính xác của hành động.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (13, '1', 'Simple present (thì hiện tại đơn)', '{"Present tenses (các thì hiện tại)"}', 'Present tenses (các thì hiện tại), Simple present (thì hiện tại đơn)', '{14, 15, 16, 17, 18}', '[{"sub_title": "", "content": [{"c": "Dùng để diễn đạt một hành động mang tính thường xuyên (regular action), theo thói quen (habitual action) hoặc hành động lặp đi lặp lại có tính qui luật.", "f": [], "e": [{"e": "I walk to school every day."}]}, {"c": "Khi chia động từ ở thì này, đối với ngôi thứ nhất (I), thứ hai (you) và thứ 3 số nhiều (they) động từ không phải chia, sử dụng động từ nguyên thể không có to như ở ví dụ nêu trên. Đối với ngôi thứ 3 số ít (he, she, it), phải có \"s\" ở sau động từ và âm đó phải được đọc lên:", "f": [], "e": [{"e": "He walks."}, {"e": "She watches TV"}]}, {"c": "Thường dùng thì hiện tại thường với một số các phó từ chỉ thời gian như today, present day, nowadays,... và với các phó từ chỉ tần suất như: always, sometimes, often, every + thời gian ...", "f": [], "e": []}, {"c": "Simple present thường không dùng để diễn đạt hành động đang xảy ra ở thời điểm hiện tại (now), ngoại trừ với các động từ thể hiện trạng thái (stative verb) như sau:", "f": [], "e": [{"e": "know,\tunderstand,\thave"}, {"e": "believe,\thate,\tneed"}, {"e": "hear,\tlove,\tappear"}, {"e": "see,\tlike,\tseem"}, {"e": "smell,\twant,\ttaste"}, {"e": "wish,\tsound,\town"}]}, {"c": "Các từ trong danh sách trên thường cũng không bao giờ xuất hiện trong thì tiếp diễn (hiện tại tiếp diễn, quá khứ tiếp diễn...).", "f": [], "e": []}, {"c": "Một số ví dụ khác về thì hiện tại thường:", "f": [], "e": [{"e": "They understand the problem now. (stative verb)"}, {"e": "He always swims in the evening. (habitual action)"}, {"e": "We want to leave now. (stative verb)"}, {"e": "The coffee tastes delicious. (stative verb)"}, {"e": "Your cough sounds bad. (stative verb)"}, {"e": "I walk to school every day. (habitual action)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (14, '1', 'Present progressive (thì hiện tại tiếp diễn)', '{"Present tenses (các thì hiện tại)"}', 'Present tenses (các thì hiện tại), Present progressive (thì hiện tại tiếp diễn)', '{13, 15, 16, 17, 18}', '[{"sub_title": "", "content": [{"c": "", "f": ["Subject + (am/is/are) + [verb-ing]"], "e": []}, {"c": "Dùng để diễn đạt một hành động xảy ra vào thời điểm hiện tại. Thời điểm này được xác định cụ thể bằng một số phó từ như: now, rightnow, at this moment.", "f": [], "e": []}, {"c": "Dùng thay thế cho thì tương lai gần, đặc biệt là trong văn nói.", "f": [], "e": [{"e": "The president is trying to contact his advisors now. (present time) We are flying to Paris next month. (future time)"}]}, {"c": "Các động từ trạng thái (stative verb) ở ví dụ sau không được chia ở thể tiếp diễn (bất cứ thì nào) khi chúng là những động từ tĩnh diễn đạt trạng thái cảm giác của hoạt động tinh thần hoặc tính chất của sự vật, sự việc.", "f": [], "e": [{"e": "know,\tunderstand,\thave"}, {"e": "believe,\thate,\tneed"}, {"e": "hear,\tlove,\tappear"}, {"e": "see,\tlike,\tseem"}, {"e": "smell,\twant,\ttaste"}, {"e": "wish,\tsound,\town"}]}, {"c": "Nhưng khi chúng quay sang hướng động từ hành động thì chúng lại được phép dùng ở thể tiếp diễn.", "f": [], "e": [{"e": "He has a lot of books."}, {"e": "He is having dinner now. (Động từ hành động: ăn tối)"}, {"e": "I think they will come in time."}, {"e": "I''m thinking of my test tomorrow. (Động từ hành động: Đang nghĩ về)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (15, '1', 'Present perfect (thì hiện tại hoàn thành)', '{"Present tenses (các thì hiện tại)"}', 'Present tenses (các thì hiện tại), Present perfect (thì hiện tại hoàn thành)', '{13, 14, 16, 17, 18}', '[{"sub_title": "", "content": [{"c": "", "f": ["Subject + (has/have) + [verb in past participle] + ..."], "e": []}]}, {"sub_title": "Thời hiện tại hoàn thành dùng để", "content": [{"c": "Diễn đạt một hành động xảy ra ở một thời điểm không xác định trong quá khứ.", "f": [], "e": [{"e": "John has traveled around the world. (We don''t know when)"}]}, {"c": "Chỉ một hành động xảy ra nhiều lần trong quá khứ.", "f": [], "e": [{"e": "George has seen this movie three time."}]}, {"c": "Một hành động bắt đầu diễn ra trong quá khứ và vẫn còn xảy ra ở hiện tại.", "f": [], "e": [{"e": "John has lived in that house for 20 years. (He still lives there.)"}, {"e": "John has lived in that house since 1984. (Giả sử hiện nay là 2004)"}]}]}, {"sub_title": "Thời hiện tại hoàn thành thường dược dùng với một số cụm từ chỉ thời gian như sau", "content": [{"c": "Dùng với now that... (giờ đây khi mà...)", "f": [], "e": [{"e": "Now that you have passed the TOEFL test successfully, you can apply for the scholarship."}]}, {"c": "Dùng với một số phó từ như till now, untill now, so far (cho đến giờ). Những cụm từ này có thể đứng đầu câu hoặc cuối câu.", "f": [], "e": [{"e": "So far the problem has not been resolved."}]}, {"c": "Dùng với recently, lately (gần đây) những cụm từ này có thể đứng đầu hoặc cuối câu. ", "f": [], "e": [{"e": "I have not seen him recently."}]}, {"c": "Dùng với before đứng ở cuối câu. ", "f": [], "e": [{"e": "I have seen him before."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (16, '1', 'Cách dùng since và for', '{"Present tenses (các thì hiện tại)"}', 'Present tenses (các thì hiện tại), Cách dùng since và for', '{13, 14, 15, 17, 18}', '[{"sub_title": "FOR + khoảng thời gian", "content": [{"c": "", "f": [], "e": [{"e": "for three days, for ten minutes, for twenty years ..."}]}]}, {"sub_title": "SINCE + thời điểm bắt đầu", "content": [{"c": "", "f": [], "e": [{"e": "since 1982, since January, ..."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (17, '1', 'Cách dùng already và yet', '{"Present tenses (các thì hiện tại)"}', 'Present tenses (các thì hiện tại), Cách dùng already và yet', '{13, 14, 15, 16, 18}', '[{"sub_title": "", "content": [{"c": "Already dùng trong câu khẳng định, already có thể đứng ngay sau have và cũng có thể đứng ở cuối câu.", "f": ["Subject + (have/has) + already + [verb in past participle] + ..."], "e": [{"e": "We have already written our reports."}, {"e": "We have written our reports already."}]}, {"c": "Yet dùng trong câu phủ định, câu nghi vấn. Yet thường xuyên đứng ở cuối câu.", "f": ["Subject + (have/has) + yet + [verb in infinitive] + ..."], "e": [{"e": "John has yet to learn the material"}, {"e": "John hasn''t learnt the material yet"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (18, '1', 'Present perfect progressive (thì hiện tại hoàn thành tiếp diễn)', '{"Present tenses (các thì hiện tại)"}', 'Present tenses (các thì hiện tại), Present perfect progressive (thì hiện tại hoàn thành tiếp diễn)', '{13, 14, 15, 16, 17}', '[{"sub_title": "", "content": [{"c": "", "f": ["Subject + (have/has) + been + [verb-ing] + ..."], "e": []}, {"c": "Chỉ đối với các hành động thộc loại (3) của thì hiện tại hoàn thành chúng ta mới có thể dùng thì hiện tại hoàn thành tiếp diễn.", "f": [], "e": [{"e": "John has been living in that house for 20 years."}, {"e": "John has lived in that house for 20 years."}]}]}, {"sub_title": "Phân biệt cách dùng giữa hai thì", "content": [{"c": "Present Perfect", "f": [], "e": []}, {"c": "Hành động đã chấm dứt ở hiện tại do đó đã có kết quả rõ rệt.", "f": [], "e": [{"e": "I''ve waited for you for half an hour. (and now I stop waiting because you didn''t come)."}]}, {"c": "Present Perfect Progressive", "f": [], "e": []}, {"c": "Hành động vẫn tiếp diễn ở hiện tại, có khả năng lan tới tương lai do đó không có kết quả rõ rệt.", "f": [], "e": [{"e": "I''ve been waiting for you for half an hour. (and now I''m still waiting, hoping that you''ll come)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (19, '1', 'Simple past (thì quá khứ đơn)', '{"Past tenses (các thì quá khứ)"}', 'Past tenses (các thì quá khứ), Simple past (thì quá khứ đơn)', '{20, 21, 22}', '[{"sub_title": "", "content": [{"c": "", "f": ["Subject + [verb in past tense (PI)] + ..."], "e": []}, {"c": "Dùng để diễn đạt một hành động đã xảy ra dứt điểm tại một thời gian xác định trong quá khứ (không còn diễn ra hoặc ảnh hưởng tới hiện tại). Thời điểm trong câu được xác định rõ rệt bằng một số các phó từ chỉ thời gian như: yesterday, at that moment, last week, ...", "f": [], "e": [{"e": "He went to Spain last year."}, {"e": "Bob bought a new bicyle yesterday."}, {"e": "Maria did her homework last night."}, {"e": "Mark washed the dishes after dinner."}, {"e": "We drove to the grocery store this afternoon."}, {"e": "George cooked dinner for his family Saturday night."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (20, '1', 'Past progresseive (thì quá khứ tiếp diễn)', '{"Past tenses (các thì quá khứ)"}', 'Past tenses (các thì quá khứ), Past progresseive (thì quá khứ tiếp diễn)', '{19, 21, 22}', '[{"content":[{"c":"","e":[],"f":["Subject + (was/were) + [verb-ing] + ..."]}],"sub_title":""},{"content":[{"c":"(1) một hành động đang xảy ra trong quá khứ thì bị một hành động khác “chen ngang” (khi đang... thì bỗng...). Trong trường hợp này, mẫu câu chung là:","e":[{"e":"I was watching TV when she came home."}],"f":["Subject 1 + Past progressive + when + Subject 2 + Simple past"]},{"c":"hoặc","e":[{"e":"When she came home, I was watching television."}],"f":["When + subject 1 + simple past, subject 2 + past progressive"]},{"c":"(2) Hai hành động cùng đồng thời xảy ra trong quá khứ. Trong trường hợp này, mẫu câu sau được áp dụng","e":[{"e":"Martha was watching television while John was reading a book."}],"f":["Subject 1 + past progressive + while + subject 2 + past progressive"]},{"c":"hoặc","e":[{"e":"While John was reading a book, Martha was watching television."}],"f":["While + subject 1 + past progressive + subject 2 + past progressive"]},{"c":"Cấu trúc sau đây cũng đôi khi được dùng nhưng không thông dụng bằng hai mẫu trên:","e":[{"e":"While John was reading a book, Martha watched television."}],"f":["While + subject 1 + past progressive + subject 2 + simple past"]},{"c":"(3) Một hành động đang xảy ra tại một thời điểm xác định trong quá khứ: ","e":[{"e":"Martha was watching TV at seven o’clock last night."},{"e":"What were you doing at one o’clock this afternoon?"},{"e":"Henry was eating a snack at midnight last night."}],"f":[]}],"sub_title":"Thì quá khứ tiếp diễn dùng để diễn đạt"}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (21, '1', 'Past perfect (thì quá khứ hoàn thành)', '{"Past tenses (các thì quá khứ)"}', 'Past tenses (các thì quá khứ), Past perfect (thì quá khứ hoàn thành)', '{19, 20, 22}', '[{"sub_title": "", "content": [{"c": "", "f": ["Subject + had + [verb in participle] + ..."], "e": []}]}, {"sub_title": "Thì quá khứ hoàn thành được dùng để diễn đạt", "content": [{"c": "(1) một hành động xảy ra trước một hành động khác trong quá khứ, trong câu thường có có 2 hành động", "f": [], "e": [{"e": "John had gone (hành động 1) to the stone before he went (hành động 2) home."}, {"e": "Jack told us yesterday that he had visited Englad in 1970"}, {"e": "Past perfect (1st acion) | simple past (2nd action) | now"}]}]}, {"sub_title": "Thì quá khứ hoàn thành thường được dùng với 3 phó từ chỉ thời gian là: after, before và when.", "content": [{"c": "", "f": ["Subject + simple past + after + subject + past perfect"], "e": [{"e": "John went home after he had gone to the store"}]}, {"c": "", "f": ["Subject + past perfect + before + subject + simple past"], "e": [{"e": "John had gone to the store before went home"}]}, {"c": "", "f": ["After + subject + past perfect + subject + simple past"], "e": [{"e": "After john had gone to the store, he went home"}]}, {"c": "", "f": ["Before + subject + simple past, subject + past perfect"], "e": [{"e": "Before John went home, he had gone to the store"}]}, {"c": "Phó từ when có thể được dùng thay cho after và before trong cả 4 mẫu trên mà không làm thay đổi ý nghĩa của câu. Chúng ta vẫn biết hành động nào xảy ra trước do có sử dụng quá khứ hoàn thành.", "f": [], "e": [{"e": "The police came when the robber had gone away."}]}, {"c": "(2) Một trạng thái đã tồn tại một thời gian trong quá khứ nhưng đã chấm dứt trước hiện tại. Trường hợp này tương tự trường hợp (3) đối với hiện tại hoàn thành nhưng trong trường hợp này không có liên hệ gì với hiện tại.", "f": [], "e": [{"e": "John had lived in New York for ten years before he moved to VietNam."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (22, '1', 'Past perfect progressive (thì quá khứ hoàn thành tiếp diễn)', '{"Past tenses (các thì quá khứ)"}', 'Past tenses (các thì quá khứ), Past perfect progressive (thì quá khứ hoàn thành tiếp diễn)', '{19, 20, 21}', '[{"sub_title": "", "content": [{"c": "", "f": ["Subject + had been + [verb-ing] + ..."], "e": []}, {"c": "Chỉ đối với các hành động thuộc nhóm (2) của thì quá khứ hoàn thành ta mới có thể dùng thì quá khứ hoàn thành tiếp diễn, với ý nghĩa của câu không thay đổi.", "f": [], "e": [{"e": "John had been living in New York for ten years before he moved to VietNam."}]}, {"c": "Lưu ý: Thì này ngày nay ít dùng, người ta thay thế nó bằng Past Perfect và chỉ dùng khi nào cần điễn đạt tính chính xác của hành động.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (23, '1', 'Simple future (thì tương lai đơn)', '{"Future tenses (các thì tương lai)"}', 'Future tenses (các thì tương lai), Simple future (thì tương lai đơn)', '{24, 25, 26}', '[{"sub_title": "", "content": [{"c": "", "f": ["Subject + (will/shall) + [verb in simple form] + ..."], "e": []}]}, {"sub_title": "Ngày nay ngữ pháp hiện đại, đặc biệt là ngữ pháp Mĩ chấp nhận việc dùng will cho tất cả các ngôi, còn shall chỉ dùng với các ngôi I, we trong một số trường hợp như sau:", "content": [{"c": "Đưa ra đề nghị một cách lịch sự", "f": [], "e": [{"e": "Shall I take you coat?"}]}, {"c": "Dùng để mời người khác một cách lịch sự", "f": [], "e": [{"e": "Shall we go out for lunch?"}]}, {"c": "Dùng để ngã giá trong khi mặc cả, mua bán", "f": [], "e": [{"e": "Shall we say: $50"}]}, {"c": "Thường được dùng với 1 văn bản mang tính pháp qui buộc các bên phải thi hành điều khoản trong văn bản:", "f": [], "e": [{"e": "All the students shall be responsible for proper execution of the dorm rule."}]}, {"c": "Trong tiếng Anh bình dân , người ta thay shall = must ở dạng câu này. Nó dùng để diễn đạt một hành động sẽ xảy ra ở một thời điểm nhất định trong tương lai nhưng không xác định cụ thể. Thường dùng với một số phó từ chỉ thời gian như tomorrow, next + time, in the future, in future, from now on.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (24, '1', 'Near future (tương lai gần)', '{"Future tenses (các thì tương lai)"}', 'Future tenses (các thì tương lai), Near future (tương lai gần)', '{23, 25, 26}', '[{"sub_title": "", "content": [{"c": "", "f": ["Subject + (am/is) + going to + [verb in simple form] + ..."], "e": []}, {"c": "Diễn đạt một hành động sẽ xảy ra trong tương lai gần, thường dùng với các phó từ dưới dạng: In a moment (lát nữa), at 2 o''clock this afternoon....", "f": [], "e": [{"e": "We are going to have a reception in a moment"}]}, {"c": "Nó chỉ 1 việc chắc chắn sẽ phải xảy ra theo như dự tính cho dù thời gian là tương lai xa.", "f": [], "e": [{"e": "We are going to take a TOEFL test next year."}]}, {"c": "Ngày nay người ta thường dùng present progressive", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (25, '1', 'Future progressive (thì tương lai tiếp diễn):', '{"Future tenses (các thì tương lai)"}', 'Future tenses (các thì tương lai), Future progressive (thì tương lai tiếp diễn):', '{23, 24, 26}', '[{"sub_title": "", "content": [{"c": "", "f": ["Subject + (will/shall) + be + [verb-ing] + ..."], "e": []}, {"c": "Dùng để diễn đạt một hành động sẽ xảy ra vào một thời điểm nhất định trong tương lai. ", "f": [], "e": [{"e": "At 8:00 am tomorrow morning we will be attending the lecture."}, {"e": "Good luck with the exam! We will be thinking of you."}]}, {"c": "Dùng kết hợp với present progressive khác để diễn đạt hai hành động đang song song xảy ra. Một ở hiện tại, còn một ở tương lai.", "f": [], "e": [{"e": "Now we are learning English here, but by this time tomorrow we will be attending the meeting at the office."}]}, {"c": "Được dùng để đề cập đến các sự kiện tương lai đã được xác định hoặc quyết định (không mang ý nghĩa tiếp diễn).", "f": [], "e": [{"e": "Professor Baxter will be giving another lecture on Roman glass-making at the same time next week."}]}, {"c": "Hoặc những sự kiện được mong đợi là sẽ xảy ra theo một tiến trình thường lệ (nhưng không diễn đạt ý định của cá nhân người nói).", "f": [], "e": [{"e": "You will be hearing from my solicitor."}, {"e": "I will be seeing you one of these days, I expect."}]}, {"c": "Dự đoán cho tương lai", "f": [], "e": [{"e": "Don''t phone now, they will be having dinner."}]}, {"c": "Diễn đạt lời đề nghị nhã nhặn muốn biết về kế hoạch của người khác", "f": [], "e": [{"e": "Will you be staying in here this evening? (ông có dự định ở lại đây tối nay chứ ạ)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (26, '1', 'Future perfect (thì tương lai hoàn thành)', '{"Future tenses (các thì tương lai)"}', 'Future tenses (các thì tương lai), Future perfect (thì tương lai hoàn thành)', '{23, 24, 25}', '[{"sub_title": "", "content": [{"c": "", "f": ["Subject + (will/shall) + have + [verb in participle] + ..."], "e": []}, {"c": "Dùng để chỉ một hành động sẽ phải được hoàn tất ở vào một thời điểm nhất định trong tương lai. Nó thường được dùng với trạng từ chỉ thời gian dưới dạng: by the end of\t, by the time + sentence", "f": [], "e": [{"e": "We will have accomplished the English grammar course by the end of next week."}, {"e": "By the time human being migrates to the moon, most of the people alive today will have died."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (27, '2', 'Sự hòa hợp giữa chủ ngữ và động từ', '{}', 'Sự hòa hợp giữa chủ ngữ và động từ, Sự hòa hợp giữa chủ ngữ và động từ', '{28, 29, 30, 31, 32, 33, 34, 35, 36}', '[{"sub_title": "", "content": [{"c": "Trong một câu tiếng Anh, chủ ngữ và động từ phải phù hợp với nhau về ngôi và số (số ít hay số nhiều)", "f": [], "e": [{"e": "The worker works very well. (số ít\t- số ít)"}, {"e": "The workers work very well. (số nhiều - số nhiều)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (28, '2', 'Các trường hợp chủ ngữ đứng tách khỏi động từ', '{"Sự hòa hợp giữa chủ ngữ và động từ"}', 'Sự hòa hợp giữa chủ ngữ và động từ, Các trường hợp chủ ngữ đứng tách khỏi động từ', '{27, 29, 30, 31, 32, 33, 34, 35, 36}', '[{"sub_title": "", "content": [{"c": "Trong câu tiếng Anh, có nhiều trường hợp rất khó xác định được đâu là chủ ngữ của câu do chủ ngữ và động từ không đi liền với nhau.", "f": [], "e": [{"e": "The boys in the room are playing chess."}]}, {"c": "Thông thường trong các trường hợp đó, một ngữ giới từ (một giới từ mở đầu và các danh từ theo sau – in the room) thường nằm giữa chủ ngữ và động từ. Các ngữ giới từ này không ảnh hưởng đến việc chia động từ.", "f": [], "e": [{"e": "The study of languages is very interesting."}, {"e": "Serveral theories on this subject have been proposed."}, {"e": "The view of these disciplines varies from time to time."}, {"e": "The danger of forest fires is not to be taken lightly."}]}, {"c": "Chú ý rằng trong các ví dụ trên các danh từ nằm trong ngữ giới từ đều trái ngược với chủ ngữ về số ít / số nhiều nhưng động từ luôn được chia theo chủ ngữ chính.", "f": [], "e": []}, {"c": "Các cụm từ sau cùng với các danh từ đi theo sau nó tạo nên hiện tường đồng chủ ngữ. Chúng đứng giữa chủ ngữ và động từ, phân tách khỏi 2 thành phần đó bởi dấu phảy. Chúng cũng không có ảnh hưởng gì đến việc chia động từ.", "f": [], "e": [{"e": "Together with,\talong with,\taccompanied by,\tas well as"}, {"e": "Mary, along with her manager and some friends, is going to a party tonight."}, {"e": "Mr. Robbins, accompanied by her wife and children, is arriving tonight."}]}, {"c": "Nếu 2 danh từ làm chủ ngữ nối với nhau bằng and thì động từ phải chia ở ngôi thứ 3 số nhiều (tương đương với they)", "f": [], "e": [{"e": "Mary and her manager are going to a party tonight."}]}, {"c": "Nhưng nếu 2 đồng chủ ngữ nối với nhau bằng or thì động từ phải chia theo danh từ đứng sau or. Nếu danh từ đó là số ít thì động từ phải chia số ít và ngược lại.", "f": [], "e": [{"e": "Mary or her manager is going to answer the press interview."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (29, '2', 'Các từ luôn đi với danh từ hoặc đại từ số ít', '{"Sự hòa hợp giữa chủ ngữ và động từ"}', 'Sự hòa hợp giữa chủ ngữ và động từ, Các từ luôn đi với danh từ hoặc đại từ số ít', '{27, 28, 30, 31, 32, 33, 34, 35, 36}', '[{"sub_title": "Đó là các danh từ ở ví dụ sau (còn gọi là các đại từ phiếm chỉ).", "content": [{"c": "any + singular noun", "f": [], "e": [{"e": "anybody, anyone, anything"}]}, {"c": "no + singular noun", "f": [], "e": [{"e": "nobody, no one, nothing"}]}, {"c": "every + singular ", "f": [], "e": [{"e": "noun everybody, everyone, everything"}, {"e": "each, either *, neither *"}]}, {"c": "*Either và neither là số ít nếu chúng không đi với or hoặc nor. Either (có nghĩa 1 trong 2) chỉ dùng cho 2 người hoặc 2 vật. Nếu 3 người (vật) trở lên phải dùng any. Neither (không một ai trong hai) chỉ dùng cho 2 người, 2 vật. Nếu 3 người (vật) trở lên dùng not any.", "f": [], "e": [{"e": "Everybody who wants to buy a ticket should be in this line."}, {"e": "Something is in my eye."}, {"e": "Anybody who has lost his ticket should report to the desk."}, {"e": "Neither of his pens is able to be used."}, {"e": "If either of you takes a vacation now, we will not be able to finish the work."}, {"e": "No problem is harder than this one."}, {"e": "Nobody works harder than John does."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (30, '2', 'Cách sử dụng none và no', '{"Sự hòa hợp giữa chủ ngữ và động từ"}', 'Sự hòa hợp giữa chủ ngữ và động từ, Cách sử dụng none và no', '{27, 28, 29, 31, 32, 33, 34, 35, 36}', '[{"sub_title": "", "content": [{"c": "None và No đều dùng được với cả danh từ số ít và số nhiều.", "f": [], "e": []}, {"c": "Nếu sau None of the là một danh từ không đếm được thì động từ phải ở ngôi thứ 3 số ít. Nếu sau nó là một danh từ số nhiều thì động từ phải chia ở ngôi thứ 3 số nhiều.", "f": ["None of the + non-count noun + singular verb", "None of the + plural count noun + plural verb"], "e": [{"e": "None of the counterfeit money has been found."}, {"e": "None of the students have finished the exam yet."}]}, {"c": "Nếu sau No là một danh từ đếm được số ít hoặc không đếm được thì động từ phải ở ngôi thứ 3 số ít. Nếu sau nó là một danh từ số nhiều thì động từ phải ở ngôi thứ 3 số nhiều", "f": ["No + (singular noun / non-count noun) + singular verb", "No + plural noun + plural verb"], "e": [{"e": "No example is relevant to this case."}, {"e": "No examples are relevant to this case."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (31, '2', 'Cách sử dụng cấu trúc either... or (hoặc...hoặc) và neither... nor (không...mà cũng không)', '{"Sự hòa hợp giữa chủ ngữ và động từ"}', 'Sự hòa hợp giữa chủ ngữ và động từ, Cách sử dụng cấu trúc either... or (hoặc...hoặc) và neither... nor (không...mà cũng không)', '{27, 28, 29, 30, 32, 33, 34, 35, 36}', '[{"sub_title": "", "content": [{"c": "Điều cần lưu ý nhất khi sử dụng cấu trúc này là động từ phải chia theo danh từ đi sau or hoặc nor. Nếu danh từ đó là số ít thì động từ chia ở ngôi thứ 3 số ít và ngược lại. Nếu or hoặc nor xuất hiện một mình (không có either hoặc neither) thì cũng áp dụng quy tắc tương tự (như đã đề cập ở phần trên)", "f": ["(neither/either) + noun + (nor/or) + plural noun + plural verb", "(neither/either) + noun + (nor/or) + singular noun + singular verb"], "e": [{"e": "Neither John nor his friends are going to the beach today."}, {"e": "Either John or his friends are going to the beach today."}, {"e": "Neither the boys nor Carmen has seen this movie before."}, {"e": "Either John or Bill is going to the beach today."}, {"e": "Neither the director nor the secretary wants to leave yet."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (32, '2', 'V-ing làm chủ ngữ', '{"Sự hòa hợp giữa chủ ngữ và động từ"}', 'Sự hòa hợp giữa chủ ngữ và động từ, V-ing làm chủ ngữ', '{27, 28, 29, 30, 31, 33, 34, 35, 36}', '[{"sub_title": "", "content": [{"c": "Khi V-ing dùng làm chủ ngữ thì động từ cũng phải chia ở ngôi thứ 3 số ít.", "f": [], "e": [{"e": "Knowing her has made him what he is."}, {"e": "Not studying has caused him many problems."}, {"e": "Washing with special cream is recommended for scalp infection."}, {"e": "Being cordial is one of his greatest assets."}, {"e": "Writing many letters makes her happy."}]}, {"c": "Người ta sẽ dùng V-ing khi muốn diễn đạt 1 hành động cụ thể xảy ra nhất thời nhưng khi muốn diễn đạt bản chất của sự vật, sự việc thì phải dùng danh từ", "f": [], "e": [{"e": "Dieting is very popular today."}, {"e": "Diet is for those who suffer from a cerain disease."}]}, {"c": "Động từ nguyên thể cũng có thể dùng làm chủ ngữ và động từ sau nó sẽ chia ở ngôi thứ 3 số ít. Nhưng người ta thường dùng chủ ngữ giả it để mở đầu câu.", "f": [], "e": [{"e": "To find the book is necessary for him = It is necessary for him to find the book."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (33, '2', 'Các danh từ tập thể', '{"Sự hòa hợp giữa chủ ngữ và động từ"}', 'Sự hòa hợp giữa chủ ngữ và động từ, Các danh từ tập thể', '{27, 28, 29, 30, 31, 32, 34, 35, 36}', '[{"sub_title": "Các danh từ ở ví dụ sau dùng để chỉ 1 nhóm người hoặc 1 tổ chức nhưng trên thực tế chúng là những danh từ số ít, do vậy các đại từ và động từ theo sau chúng cũng ở ngôi thứ 3 số ít", "content": [{"c": "Congress,\tOrganization,\tGovernment", "f": [], "e": []}, {"c": "family,\tteam,\tjury", "f": [], "e": []}, {"c": "group,\tarmy,\tmajority*", "f": [], "e": []}, {"c": "ommittee,\tclub,\tminority", "f": [], "e": []}, {"c": "class,\tcrowd,\tpublic", "f": [], "e": [{"e": "The committee has met, and it has rejected the proposal."}, {"e": "The family was elated by the news."}, {"e": "The crowd was wild with excitement"}, {"e": "Congress has initiated a new plan to combat inflation."}, {"e": "The organization has lot many members this year."}, {"e": "Our team is going to win the game."}]}, {"c": "Tuy nhiên nếu các thành viên trong nhóm đang hoạt động riêng rẽ, động từ sẽ chia ở ngôi thứ 3 số nhiều: Congress votes for the bill. (Quốc hội bỏ phiếu cho dự luật, gồm tất cả mọi người)", "f": [], "e": [{"e": "Congress are discussing about the bill. (Quốc hội đang tranh luận về dự luật, tức là một số tán thành, một số phản đối. Thi TOEFL không bắt lỗi này)."}]}, {"c": "Danh từ majority được dùng tuỳ theo thành phần sau nó để chia động từ The majority + singular verb", "f": ["The majority of the + plural noun + plural verb"], "e": [{"e": "The majority believes that we are in no danger."}, {"e": "The majority of the students believe him to be innocent."}]}, {"c": "", "f": ["The police/the sheep/the fish + plural verb."], "e": [{"e": "The sheep are breaking away"}, {"e": "The police come only to see the dead bodies and a ruin in the bank"}]}, {"c": "", "f": ["A couple + singular verb"], "e": [{"e": "A couple is walking on the path"}]}, {"c": "", "f": ["The couple + plural verb"], "e": [{"e": "The couple are racing their horses through the meadow."}]}, {"c": "Các cụm từ ở nhóm sau chỉ một nhóm động vật hoặc gia súc. Cho dù sau giới từ of là danh từ số nhiều thì động từ vẫn chia theo ngôi chủ ngữ chính – ngôi thứ 3 số ít: flock of birds/ sheep, school of fish, herd of cattle, pride of lions, pack of dogs", "f": [], "e": [{"e": "The flock of birds is circling overhead. The herd of casttle is breaking away."}, {"e": "A school of fish is being attacked by sharks."}]}, {"c": "Tất cả các danh từ tập thể chỉ thời gian, tiền bạc, số đo, ... khi được đề cập đến như một thể thống nhất thì đều được xem là một danh từ số ít. Do đó các động từ và đại từ theo sau chúng phải ở ngôi thứ 3 số ít.", "f": [], "e": [{"e": "Twenty-five dollars is too much for the meal."}, {"e": "Fifty minutes isn’t enough time to finish this test"}, {"e": "Twenty dollars is all I can afford to pay for that radio."}, {"e": "Two miles is too much to run in one day."}, {"e": "He has contributed $50, and now he wants to contribute another fifty."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (34, '2', 'Cách sử dụng a number of, the number of', '{"Sự hòa hợp giữa chủ ngữ và động từ"}', 'Sự hòa hợp giữa chủ ngữ và động từ, Cách sử dụng a number of, the number of', '{27, 28, 29, 30, 31, 32, 33, 35, 36}', '[{"sub_title": "", "content": [{"c": "A number of = “Một số những ...”, đi với danh từ số nhiều, động từ chia ở số nhiều.", "f": ["A number of + plural noun + plural verb"], "e": [{"e": "A number of students are going to the class picnic (Một số sinh viên sẽ đi ...)"}, {"e": "A number of applicants have already been interviewed."}]}, {"c": "The number of = “Số lượng những ...”, đi với danh từ số nhiều, động từ vẫn ở ngôi thứ 3 số ít.", "f": ["The number of + plural noun + plural verb"], "e": [{"e": "The number of days in a week is seven. (Số lượng ngày trong tuần là 7)"}, {"e": "The number of residents who have been questioned on this matter is quite small."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (35, '2', 'Các danh từ luôn ở số nhiều', '{"Sự hòa hợp giữa chủ ngữ và động từ"}', 'Sự hòa hợp giữa chủ ngữ và động từ, Các danh từ luôn ở số nhiều', '{27, 28, 29, 30, 31, 32, 33, 34, 36}', '[{"sub_title": "", "content": [{"c": "Ví dụ sau là những danh từ bao giờ cũng ở hình thái số nhiều vì chúng bao gồm 2 thực thể nên các đại từ và động từ đi cùng với chúng cũng phải ở số nhiều.", "f": [], "e": [{"e": "scissors shorts pants"}, {"e": "jeans tongs trousers"}, {"e": "eyeglasses pliers tweezers"}]}, {"c": "Nếu muốn chúng thành ngôi số ít phải dùng a pair of...", "f": [], "e": [{"e": "The pants are in the drawer."}, {"e": "A pair of pants is in the drawer."}, {"e": "These scissors are dull. (Cái kéo này cùn. Chú ý chỉ có 1 cái kéo nhưng cũng dùng với these)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (36, '2', 'Cách dùng there is, there are', '{"Sự hòa hợp giữa chủ ngữ và động từ"}', 'Sự hòa hợp giữa chủ ngữ và động từ, Cách dùng there is, there are', '{27, 28, 29, 30, 31, 32, 33, 34, 35}', '[{"sub_title": "", "content": [{"c": "Được dùng để chỉ sự tồn tại của người hoặc vật tại một nơi nào đó. Chủ ngữ thật của câu là là danh từ đi sau động từ. Nếu nó là danh từ số ít thì động từ to be chia ở ngôi thứ 3 số ít và ngược lại. Mọi biến đổi về thì và thể đều ở to be còn there giữ nguyên.", "f": ["(There is/There was/There has been) + singular subject (or non-cnount) ..."], "e": [{"e": "There has been an increase in the importation of foreign cars."}, {"e": "Threre is a storm approaching."}, {"e": "There was an accident last night."}, {"e": "There was water on the floor."}]}, {"c": "", "f": ["(There are/There were/There have been) + plural subject..."], "e": [{"e": "There have been a number of telephone calls today."}, {"e": "There were too many people at the party."}]}]}, {"sub_title": "Lưu ý:", "content": [{"c": "Các cấu trúc: there is certain/ sure/ likely/ bound to be = chắc chắn là sẽ có", "f": [], "e": [{"e": "There is sure to be trouble when she gets his letter. (Chắc chắn là sẽ có rắc rối khi cô ấy nhận được thư anh ta)"}, {"e": "Do you think there is likely to be snow. (Anh cho rằng chắc chắn sẽ có tuyết chứ)"}]}, {"c": "Trong dạng tiếng Anh quy chuẩn hoặc văn chương một số các động từ khác ngoài to be cũng được sử dụng với there:", "f": ["There + verb + (of state/of arrival) + noun as subject"], "e": []}, {"c": "Động từ trạng thái: stand/ lie/ remain/ exist/ live", "f": [], "e": []}, {"c": "Động từ chỉ sự đến: enter/ go/ come/ follow/ develop", "f": [], "e": [{"e": "In a small town in Germany there once lived a poor shoemaker. (Tại một thị trấn nhỏ ở Đức có một ông thợ giày nghèo sống ở đó)"}, {"e": "There remains nothing more to be done . (Chả còn có gì nữa mà làm)"}, {"e": "Suddenly there entered a strange figure dressed all in black. (Bỗng có một hình bóng kì lạ mặc toàn đồ đen đi vào)"}, {"e": "There followed an uncomfortable silence. (Sau đó là một sự im lặng đến khó chịu)"}]}, {"c": "There + subject pronoun + go/ come/ be: kìa/ thế là/ rồi thì ", "f": [], "e": [{"e": "There he comes (Anh ta đã đến rồi kia kìa)"}, {"e": "There you are, I have been waiting for you for over an hour. (Anh đây rồi, tôi đang chờ anh đến hơn một tiếng rồi đấy)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (37, '2', 'Subject pronoun (đại từ nhân xưng chủ ngữ)', '{"Đại từ"}', 'Đại từ, Subject pronoun (đại từ nhân xưng chủ ngữ)', '{38, 39, 40, 41}', '[{"sub_title": "I, You, He, She, It, We, They", "content": [{"c": "Đại từ nhân xưng chủ ngữ (còn gọi là đại từ nhân xưng) thường đứng ở vị trí chủ ngữ trong câu hoặc đứng sau động từ be, đằng sau các phó từ so sánh như than, as, that...", "f": [], "e": [{"e": "I am going to the store."}, {"e": "We have lived here for twenty years."}, {"e": "The teachers who were invited to the party were George, Bill and I."}, {"e": "It was she who called you."}, {"e": "George and I would like to leave now."}, {"e": "We students are going to have a party."}]}, {"c": "Ngay sau các ngôi số nhiều như we, you bạn có thể dùng một danh từ số nhiều để làm rõ we, you là chỉ cái gì.", "f": [], "e": [{"e": "We students are going to have a party (Sinh viên chúng tôi)"}, {"e": "You guys (Bọn mày)"}]}, {"c": "We/ You/ They có thể dùng với all/ both. Trong trường hợp câu có động từ đơn thì chúng đi liền với nhau:", "f": [], "e": [{"e": "We all go to school now."}, {"e": "They both bought the ensurance You all come shopping."}]}, {"c": "Nhưng nếu all hoặc both đi với các đại từ này ở dạng câu có trợ động từ thì all hoặc both sẽ đứng sau trợ động từ:", "f": [], "e": [{"e": "We will all go to school next week. They have both bought the insurance."}]}, {"c": "All và Both cũng phải đứng sau động từ to be, trước tính từ ", "f": [], "e": [{"e": "We are all ready to go swimming."}]}, {"c": "Dùng he/she thay thế cho các vật nuôi nếu chúng được xem là có tính cách, thông minh hoặc tình cảm (chó, mèo, ngựa)", "f": [], "e": [{"e": "Go and find the cat if where she stays in."}, {"e": "How''s your new car? Terrific, she is running beautifully."}]}, {"c": "Tên nước, tên các con tàu được thay thế trang trọng bằng she (ngày nay it dùng). ", "f": [], "e": [{"e": "England is an island country and she is governed by a mornach."}, {"e": "Titanic was the biggest passenger ship ever built."}, {"e": "She could carry as many as 2000 passenger on board."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (38, '2', 'Complement pronoun (đại từ nhân xưng tân ngữ)', '{"Đại từ"}', 'Đại từ, Complement pronoun (đại từ nhân xưng tân ngữ)', '{37, 39, 40, 41}', '[{"sub_title": "Me, you, him, her, it, us, them", "content": [{"c": "Đại từ tân ngữ đứng ở vị trí tân ngữ (đằng sau động từ hoặc giới trừ khi giới từ đó mở đầu một mệnh đề mới). Bạn cần phân biệt rõ đại từ tân ngữ với đại từ chủ ngữ. Chủ ngữ là chủ thể của hành động (gây ra hành động), còn tân ngữ là đối tượng nhận sự tác động của hành động.", "f": [], "e": [{"e": "They invited us to the party last night."}, {"e": "The teacher gave him a bad grade."}, {"e": "I told her a story."}, {"e": "The policeman was looking for him."}]}, {"c": "Đằng sau us có thể dùng một danh từ số nhiều trực tiếp, giống như đối với đại từ nhân xưng chủ ngữ.", "f": [], "e": [{"e": "The teacher has made a lot of questions for us students."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (39, '2', 'Possessive pronoun (đại từ sở hữu)', '{"Đại từ"}', 'Đại từ, Possessive pronoun (đại từ sở hữu)', '{37, 38, 40, 41}', '[{"sub_title": "", "content": [{"c": "Người ta dùng đại từ sở hữu để tránh khỏi phải nhắc lại tính từ sở hữu + danh từ đã đề cập trước đó. Nó có nghĩa: mine = cái của tôi; yours = cái của (các) bạn; ... Do đó chúng thay thế cho danh từ. Đừng bao giờ dùng cả đại từ sở hữu lẫn danh từ. Mặc dù cách viết của his và its đối với tính từ sở hữu và đại từ sở hữu là giống nhau nhưng bạn cần phân biệt rõ hai trường hợp này.", "f": [], "e": [{"e": "This is my book; that is yours. (yours = your book)"}, {"e": "Your teacher is the same as his. (his = his teacher)"}, {"e": "Jill’s dress is green and mine is red. (mine = my dress)"}, {"e": "Your books are heavy, ours are heavy too. (ours = our books)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (40, '2', 'Possessive adjectives (tính từ sở hữu)', '{"Đại từ"}', 'Đại từ, Possessive adjectives (tính từ sở hữu)', '{37, 38, 39, 41}', '[{"sub_title": "My, your, his, her, its, our, their", "content": [{"c": "Tính từ sở hữu khác với đại từ sở hữu (nêu trên đây) ở chỗ nó bổ nghĩa cho danh từ chứ không thay thế cho danh từ. Gọi là tính từ sở hữu vì nó thể hiện tính chất sở hữu của người hoặc vật đối với danh từ đi sau nó. Chú ý rằng cũng dùng tính từ sở hữu đối với các bộ phận trên cơ thể.", "f": [], "e": [{"e": "John is eating his dinner."}, {"e": "This is not my book."}, {"e": "The cat has injured its foot."}, {"e": "The boy broke his arm yesterday."}, {"e": "She forgot her homework this morning."}, {"e": "My food is cold."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (41, '2', 'Reflexive pronoun (đại từ phản thân)', '{"Đại từ"}', 'Đại từ, Reflexive pronoun (đại từ phản thân)', '{37, 38, 39, 40}', '[{"sub_title": "", "content": [{"c": "Dùng để diễn đạt chủ ngữ vừa là tác nhân gây ra hành động, vừa là tác nhân nhận tác động của hành động đó. Nó đứng ngay đằng sau động từ hoặc giới từ for, to ở cuối câu.", "f": [], "e": [{"e": "Jill bought himself a new car."}]}, {"c": "Chú ý: “Jill bought him a new car” thì câu có nghĩa khác: “him” = another person.", "f": [], "e": [{"e": "I washed myself"}, {"e": "He sent the letter to himself."}, {"e": "She served herself in the cafeteria."}, {"e": "We hurt ourselves playing football"}, {"e": "John and Mary hurt themselves in a car accident."}, {"e": "You can see the difference for yourselves."}]}, {"c": "Dùng để nhấn mạnh việc chủ ngữ tự làm lấy việc gì, trong trường hợp này nó đứng ngay sau chủ ngữ hoặc sau từ by.", "f": [], "e": [{"e": "I myself believe that there is no God."}, {"e": "She prepared the nine-course meal by herself."}, {"e": "John washed the dishes by himself."}, {"e": "The students themselves decorated the room."}]}, {"c": "Chú ý: ở dạng số nhiều self biến thành selves.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (42, '2', 'Động từ dùng làm tân ngữ', '{"Tân ngữ (complement / object) và các vấn đề liên quan"}', 'Tân ngữ (complement / object) và các vấn đề liên quan, Động từ dùng làm tân ngữ', '{43}', '[{"sub_title": "", "content": [{"c": "Không phải bất cứ động từ nào trong tiếng Anh cũng đều đòi hỏi tân ngữ đứng sau nó là một danh từ. Một số các động từ lại đòi hỏi tân ngữ sau nó phải là một động từ khác. Động từ dùng làm tân ngữ được chia làm hai loại", "f": [], "e": []}]}, {"sub_title": "Loại 1: Động từ nguyên thể làm tân ngữ (to + verb)", "content": [{"c": "Dưới đây là những động từ đòi hỏi tân ngữ sau nó là một động từ nguyên thể khác.", "f": [], "e": [{"e": "agree,\tdesire,\thope,\tplan,\tstrive"}, {"e": "attempt,\texpect,\tintend,\tprepare,\ttend"}, {"e": "claim,\tfail,\tlearn,\tpretend,\twant"}, {"e": "decide,\tforget,\tneed,\trefuse,\twish"}, {"e": "demand,\thesitate,\toffer,\tseem"}, {"e": "John expects to begin studying law next semester."}, {"e": "Mary learned to swim when she was very young."}, {"e": "The committee decided to postpone the meeting."}, {"e": "The president will attempt to reduce inflation rate."}]}, {"c": "Trong câu phủ định, thêm not vào trước động từ làm tân ngữ: ", "f": [], "e": [{"e": "John decided not to buy the car."}]}]}, {"sub_title": "Loại 2: Động từ Verb-ing dùng làm tân ngữ", "content": [{"c": "Dưới đây là những động từ đòi hỏi tân ngữ theo sau nó phải là một Verb-ing", "f": [], "e": [{"e": "admit,\tenjoy,\tsuggest"}, {"e": "appreciate,\tfinish,\tconsider"}, {"e": "avoid,\tmiss,\tmind"}, {"e": "can''t, help,\tpostpone,\trecall"}, {"e": "delay,\tpractice,\trisk"}, {"e": "deny,\tquit,\trepeat"}, {"e": "resist,\tresume,\tresent"}, {"e": "John admitted stealing the jewels."}, {"e": "We enjoyed seeing them again after so many years."}, {"e": "You shouldn’t risk entering that building in its present condition."}, {"e": "He was considering buying a new car until the prices went up."}, {"e": "The Coast Guard has reported seeing another ship in the Florida Straits."}]}, {"c": "Trong câu phủ định, thêm not vào trước Verb-ing. ", "f": [], "e": [{"e": "John regretted not buying the car."}]}, {"c": "Lưu ý rằng trong ví dụ này có mẫu động từ can''t help doing/ but do smt có nghĩa ‘không thể đừng được phải làm gì’", "f": [], "e": [{"e": "With such good oranges, we can''t help buying two kilos at a time."}]}]}, {"sub_title": "Dưới đây là những động từ mà tân ngữ sau nó có thể là một động từ nguyên thể hoặc một verb-ing mà ngữ nghĩa không thay đổi.", "content": [{"c": "", "f": ["begin, can''t stand, continue, dread, hate, like, love, prefer,\tstart, try"], "e": [{"e": "He started to study after dinner = he started studying after dinner."}]}, {"c": "Lưu ý rằng trong ví dụ này có một động từ can''t stand to do/doing smt: không thể chịu đựng được khi phải làm gì.", "f": [], "e": [{"e": "He can''t stand to wait (waiting) such a long time."}]}]}, {"sub_title": "Bốn động từ đặc biệt", "content": [{"c": "Đó là những động từ mà ngữ nghĩa của chúng sẽ đổi khác hoàn toàn khi tân ngữ sau nó là một động từ nguyên thể hoặc verb-ing.", "f": [], "e": []}, {"c": "Stop to do smt: dừng lại để làm gì", "f": [], "e": [{"e": "He stoped to smoke (Anh ta dừng lại để hút thuốc)."}]}, {"c": "Stop doing smt: dừng làm việc gì", "f": [], "e": [{"e": "He stoped smoking (Anh ta đã bỏ thuốc)."}]}, {"c": "Remember to do smt: Nhớ sẽ phải làm gì", "f": [], "e": [{"e": "Remember to send this letter. (Hãy nhớ gửi bức thư này nhé.)"}]}, {"c": "Remember doing smt: Nhớ là đã làm gì", "f": [], "e": [{"e": "I remember locking the door before leaving, but now I can''t find the key."}]}, {"c": "Đặc biệt nó thường được dùng với mẫu câu: S + still remember + V-ing : Vẫn còn nhớ là đã...", "f": [], "e": [{"e": "I still remember buying the first motorbike"}]}, {"c": "Forget to do smt: quên sẽ phải làm gì", "f": [], "e": [{"e": "I forgot to pickup my child after school (Tôi quên không đón con)"}]}, {"c": "Forget doing smt: (quên là đã làm gì). Đặc biệt nó thường được dùng với mẫu câu S + will never forget + V-ing: sẽ không bao giờ quên được là đã ...", "f": [], "e": [{"e": "She will never forget meeting the Queen (Cô ấy không bao giờ quên lần gặp Nữ hoàng)"}]}, {"c": "Regret to do smt: Lấy làm tiếc vì phải làm gì (thường dùng khi báo tin xấu)", "f": [], "e": [{"e": "We regret to inform the passengers that the flight for Washington DC was canceled because of the bad weather."}]}, {"c": "Regret doing smt: Lấy làm tiếc vì đã làm gì", "f": [], "e": [{"e": "He regrets leaving school early. It''s the biggest mistake in his life."}]}]}, {"sub_title": "Động từ đứng sau giới từ", "content": [{"c": "Tất cả các động từ đứng ngay sau giới từ đều phải ở dạng V-ing.", "f": ["Verb + preposition + verb-ing"], "e": []}, {"c": "Sau đây là ví dụ các động từ có giới từ theo sau, vì vậy các động từ khác đi sau động từ này phải dùng ở dạng verb-ing.", "f": [], "e": [{"e": "approve of,\tgive up,\trely on,\tworry about"}, {"e": "be better of,\tinsist on,\tsucceed in,\tobject to"}, {"e": "count on,\tkeep on,\tthink about,\tlook forward to"}, {"e": "depend on,\tput off,\tthink of,\tconfess to"}, {"e": "John gave up smoking because of his doctor’s advice."}, {"e": "He insisted on taking the bus instead of the plane."}, {"e": "Hery is thinking of going to France next year."}, {"e": "Fred confessed to stealing the jewels"}]}, {"c": "Chú ý rằng ở 3 động từ cuối cùng trong ví dụ trên, có giới từ to đi sau động từ. Đó là giới từ chứ không phải là to trong động từ nguyên thể (to do st), nên theo sau nó phải là một verb-ing chứ không phải là một verb nguyên thể.", "f": [], "e": [{"e": "We are not looking forward to going back to school."}, {"e": "Jill objected to receiving the new position."}, {"e": "He confessed to causing the fire."}]}, {"c": "", "f": ["Adjective + preposition + verb-ing"], "e": [{"e": "accustomed to, afraid of,\tintent on, interested in,\tcapable of, fond of,\tsuccessful in, tired of"}, {"e": "Mitch is afraid of getting married now."}, {"e": "We are accustomed to sleeping late on weekends."}, {"e": "I am fond of dancing."}, {"e": "We are interested in seeing this film."}]}, {"c": "", "f": ["Noun + preposition + verb-ing"], "e": [{"e": "choice of, excuse for,\tintention of, method for,\tpossibility of, reason for,\t(method of)"}, {"e": "There is no reason for leaving this early."}, {"e": "George has no excuse for droping out of school."}, {"e": "There is a possibility of acquiring this property at a good price."}, {"e": "He has developed a method for evaluating this problem."}]}, {"c": "Các trường hợp khác:", "f": [], "e": []}, {"c": "Trong các trường hợp khác, động từ đi sau giới từ cũng phải ở dạng verb-ing.", "f": [], "e": [{"e": "After leaving the party, he drove home."}, {"e": "He should have stayed in New York instead of moving to Maine."}]}]}, {"sub_title": "Động từ đi sau tính từ:", "content": [{"c": "Nói chung, nếu động từ đi ngay sau tính từ (không có giới từ) thì được dùng ở dạng nguyên thể. Những tính từ đó bao gồm.", "f": [], "e": [{"e": "anxious,\teager,\tpleased,\tusual"}, {"e": "boring,\teasy,\tprepared,\tcommon"}, {"e": "dangerous,\tgood,\tready,\tdifficult"}, {"e": "hard,\tstrange,\table"}, {"e": "It is dangerous to drive in this weather. Mike is anxious to see his family."}, {"e": "We are ready to leave now. It is difficult to pass this test."}]}, {"c": "Chú ý: able và capable có nghĩa như nhau nhưng cách dùng khác nhau: (able/ unable) to do smt = (capable/ incapable) of doing smt.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (43, '2', 'Đại từ đứng trước động từ nguyên thể hoặc v-ing trong tân ngữ', '{"Tân ngữ (complement / object) và các vấn đề liên quan"}', 'Tân ngữ (complement / object) và các vấn đề liên quan, Đại từ đứng trước động từ nguyên thể hoặc v-ing trong tân ngữ', '{42}', '[{"sub_title": "Trường hợp tân ngữ là động từ nguyên thể", "content": [{"c": "Trong trường hợp tân ngữ là động từ nguyên thể (loại 1) thì bất cứ danh từ hay đại từ nào trực tiếp đứng trước nó cũng phải ở dạng tân ngữ (complement form).", "f": ["S + V + (pronoun/noun in complement form) + [to + verb] + ..."], "e": [{"e": "Joe asked her to call him."}]}, {"c": "Sau đây là một số động từ đòi hỏi tân ngữ là một động từ nguyên thể có đại từ làm tân ngữ gián tiếp.", "f": [], "e": [{"e": "allow, ask, beg,\tconvince, expect, instruct,\tinvite, order, permit,\tpersuade, prepare, promise,\tremind, urge, want"}, {"e": "We ordered him to appear in court."}, {"e": "I urge you to reconsider your decision."}, {"e": "They were trying to persuade him to change his mind."}, {"e": "The teacher permitted them to turn their assignments in late."}, {"e": "You should prepare your son to take this examination."}]}]}, {"sub_title": "Trường hợp tân ngữ là V-ing", "content": [{"c": "Trong trường hợp tân ngữ là một V- ing thì đại từ/danh từ phải ở dạng sở hữu.", "f": ["Subject + verb + {pronoun/noun}(possessive form) + verb-ing + ..."], "e": [{"e": "We understand your not being able to stay longer."}, {"e": "We object to their calling at this hour."}, {"e": "He regrets her leaving."}, {"e": "We are looking forward to their coming next year."}, {"e": "We don’t approve of John’s buying this house."}, {"e": "We resent the teacher’s not announcing the test sooner."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (44, '2', 'Need', '{"Một số động từ đặc biệt (need, dare, to be, get)"}', 'Một số động từ đặc biệt (need, dare, to be, get), Need', '{45, 46, 47}', '[{"sub_title": "Need dùng như một động từ thường:", "content": [{"c": "Động từ đi sau need chỉ ở dạng nguyên thể khi chủ ngữ là một vật thể sống:", "f": [], "e": [{"e": "My friend needs to learn Spanish."}, {"e": "He will need to drive alone tonight."}, {"e": "John needs to paint his house."}]}, {"c": "Động từ đi sau need phải ở dạng verb-ing hoặc dạng bị động nếu chủ ngữ không phải là vật thể sống.", "f": [], "e": [{"e": "The grass needs cutting OR The grass needs to be cut."}, {"e": "The telivision needs repairing OR The TV needs to be repaired."}, {"e": "Your thesis needs rewriting OR Your thesis needs to be rewritten."}]}, {"c": "", "f": ["Chú ý: need + noun = to be in need of + noun"], "e": [{"e": "Jill is in need of money = Jill needs money"}, {"e": "The roof is in need of repair = The roof needs repairing"}]}, {"c": "Want và Require cũng đôi khi được dùng theo mẫu câu này nhưng không phổ biến: ", "f": [], "e": [{"e": "Your hair wants cutting"}, {"e": "All cars require servicing regularly"}]}]}, {"sub_title": "Need dùng như một trợ động từ", "content": [{"c": "Chỉ dùng ở thể nghi vấn hoặc phủ định thì hiện tại. Ngôi thứ ba số ít không có \"s\" tận cùng. Không dùng với trợ động từ to do. Sau need (trợ động từ) là một động từ bỏ to:", "f": [], "e": [{"e": "We needn''t reserve seats - there will be plenty of rooms. Need I fill out the form?"}]}, {"c": "Thường dùng sau các từ như if/ whether/ only/ scarcely/ hardly/ no one", "f": [], "e": [{"e": "I wonder if I need fill out the form. This is the only form you need fill out."}]}, {"c": "Needn''t + have + P2 : Lẽ ra không cần phải", "f": [], "e": [{"e": "You needn''t have come so early - only waste your time."}]}, {"c": "Needn''t = không cần phải; trong khi mustn''t = không được phép.", "f": [], "e": [{"e": "You needn’t apply for a visa to visit France if you hold a EU passport, but if you are not an EU citizen, you mustn’t unless you have a visa."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (45, '2', 'Dare (dám)', '{"Một số động từ đặc biệt (need, dare, to be, get)"}', 'Một số động từ đặc biệt (need, dare, to be, get), Dare (dám)', '{44, 46, 47}', '[{"sub_title": "Dùng như một nội động từ", "content": [{"c": "Không dùng ở thể khẳng định, chỉ dùng ở thể nghi vấn và phủ định.", "f": [], "e": [{"e": "Did they dare (to) do such a thing? = Dared they do such a thing? (Họ dám làm như vậy sao?)"}, {"e": "He didn''t dare (to) say anything = He dared not say anything. (Anh ta không dám nói gì.)"}]}, {"c": "Dare không được dùng ở thể khẳng định ngoại trừ thành ngữ I dare say/ I daresay với 2 nghĩa sau: Tôi cho rằng: ", "f": [], "e": [{"e": "I dare say there is a restaurant at the end of the train."}]}, {"c": "Tôi thừa nhận là: ", "f": [], "e": [{"e": "I daresay you are right."}]}, {"c": "How dare/ dared + S + Verb in simple form: Sao ... dám (tỏ sự giận giữ) ", "f": [], "e": [{"e": "How dared you open my letter (Sao mày dám mở thư của tao)"}]}]}, {"sub_title": "Dùng như một ngoại động từ", "content": [{"c": "Mang nghĩa “thách thức”: Dare sb to do smt = Thách ai làm gì", "f": [], "e": [{"e": "They dare the boy to swim across the river in such a cold weather."}, {"e": "I dare you to touch my toes (Tao thách mày dám động đến một sợi lông của tao)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (46, '2', 'Cách sử dụng to be trong một số trường hợp', '{"Một số động từ đặc biệt (need, dare, to be, get)"}', 'Một số động từ đặc biệt (need, dare, to be, get), Cách sử dụng to be trong một số trường hợp', '{44, 45, 47}', '[{"sub_title": "", "content": [{"c": "To be of + noun = to have: có (dùng để chỉ tính chất hoặc tình cảm) ", "f": [], "e": [{"e": "Mary is of a gentle nature (Mary có một bản chất tử tế)"}]}, {"c": "To be of + noun: Nhấn mạnh cho danh từ đứng đằng sau", "f": [], "e": [{"e": "The newly-opened restaurant is of (ở ngay) the Leceister Square."}]}, {"c": "To be + to + verb: là dạng cấu tạo đặc biệt, sử dụng trong trường hợp:", "f": [], "e": []}, {"c": "Để truyền đạt các mệnh lệnh hoặc các chỉ dẫn từ ngôi thứ nhất qua ngôi thứ hai đến ngôi thứ ba.", "f": [], "e": [{"e": "No one is to leave this building without the permission of the police."}]}, {"c": "Dùng với mệnh đề if khi mệnh đề chính diễn đạt một câu điều kiện: Một điều phải xảy ra trước nếu muốn một điều khác xảy ra. (Nếu muốn... thì phải..)", "f": [], "e": [{"e": "If we are to get there by lunch time we had better hurry."}, {"e": "Something must be done quickly if the endangered birds are to be saved."}, {"e": "He knew he would have to work hard if he was to pass his exam"}]}, {"c": "Được dùng để thông báo những yêu cầu xin chỉ dẫn:", "f": [], "e": [{"e": "He asked the air traffic control where he was to land."}]}, {"c": "Được dùng khá phổ biến để truyền đạt một dự định, một sự sắp đặt, đặc biệt khi nó là chính thức.", "f": [], "e": [{"e": "She is to get married next month."}, {"e": "The expedition is to start in a week."}, {"e": "We are to get a ten percent wage rise in June."}]}, {"c": "Cấu trúc này thông dụng trên báo chí, khi là tựa đề báo thì to be được bỏ đi.", "f": [], "e": [{"e": "The Primer Minister (is) to make a statement tomorrow."}]}, {"c": "were + S + to + verb = if + S + were + to + verb = thế nếu (một giả thuyết) ", "f": [], "e": [{"e": "Were I to tell you that he passed his exams, would you believe me."}]}, {"c": "was/ were + to + verb: Để diễn đạt ý tưởng về một số mệnh đã định sẵn", "f": [], "e": [{"e": "They said goodbye without knowing that they were never to meet again."}, {"e": "Since 1840, American Presidents elected in years ending in zero were to be died (have been destined to die) in office."}]}, {"c": "to be about to + verb = near future (sắp sửa) ", "f": [], "e": [{"e": "They are about to leave."}]}, {"c": "Be + adj ... (mở đầu cho một ngữ) = tỏ ra...", "f": [], "e": [{"e": "Be careless in a national park where there are bears around and the result are likely to be tragical indeed."}]}, {"c": "Be + subject + noun/ noun phrase/ adjective = cho dù là ...", "f": [], "e": [{"e": "Societies have found various methods to support and train their artists, be it the Renaissance system of royal support of the sculptors and painters of the period or the Japanese tradition of passing artistic knowledge from father to son. (Ở các xã hội đều tìm thấy một số phương pháp hỗ trợ và đào tạo các nghệ sỹ, cho dù là hệ thống hỗ trợ các nhà điêu khắc và hoạ sỹ của các hoàng gia thời kỳ Phục hưng hay phương pháp truyền thụ hiểu biết nghệ thuật từ cha sang con theo truyền thống Nhật Bản)"}, {"e": "To have technique is to possess the physical expertise to perform whatever steps a given work may contain, be they simple or complex. (Có được kỹ thuật là sẽ có được sự điêu luyện để thực  hiện bất kỳ thao tác nào mà một công việc đòi hỏi, cho dù là chúng đơn giản hay phức tạp)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (47, '2', 'Cách sử dụng to get trong một số trường hợp', '{"Một số động từ đặc biệt (need, dare, to be, get)"}', 'Một số động từ đặc biệt (need, dare, to be, get), Cách sử dụng to get trong một số trường hợp', '{44, 45, 46}', '[{"sub_title": "To get + P2", "content": [{"c": "Chỉ việc chủ ngữ tự làm lấy một việc gì hoặc tình huống mà chủ ngữ đang gặp phải.", "f": ["get washed/ dressed/ prepared/ lost/ drowned/ engaged/ married/ divorced."], "e": [{"e": "You will have 5 minutes to get dressed.(Em có 5 phút để mặc quần áo)"}, {"e": "He got lost in old Market Street yesterday. (tình huống bị lạc đường)"}]}, {"c": "Tuyệt nhiên không được lẫn trường hợp này với dạng bị động.", "f": [], "e": []}]}, {"sub_title": "Get + V-ing = Start + V-ing: Bắt đầu làm gì", "content": [{"c": "", "f": [], "e": [{"e": "We''d better get moving, it''s late."}]}]}, {"sub_title": "Get sb/smt +V-ing: Làm cho ai/ cái gì bắt đầu.", "content": [{"c": "", "f": [], "e": [{"e": "Please get him talking about the main task. (Làm ơn bảo anh ta hãy bắt đầu đi vào vấn đề chính)"}, {"e": "When we get the heater running, the whole car will start to warm up. (Khi chúng ta cho máy sưởi bắt đầu chạy..)"}]}]}, {"sub_title": "Get + to + verb", "content": [{"c": "ìm được cách.", "f": [], "e": [{"e": "We could get to enter the stadium without tickets.(Chúng tôi đã tìm được cách lọt vào...)"}]}, {"c": "Có cơ may", "f": [], "e": [{"e": "When do I get to have a promotion? (Khi nào tôi có cơ may được tăng lương đây?)"}]}, {"c": "Được phép", "f": [], "e": [{"e": "At last we got to meet the general director. (Cuối cùng thì rồi chúng tôi cũng được phép gặp tổng đạo diễn)"}]}]}, {"sub_title": "Get + to + Verb (về hành động) = Come + to + Verb (về nhận thức) = Gradually = dần dần", "content": [{"c": "", "f": [], "e": [{"e": "We will get to speak English more easily as time goes by."}, {"e": "He comes to understand that learning English is not much difficult."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (48, '2', 'Câu hỏi', '{}', 'Câu hỏi, Câu hỏi', '{49, 50, 51, 52}', '[{"sub_title": "", "content": [{"c": "Tiếng Anh có nhiều loại câu hỏi có những chức năng và mục đích khác nhau. Trong câu hỏi, trợ động từ hoặc động từ be bao giờ cũng đứng trước chủ ngữ. Nếu không có trợ động từ hoặc động từ be, ta phải dùng dạng thức do, does, did như một trợ động từ để thay thế. Sau các động từ hoặc trợ động từ đó, phải dùng động chính từ ở dạng nguyên thể không có to. Thì và thể của câu hỏi chỉ được chia bởi trợ động từ, chứ không phải động từ chính.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (49, '2', 'Câu hỏi yes/ no', '{"Câu hỏi"}', 'Câu hỏi, Câu hỏi yes/ no', '{48, 50, 51, 52}', '[{"sub_title": "", "content": [{"c": "", "f": ["(auxiliary/be/do/des/did) + S + v"], "e": []}, {"c": "Sở dĩ ta gọi là như vậy vì khi trả lời, dùng Yes/No. Nhớ rằng khi trả lời:", "f": ["Yes + Positive verb"], "e": []}, {"c": "", "f": ["No + Negative verb."], "e": []}, {"c": "(không được trả lời theo kiểu câu tiếng Việt)", "f": [], "e": [{"e": "Isn''t Mary going to school today?"}, {"e": "Was Mark sick yesterday?"}, {"e": "Have you seen this movie before?"}, {"e": "Will the committe decide on the proposal today?"}, {"e": "Don''t you still want to use the telephone?"}, {"e": "Did you go to class yesterday?"}, {"e": "Doesn''t Ted like this picture?"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (50, '2', 'Câu hỏi lấy thông tin (information question)', '{"Câu hỏi"}', 'Câu hỏi, Câu hỏi lấy thông tin (information question)', '{48, 49, 51, 52}', '[{"sub_title": "", "content": [{"c": "Đối với loại câu hỏi này, câu trả lời không thể đơn giản là yes hay no mà phải có thêm thông tin. Chúng thường dùng các từ nghi vấn, chia làm 3 loại sau", "f": [], "e": []}]}, {"sub_title": "Who hoặc What: câu hỏi chủ ngữ", "content": [{"c": "Đây là câu hỏi khi muốn biết chủ ngữ hay chủ thể của hành động.", "f": ["(Who/What) + verb + (complement) + (modifier)"], "e": [{"e": "Something happened lastnight => What happened last night?"}, {"e": "Someone opened the door. => Who opened the door?"}]}, {"c": "Chú ý các câu sau đây là sai ngữ pháp: ", "f": [], "e": [{"e": "Who did open the door? (SAI)"}, {"e": "What did happen lastnight? (SAI)"}]}]}, {"sub_title": "Whom hoặc What: câu hỏi tân ngữ", "content": [{"c": "Đây là các câu hỏi dùng khi muốn biết tân ngữ hay đối tượng tác động của hành động", "f": ["(Whom/What) + (auxiliary/do/des/did) + S + V + (modifier)"], "e": []}, {"c": "Nhớ rằng trong tiếng Anh viết chính tắc bắt buộc phải dùng whom mặc dù trong tiếng Anh nói có thể dùng who thay cho whom trong mẫu câu trên.", "f": [], "e": [{"e": "George bought something at the store. => What did George buy at the store?"}, {"e": "Ana knows someone from UK. => Whom does Ana know from UK?"}]}]}, {"sub_title": "When, Where, How và Why: Câu hỏi bổ ngữ", "content": [{"c": "Dùng khi muốn biết nơi chốn, thời gian, lý do, cách thức của hành động.", "f": ["(When/Where/How/Why) + (auxiliary/be/do/does/did) + S + V + complement + modifier"], "e": [{"e": "How did Maria get to school today?"}, {"e": "When did he move to London?"}, {"e": "Why did she leave so early?"}, {"e": "Where has Ted gone?"}, {"e": "When will she come back?"}]}, {"c": "Chú ý tránh nhầm lẫn với các câu sai như ví dụ ở phần (Who hoặc What)", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (51, '2', 'Câu hỏi phức (embedded question)', '{"Câu hỏi"}', 'Câu hỏi, Câu hỏi phức (embedded question)', '{48, 49, 50, 52}', '[{"sub_title": "", "content": [{"c": "Là câu hoặc câu hỏi chứa trong nó một câu hỏi khác. Câu có hai thành phần nối với nhau bằng một từ nghi vấn (question word). Động từ ở mệnh đề thứ hai (mệnh đề nghi vấn) phải đi sau và chia theo chủ ngữ, không được đảo vị trí như ở câu hỏi độc lập.", "f": ["S + V (phrase) + question word + S + V"], "e": [{"e": "The authorities can''t figure out why the plane landed at the wrong airport."}, {"e": "We haven’t assertained where the meeting will take place."}]}, {"c": "Trong trường hợp câu hỏi phức là một câu hỏi, áp dụng mẫu câu sau:", "f": ["auxiliary + S + V + question word + S + V"], "e": [{"e": "Do you know where he went?"}, {"e": "Could you tell me what time it is?"}]}, {"c": "Question word có thể là một từ, cũng có thể là một cụm từ như: whose + noun, how many, how much, how long, how often, what time, what kind.", "f": [], "e": [{"e": "I have no idea how long the interview will take."}, {"e": "Do you know how often the bus run at night?"}, {"e": "Can you tell me how far the museum is from the store?"}, {"e": "I’ll tell you what kind of ice-cream tastes best."}, {"e": "The teacher asked us whose book was on his desk."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (52, '2', 'Câu hỏi đuôi (tag questions)', '{"Câu hỏi"}', 'Câu hỏi, Câu hỏi đuôi (tag questions)', '{48, 49, 50, 51}', '[{"sub_title": "", "content": [{"c": "Trong câu hỏi đuôi, người đặt câu hỏi đưa ra một mệnh đề (mệnh đề chính) nhưng không hoàn toàn chắc chắn về tính đúng / sai của mệnh đề đó, do vậy họ dùng câu hỏi dạng này để kiểm chứng về mệnh đề đưa ra.", "f": [], "e": [{"e": "He should stay in bed, shouldn''t he? (Anh ấy nên ở yên trên giường, có phải không?)"}, {"e": "She has been studying English for two years, hasn''t she?"}, {"e": "There are only twenty-eight days in February, aren’t there?"}, {"e": "It’s raining now, isn’t it? (Trời vẫn còn mưa, phải không?)"}, {"e": "You and I talked with the professor yesterday, didn’t we?"}, {"e": "You won’t be leaving for now, will you?"}, {"e": "Jill and Joe haven’t been to VN, have they?"}]}]}, {"sub_title": "Câu hỏi đuôi chia làm hai thành phần tách biệt nhau bởi dấu phẩy theo quy tắc sau:", "content": [{"c": "Sử dụng trợ động từ giống như ở mệnh đề chính để làm phần đuôi câu hỏi. Nếu không có trợ động từ thì dùng do, does, did để thay thế.", "f": [], "e": []}, {"c": "Nếu mệnh đề chính ở thể khẳng định thì phần đuôi ở thể phủ định và ngược lại.", "f": [], "e": []}, {"c": "Thì của động từ ở đuôi phải theo thì của động từ ở mệnh đề chính.", "f": [], "e": []}, {"c": "Chủ ngữ của mệnh đề chính và của phần đôi là giống nhau. Đại từ ở phần đuôi luôn phải để ở dạng chủ ngữ (in subject form)", "f": [], "e": []}, {"c": "Phần đuôi nếu ở dạng phủ định thì thường được rút gọn (n’t). Nếu không rút gọn thì phải theo thứ tự: auxiliary + subject + not? (He saw it yesterday, did he not?)", "f": [], "e": []}, {"c": "Động từ have có thể là động từ chính, cũng có thể là trợ động từ. Khi nó là động từ chính của mệnh đề trong tiếng Anh Mỹ thì phần đuôi phải dùng trợ động từ do, does hoặc did. Tuy nhiên, trong tiếng Anh Anh thì bạn có thể dùng chính have như một trợ động từ trong trường hợp này. Khi bạn thi TOEFL là kỳ thi kiểm tra tiếng Anh Mỹ, bạn phải chú ý để khỏi bị mất điểm.", "f": [], "e": [{"e": "You have two children, haven’t you? (British English: OK, TOEFL: NOT)"}, {"e": "You have two children, don''t you? (Correct in American English)"}]}, {"c": "There is, there are và it is là các chủ ngữ giả nên phần đuôi được phép dùng lại there hoặc it giống như trường hợp đại từ làm chủ ngữ.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (53, '2', 'Phụ hoạ câu khẳng định', '{"Lối nói phụ họa"}', 'Lối nói phụ họa, Phụ hoạ câu khẳng định', '{54}', '[{"sub_title": "", "content": [{"c": "Khi muốn nói một người hoặc vật nào đó làm một việc gì đó và một người, vật khác cũng làm một việc như vậy, người ta dùng so hoặc too. Để tránh phải lặp lại các từ của câu trước (mệnh đề chính), người ta dùng liên từ and và thêm một câu đơn giản (mệnh đề phụ) có sử dụng so hoặc too. Ý nghĩa của hai từ này có nghĩa là “cũng thế”.", "f": [], "e": [{"e": "John went to the mountains on his vacation, and we did too."}, {"e": "John went to the mountains on his vacation, and so did we."}, {"e": "I will be in VN in May, and they will too."}, {"e": "I will be in VN in May, and so will they."}, {"e": "He has seen her play, and the girls have too."}, {"e": "He has seen her play, and so have the girls."}, {"e": "We are going to the movie tonight, and he is too."}, {"e": "We are going to the movie tonight, and so is he."}, {"e": "She will wear a custome to the party, and we will too."}, {"e": "She will wear a custome to the party, and so will we."}, {"e": "Picaso was a famous painter, and Rubens was too."}, {"e": "Picaso was a famous painter, and so was Rubens."}]}]}, {"sub_title": "Tuỳ theo từ nào được dùng mà cấu trúc câu có sự thay đổi:", "content": [{"c": "Khi trong mệnh đề chính có động từ be ở bất cứ thì nào thì trong mệnh đề phụ cũng dùng từ be ở thì đó.", "f": ["affirmative sattement (be) + and + (S + verb (be) + too/so +verb (be) + S)"], "e": [{"e": "I am happy, and you are too."}, {"e": "I am happy, and so are you."}]}, {"c": "Khi trong mệnh đề chính có một cụm trợ động từ + động từ, ví dụ will go, should do, has done, have written, must consider, ... thì các trợ động từ trong mệnh đề đó được dùng lại trong mệnh đề phụ.", "f": ["affirmative statement (compound verb) + and + {S + (auxiliary only) + too/so + (auxiliary only) + S}"], "e": [{"e": "They will work in the lab tomorrow, and you will too."}, {"e": "They will work in the lab tomorrow, and so will you."}]}, {"c": "Khi trong mệnh đề chính không phải là động từ be, cũng không có trợ động từ, bạn phải dùng các từ do, does, did làm trợ động từ thay thế. Thì và thể của trợ động từ này phải chia theo chủ ngữ của mệnh đề phụ.", "f": ["affirmative statement (compound verb) + and + {S + (do, does, or did) + too/so + (do, does, or did) + S}"], "e": [{"e": "Jane goes to that school, and my sister does too."}, {"e": "Jane goes to that school, and so does my sister."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (54, '2', 'Phụ hoạ câu khẳng định', '{"Lối nói phụ họa"}', 'Lối nói phụ họa, Phụ hoạ câu khẳng định', '{53}', '[{"sub_title": "", "content": [{"c": "Cũng giống như too và so trong câu khẳng định, để phụ hoạ một câu phủ định, người ta dùng either hoặc neither. Hai từ này có nghĩa “cũng không”. Ba quy tắc đối với trợ động từ, động từ be hoặc do, does, did cũng được áp dụng giống như trên. Ta cũng có thể gói gọn 3 quy tắc đó vào một công thức như sau:", "f": ["negative statement + and + {S + negative auxiliary (or be) + either/neither + possitive auxiliary (or be) + S}"], "e": [{"e": "I didn''t see Mary this morning, and John didn''t either"}, {"e": "I didn''t see Mary this morning, and neither did John."}, {"e": "She won’t be going to the conference, and her friends won’t either."}, {"e": "She won’t be going to the conference, and neither will her friends."}, {"e": "John hasn’t seen the new movie yet, and I haven’t either."}, {"e": "John hasn’t seen the new movie yet, and neither have I."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (55, '2', 'Câu phủ định (negation)', '{}', 'Câu phủ định (negation), Câu phủ định (negation)', '{56, 57, 58, 59, 60, 61, 62, 63, 64}', '[{"sub_title": "", "content": [{"c": "Để tạo câu phủ định đặt not sau trợ động từ hoặc động từ be . Nếu không có trợ động từ hoặc động từ be thì dùng dạng thức thích hợp của do, does hoặc did để thay thế.", "f": [], "e": [{"e": "John is rich => John is not rich."}, {"e": "Mark has seen Bill => Mark has not seen Bill"}, {"e": "Mary can swim => Mary cannot swim."}, {"e": "I went to the store yesterday => I did not go to the store yesterday."}, {"e": "Mark likes spinach => Mark doesn’t like spinach."}, {"e": "I want to leave now => I don’t want to leave now."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (56, '2', 'Some/any', '{"Câu phủ định (negation)"}', 'Câu phủ định (negation), Some/any', '{55, 57, 58, 59, 60, 61, 62, 63, 64}', '[{"sub_title": "", "content": [{"c": "Đặt any đằng trước danh từ làm vị ngữ sẽ nhấn mạnh câu phủ định. Cũng có thể nhấn mạnh một câu phủ định bằng cách dùng no + danh từ hoặc a single + danh từ số ít.", "f": [], "e": [{"e": "John has some money => John doesn’t have any money."}, {"e": "He sold some magazines yesterday => He didn''t sell a single magazine yesterday."}, {"e": "He sold no magazine yesterday."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (57, '2', 'Một số các câu hỏi ở dạng phủ định lại mang ý nghĩa khác (không dùng dấu ?)', '{"Câu phủ định (negation)"}', 'Câu phủ định (negation), Một số các câu hỏi ở dạng phủ định lại mang ý nghĩa khác (không dùng dấu ?)', '{55, 56, 58, 59, 60, 61, 62, 63, 64}', '[{"sub_title": "", "content": [{"c": "Nhấn mạnh cho sự khẳng định của người nói.", "f": [], "e": [{"e": "Shouldn ''t you put on your hat, too! (Thế thì anh cũng đội luôn mũ vào đi)"}, {"e": "Didn''t you say that you would come to the party tonight (Thế anh đã chẳng nói là anh đi dự tiệc tối nay hay sao)"}]}, {"c": "Dùng để tán dương", "f": [], "e": [{"e": "Wasn ''t the weather wonderful yesterday (Thời tiết hôm qua đẹp tuyệt vời)"}, {"e": "Wouldn''t it be nice if we didn''t have to work on Friday. (Thật là tuyệt vời khi chúng ta không phải làm việc ngày thứ 6)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (58, '2', 'Hai lần phủ định', '{"Câu phủ định (negation)"}', 'Câu phủ định (negation), Hai lần phủ định', '{55, 56, 57, 59, 60, 61, 62, 63, 64}', '[{"sub_title": "", "content": [{"c": "Mang ý nghĩa nhấn mạnh", "f": ["Negative + Negative = Positive"], "e": [{"e": "It''s unbelieveable he is not rich. (Chẳng ai có thể tin được là anh ta lại không giàu có.)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (59, '2', 'Phủ định kết hợp với so sánh', '{"Câu phủ định (negation)"}', 'Câu phủ định (negation), Phủ định kết hợp với so sánh', '{55, 56, 57, 58, 60, 61, 62, 63, 64}', '[{"sub_title": "", "content": [{"c": "Mang nghĩa so sánh tuyệt đối", "f": ["Negative + comparative (more/ less) = superlative"], "e": [{"e": "I couldn''t agree with you less = I absolutely agree with you."}, {"e": "You couldn''t have gone to the beach on a better day = It''s the best day to go to the beach."}]}, {"c": "Nhưng phải hết sức cẩn thận vì", "f": [], "e": [{"e": "He couldn''t have been more unfriendly when I met him first. = the most unfriendly"}, {"e": "The surgery couldn''t have been more unnecessary. = absolutely unnecessary"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (60, '2', 'Cấu trúc phủ định song song', '{"Câu phủ định (negation)"}', 'Câu phủ định (negation), Cấu trúc phủ định song song', '{55, 56, 57, 58, 59, 61, 62, 63, 64}', '[{"sub_title": "", "content": [{"c": "không ... mà lại càng không", "f": ["Negative... even/still less/much less + noun/ verb in simple form"], "e": [{"e": "These students don''t like reading novel, much less textbook (Những sinh viên này chẳng thích đọc tiểu thuyết, chứ chưa nói đến sách giáo khoa)"}, {"e": "It''s unbelieveable how he could have survived such a freefall, much less live to tell about it on television (Thật không thể tin được anh ta lại có thể sống sót sau cú rơi tự do đó, chứ đừng nói đến chuyện lên TV kể về nó)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (61, '2', 'Phủ định không dùng thể phủ định của động từ', '{"Câu phủ định (negation)"}', 'Câu phủ định (negation), Phủ định không dùng thể phủ định của động từ', '{55, 56, 57, 58, 59, 60, 62, 63, 64}', '[{"sub_title": "", "content": [{"c": "Một số các phó từ trong tiếng Anh mang nghĩa phủ định (negative adverb), khi đã dùng nó thì trong câu không dùng cấu tạo phủ định của động từ nữa", "f": ["subject + negative adverb + positive verb"], "e": []}, {"c": "Hardly, barely, scarcely = almost nothing/ almost not at all = hầu như không. Hardly ever, seldom, rarely = almost never = hầu như không bao giờ.", "f": ["subject + to be + negative adverb"], "e": [{"e": "John rarely comes to class on time. (John chẳng mấy khi đến lớp đúng giờ)"}, {"e": "Tom hardly studied lastnight. (Tôm chẳng học gì tối qua)"}, {"e": "She scarcely remembers the accident. (Cô ấy khó mà nhớ được vụ tai nạn)"}, {"e": "We seldom see photos of these animals. (Chúng tôi hiếm khi thấy ảnh của những động vật này)"}]}, {"c": "Lưu ý rằng các phó từ này không mang nghĩa phủ định hoàn toàn mà mang nghĩa gần như phủ định. Đặc biệt là những từ như barely và scarcely khi đi với những từ như enough và only hoặc những thành ngữ chỉ sự chính xác.", "f": [], "e": [{"e": "Do you have enough money for the tution fee?"}, {"e": "Only barely (Vừa đủ)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (62, '2', 'Thể phủ định của một số động từ đặc biệt', '{"Câu phủ định (negation)"}', 'Câu phủ định (negation), Thể phủ định của một số động từ đặc biệt', '{55, 56, 57, 58, 59, 60, 61, 63, 64}', '[{"sub_title": "", "content": [{"c": "Đối với những động từ như to think, to believe, to suppose, to imagine + that + sentense. Khi chuyển sang câu phủ định, phải cấu tạo phủ định ở các động từ đó, không được cấu tạo phủ định ở mệnh đề thứ hai.", "f": [], "e": [{"e": "I don''t think you came to class yesterday. (Không dùng: I think you didn''t come to class yesterday)"}, {"e": "I don''t believe she stays at home now."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (63, '2', 'No matter', '{"Câu phủ định (negation)"}', 'Câu phủ định (negation), No matter', '{55, 56, 57, 58, 59, 60, 61, 62, 64}', '[{"sub_title": "", "content": [{"c": "Dù có... đi chăng nữa... thì", "f": ["No matter + who/what/which/where/when/how + Subject + verb in present"], "e": [{"e": "No matter who telephones, say I’m out. (Cho dù là ai gọi đến thì hãy bảo là tôi đi vắng)"}, {"e": "No matter where you go, you will find Coca-Cola. (Cho dù anh có đi đến đâu, anh cũng sẽ thấy nhãn hiệu Coca-Cola)"}, {"e": "No matter who = whoever;"}, {"e": "No matter what = whatever"}, {"e": "No matter what (whatever) you say, I won’t believe you. (Cho dù anh có nói gì đi chăng nữa, tôi cũng không tin anh)"}]}, {"c": "Các cấu trúc này có thể đứng cuối câu mà không cần có mệnh đề theo sau", "f": [], "e": [{"e": "I will always love you, no matter what."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (64, '2', 'Cách dùng not ... at all; at all', '{"Câu phủ định (negation)"}', 'Câu phủ định (negation), Cách dùng not ... at all; at all', '{55, 56, 57, 58, 59, 60, 61, 62, 63}', '[{"sub_title": "", "content": [{"c": "Chẳng chút nào: Chúng thường đứng cuối câu phủ định", "f": ["Not ... at all:"], "e": [{"e": "I didn’t understand anything at all."}, {"e": "She was hardly frightened at all"}]}, {"c": "At all còn được dùng trong câu hỏi, đặc biệt với những từ như if/ever/any...", "f": [], "e": [{"e": "Do you play poker at all? (Anh có chơi bài poker được chứ?)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (65, '2', 'Câu mệnh lệnh', '{}', 'Câu mệnh lệnh, Câu mệnh lệnh', '{66, 67, 68}', '[{"sub_title": "", "content": [{"c": "Câu mệnh lệnh là câu có tính chất sai khiến nên còn gọi là câu cầu khiến. Một người ra lệnh hoặc yêu cầu cho một người khác làm một việc gì đó. Nó thường theo sau bởi từ please. Chủ ngữ của câu mệnh lệnh được ngầm hiểu là you. Luôn dùng dạng thức nguyên thể (không có to) của động từ trong câu mệnh lệnh. Câu mệnh lệnh chia làm 2 loại: Trực tiếp và gián tiếp.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (66, '2', 'Mệnh lệnh thức trực tiếp', '{"Câu mệnh lệnh"}', 'Câu mệnh lệnh, Mệnh lệnh thức trực tiếp', '{65, 67, 68}', '[{"sub_title": "", "content": [{"c": "", "f": [], "e": [{"e": "Close the door"}, {"e": "Please turn off the light."}, {"e": "Open the window."}, {"e": "Be quiet."}]}, {"c": "Sau đây là lời thoại trong một đoạn quảng cáo bia trên TV:", "f": [], "e": []}, {"c": "Tên cướp xông vào một quán bia, rút súng ra chĩa vào mọi người và quát:", "f": [], "e": [{"e": "Give me your jewelry! Don’t move!"}]}, {"c": "Một thanh niên từ từ tiến lại từ phía sau, gí một chai bia Laser lạnh vào gáy hắn:", "f": [], "e": [{"e": "Drop your weapon!"}]}, {"c": "Tên cướp tưởng sau gáy hắn là một họng súng liền buông vũ khí đầu hàng.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (67, '2', 'Mệnh lệnh gián tiếp', '{"Câu mệnh lệnh"}', 'Câu mệnh lệnh, Mệnh lệnh gián tiếp', '{65, 66, 68}', '[{"sub_title": "", "content": [{"c": "Dạng thức gián tiếp thường được dùng với", "f": ["to order/ ask/ say/ tell sb to do smt."], "e": [{"e": "John asked Jill to turn off the light."}, {"e": "Please tell Jaime to leave the room."}, {"e": "I ordered him to open the book."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (68, '2', 'Dạng phủ định của câu mệnh lệnh', '{"Câu mệnh lệnh"}', 'Câu mệnh lệnh, Dạng phủ định của câu mệnh lệnh', '{65, 66, 67}', '[{"sub_title": "", "content": [{"c": "Ở dạng phủ định, thêm Don’t vào trước động từ trong câu trực tiếp (kể cả động từ be) hoặc thêm not vào trước động từ nguyên thể trong câu gián tiếp.", "f": [], "e": [{"e": "Don’t move! Or I’ll shoot. (Đứng im, không tao bắn)"}, {"e": "Don’t turn off the light when you go out."}, {"e": "Don’t be silly. I’ll come back. (Đừng có ngốc thế, rồi anh sẽ về mà)"}, {"e": "John asked Jill not to turn off the light."}, {"e": "Please tell Jame not to leave the room."}, {"e": "I ordered him not to open his book."}]}, {"c": "Chú ý: let''s khác let us, let''s go: mình đi nào", "f": [], "e": []}, {"c": "let us go: hãy để chúng tôi đi. Câu hỏi có đuôi của let''s là shall we", "f": [], "e": [{"e": "Let''s go out for dinner, shall we"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (69, '2', 'Các trợ động từ (modal auxiliaries)', '{}', 'Các trợ động từ (modal auxiliaries), Các trợ động từ (modal auxiliaries)', '{70, 71}', '[{"sub_title": "", "content": [{"c": "Các trợ động từ (còn gọi là trợ động từ hình thái) dùng để bổ nghĩa thêm cho động từ chính về tính chất, mức độ, khả năng, hình thái, ... của hành động. Chúng ta sẽ lần lượt đề cập đến ý nghĩa của chúng ở các phần sau. Do chúng là các trợ động từ, nên không thay thế được cho động từ chính (phải luôn có động từ chính đi kèm), cũng như không dùng kèm với các trợ động từ khác cùng loại hoặc với các trợ động từ do, does, did. Cũng không dùng tiểu từ to trước và sau các trợ động từ. Trợ động từ hình thái không biến đổi theo ngôi hay theo số (số ít và số nhiều như nhau, ngôi thứ nhất, thứ hai hay thứ ba cũng như nhau). Động từ chính đi sau trợ động từ cũng không chia (luôn ở dạng nguyên thể không có to).", "f": [], "e": []}, {"c": "present tense", "f": [], "e": [{"e": "will, can, may, shall, must (have to), would (use to)"}]}, {"c": "past tense", "f": [], "e": [{"e": "could, might, should (ought to) (had better) (had to)"}]}, {"c": "Các từ đặt trong ngoặc là các động từ bán hình thái. Chúng có vai trò và ý nghĩa giống như các trợ động từ hình thái nhưng về ngữ pháp thì không giống, bởi vì chúng biến đổi theo ngôi và số. Ví dụ về cách dùng trợ động từ:", "f": [], "e": [{"e": "I can swim; she can swim, too. (không chia theo ngôi)"}, {"e": "He can swim. (Không dùng: He cans swim hoặc He can swims)"}, {"e": "They will leave now. (Không dùng: They will leaving now hoặc They will can leave now.)"}, {"e": "They have to go now."}, {"e": "He has to go now. (chia theo ngôi)."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (70, '2', 'Câu phủ định dùng trợ động từ', '{"Các trợ động từ (modal auxiliaries)"}', 'Các trợ động từ (modal auxiliaries), Câu phủ định dùng trợ động từ', '{69, 71}', '[{"sub_title": "", "content": [{"c": "Trong câu phủ định, thêm not vào sau trợ động từ, trước động từ chính", "f": [], "e": [{"e": "John will leave now. => John will not leave now."}, {"e": "He can swim => He can not swim."}]}, {"c": "Chú ý khi viết tắt: will not => won''t; must not => musn''t; would not => wouldn''t; could not => couldn''t; can not => can''t.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (71, '2', 'Câu nghi vấn dùng trợ động từ', '{"Các trợ động từ (modal auxiliaries)"}', 'Các trợ động từ (modal auxiliaries), Câu nghi vấn dùng trợ động từ', '{69, 70}', '[{"sub_title": "", "content": [{"c": "Trong câu hỏi, đặt trợ động từ ở đầu câu", "f": [], "e": [{"e": "John will leave now. =>Will he leave now?"}]}, {"c": "Xin nhắc lại, trợ động từ hình thái luôn đi với dạng thức nguyên thể không có to của động từ. Vì vậy, sau trợ động từ hình thái không bao giờ có các dạng [verb-ing], [verb+s], [to + verb] hay thì quá khứ của động từ. Chỉ có hai cách sử dụng trợ động từ hình thái", "f": [], "e": []}, {"c": "(1) modal + [simple form of verb]: would be, can go, will have, must see, ...", "f": [], "e": []}, {"c": "(2) modal + have + [verb in past participle]: could have gone, would have been,..", "f": [], "e": []}, {"c": "Tất nhiên trong cách (2), từ have chính là động từ nguyên thể không có to; không được thay thế nó bằng has hay had.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (72, '2', 'Câu điều kiện', '{}', 'Câu điều kiện, Câu điều kiện', '{73, 74, 75, 76, 77, 78, 79, 80}', '[{"sub_title": "", "content": [{"c": "Các trợ động từ hình thái như will, would, can, could thường xuất hiện trong các câu điều kiện. Các câu điều kiện thường chứa từ if (nếu). Có hai loại câu điều kiện là điều kiện có thực và điều kiện không có thực.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (73, '2', 'Điều kiện có thể thực hiện được (điều kiện có thực hay điều kiện dạng i)', '{"Câu điều kiện"}', 'Câu điều kiện, Điều kiện có thể thực hiện được (điều kiện có thực hay điều kiện dạng i)', '{72, 74, 75, 76, 77, 78, 79, 80}', '[{"sub_title": "", "content": [{"c": "Câu điều kiện có thực là câu mà người nói dùng để diễn đạt một hành động hoặc một tình huống thường xảy ra (thói quen) hoặc sẽ xảy ra (trong tương lai) nếu điều kiện ở mệnh đều chính được thoả mãn. Nếu nói về tương lai, dạng câu này được sử dụng khi nói đến một điều kiện có thể thực hiện được hoặc có thể xảy ra.", "f": [], "e": []}]}, {"sub_title": "TƯƠNG LAI (FUTURE ACTION)", "content": [{"c": "", "f": ["If + S + simple present ... S + (will/can/shall/may/must) + Verb in simple form"], "e": [{"e": "If he tries much more, he will improve his English."}, {"e": "If I have money, I will buy a new car."}]}]}, {"sub_title": "THÓI QUEN (HABITUAL)", "content": [{"c": "", "f": ["If + S + simple present tense ... + simple present tense ..."], "e": [{"e": "If the doctor has morning office hours, he visits every patiens in the affternoon."}, {"e": "I usually walk to school if I have enough time."}]}]}, {"sub_title": "MỆNH LỆNH (COMMAND)", "content": [{"c": "", "f": ["If + S + simple present tense ... + command form of verb + ..."], "e": [{"e": "If you go to the Post Office, mail this letter for me."}, {"e": "Please call me if you hear anything from Jane."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (74, '2', 'Điều kiện không thể thực hiện được (điều kiện không có thực hay điều kiện dạng ii, iii)', '{"Câu điều kiện"}', 'Câu điều kiện, Điều kiện không thể thực hiện được (điều kiện không có thực hay điều kiện dạng ii, iii)', '{72, 73, 75, 76, 77, 78, 79, 80}', '[{"sub_title": "", "content": [{"c": "Câu điều kiện không có thực dùng để diễn tả một hành động hoặc một trạng thái sẽ xảy ra hoặc đã có thể xảy ra nếu như tình huống được đặt ra trong câu khác với thực tế đang xảy ra hoặc đã xảy ra. Câu điều kiện không có thực thường gây nhầm lẫn vì sự thực về sự kiện mà câu thể hiện lại trái ngược với cách thể hiện của câu: nếu động từ của câu là khẳng định thì ý nghĩa thực của câu lại là phủ định và ngược lại.", "f": [], "e": [{"e": "If I were rich, I would travel around the world. (I am not rich) (I’m not going to travel around the world)"}, {"e": "If I hadn’t been in a hurry, I wouldn’t have had an accident. (I was in a hurry) (I had an accident)"}]}]}, {"sub_title": "Điều kiện không có thực ở hiện tại (dạng II)", "content": [{"c": "PRESENT OR FUTURE TIME", "f": ["If + S + simple past ... + (would/could/should/might) + [verb in simple form]"], "e": [{"e": "If I had enough money now, I would buy a tourist trip to the moon."}, {"e": "He would tell you about it if he were here."}, {"e": "If he didn’t speak so quickly, you could understand him. (He speaks very quicky) (You can’t understand him) (Động từ to be phải chia là were ở tất cả các ngôi)"}, {"e": "If I were you, I wouldn''t go to that movie."}]}]}, {"sub_title": "Điều kiện không có thực trong quá khứ (dạng III)", "content": [{"c": "PAST TIME", "f": ["If + S + past perfect ... + (would/could/should/might) + have + [past participle verb]"], "e": [{"e": "If we had known that you were there, we would have written you a letter. (We didn’t know ...) (We didn’t write you a letter)"}, {"e": "If we hadn’t lost our way, we would have arrived sooner."}, {"e": "If he had studied harder for that test, he would have passed it."}]}, {"c": "Chú ý rằng cũng có thể thể hiện một điều kiện không có thực mà không dùng if. Trong trường hợp đó, trợ động từ had được đưa lên đầu câu, đứng trước chủ ngữ. Mệnh đề điều kiện sẽ đứng trước mệnh đề chính.", "f": [], "e": [{"e": "Had we known that you were there, we would have written you a letter."}, {"e": "Had he studied harder for the test, he would have passed it."}]}, {"c": "Lưu ý: Câu điều kiện không phải lúc nào cũng tuân theo qui luật trên. Trong một số trường hợp đặc biệt, một vế của điều kiện là quá khứ nhưng vế còn lại có thể ở hiện tại (do thời gian qui định).", "f": [], "e": [{"e": "If she had caught the train, she would be here by now."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (75, '2', 'Cách sử dụng will, would, could, should trong một số trường hợp khác', '{"Câu điều kiện"}', 'Câu điều kiện, Cách sử dụng will, would, could, should trong một số trường hợp khác', '{72, 73, 74, 76, 77, 78, 79, 80}', '[{"sub_title": "", "content": [{"c": "Thông thường các trợ động từ này không được sử dụng với if trong mệnh đề điều kiện của câu điều kiện, tuy nhiên vẫn có một số ngoại lệ như sau:", "f": [], "e": []}, {"c": "If you (will/would): Nếu\tvui lòng. Thường được dùng trong các yêu cầu lịch sự. Would lịch sự hơn will.", "f": [], "e": [{"e": "If you will/would wait for a moment, I will go and see if Mr Conner is here."}]}, {"c": "If + Subject + Will/Would: Nếu\tchịu. Để diễn đạt ý tự nguyện.", "f": [], "e": [{"e": "If he will listen to me, I can help him."}]}, {"c": "Will còn được dùng theo mẫu câu này để diễn đạt sự ngoan cố: Nếu ..... nhất định, Nếu\tcứ.", "f": [], "e": [{"e": "If you will turn on the music loudly so late tonight, no wonder why your neighbours complain."}]}, {"c": "If you could: Xin vui lòng. Diễn đạt lịch sự 1 yêu cầu mà người nói cho rằng người kia sẽ đồng ý như là một lẽ đương nhiên.", "f": [], "e": [{"e": "If you could open your book, please."}]}, {"c": "If + Subject + should +\t+ command: Ví phỏng như. Diễn đạt một tình huống dù có thể xảy ra được song rất khó.", "f": [], "e": [{"e": "If you should find any difficulty in using that TV, please call me."}]}, {"c": "Có thể đảo should lên trên chủ ngữ và bỏ if", "f": [], "e": [{"e": "Should you find any difficulty in using that TV, please call me."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (76, '2', 'Cách sử dụng if trong một số trường hợp khác', '{"Câu điều kiện"}', 'Câu điều kiện, Cách sử dụng if trong một số trường hợp khác', '{72, 73, 74, 75, 77, 78, 79, 80}', '[{"sub_title": "", "content": [{"c": "If... then: Nếu... thì", "f": [], "e": [{"e": "If she can’t come to us, then we will have to go and see her."}]}, {"c": "If dùng trong dạng câu suy diễn logic (không phải câu điều kiện): Động từ ở các mệnh đề diễn biến bình thường theo thời gian của chính nó.", "f": [], "e": [{"e": "If you want to learn a musical instrument, you have to practice."}, {"e": "If you did not do much maths at school, you will find economics difficult to understand."}, {"e": "If that was Marry, why didn’t she stop and say hello."}]}, {"c": "If... should = If... happen to... = If... should happen to... diễn đạt sự không chắc chắn (Xem thêm phần sử dụng should ở trên)", "f": [], "e": [{"e": "If you should happen to pass a supermarket, perhaps you could get some eggs. (Ngộ nhỡ mà anh có tình cờ ghé qua chợ có lẽ mua cho em ít trứng)"}]}, {"c": "If.. was/were to... Diễn đạt điều kiện không có thật hoặc tưởng tượng. Nó gần giống câu điều kiện không có thật ở hiện tại.", "f": [], "e": [{"e": "If our boss was/were to come in now (= if the boss came in now), we would be in real trouble."}, {"e": "What would we do if I was/were to lose my job."}]}, {"c": "Hoặc có thể diễn đạt một ý lịch sự khi đưa ra đề nghị", "f": [], "e": [{"e": "If you were to move your chair a bit, we could all sit down. (Nếu anh vui lòng dịch ghế của anh ra một chút thì chúng ta có thể cùng ngồi được)"}]}, {"c": "Note: Cấu trúc này tuyệt đối không được dùng với các động từ tĩnh tại hoặc chỉ trạng thái tư duy Correct: If I knew her name, I would tell you. Incorrect: If I was/were to know...", "f": [], "e": []}, {"c": "If it + to be + not + for: Nếu không vì, nếu không nhờ vào. Thì hiện tại:", "f": [], "e": [{"e": "If it wasn’t/weren’t for the children, that couple wouldn’t have any thing to talk about. (Nếu không vì những đứa con thì vợ chồng nhà ấy chả có chuyện gì mà nói)"}]}, {"c": "Thì quá khứ", "f": [], "e": [{"e": "If it hadn’t been for your help, I don’t know what to do. (Nếu không nhờ vào sự giúp đỡ của anh thì tôi cũng không biết phải làm gì đây)."}]}, {"c": "Có thể đảo lại", "f": [], "e": [{"e": "Had it not been for your help, I don’t know what to to."}]}, {"c": "Not đôi khi được thêm vào những động từ sau if để bày tỏ sự nghi ngờ, không chắc chắn. (Có nên ... Hay không ...)", "f": [], "e": [{"e": "I wonder if we shouldn’t ask the doctor to look at Mary."}]}, {"c": "It would... if + subject + would... (sẽ là... nếu – không được dùng trong văn viết) It would be better if they would tell every body in advance. (Sẽ là tốt hơn nếu họ kể cho mọi người từ trước)", "f": [], "e": [{"e": "How would we feel if this would happen to our family. (Ta sẽ cảm thấy thế nào nếu điều này xảy ra đối với gia đình chúng ta.)"}]}, {"c": "If...’d have...’d have: Dùng trong văn nói, không dùng trong văn viết, diễn đạt điều kiện không thể xảy ra ở quá khứ", "f": [], "e": [{"e": "If I’d have known, I’d have told you."}, {"e": "If she’d have recognized him it would have been funny."}]}, {"c": "If + preposition + noun/verb... (subject + be bị lược bỏ) If in doubt, ask for help. (= If you are in doubt, ...)", "f": [], "e": [{"e": "If about to go on a long journey, try to have a good nights sleep. (= If you are about to go on...)"}]}, {"c": "If dùng với một số từ như any/anything/ever/not để diễn đạt phủ định There is little if any good evidence for flying saucers. (There is little evidence, if there is any at all, for flying saucers) (Có rất ít bằng chứng về đĩa bay, nếu quả là có thực)", "f": [], "e": [{"e": "I’m not angry. If anything, I feel a little surprised. (Tôi không giận dữ đâu. Mà có chăng tôi cảm thấy hơi ngạc nhiên) Cách nói này còn diễn đạt ý kiến ướm thử: Nếu có..."}, {"e": "I’d say he was more like a father, if anything (Tôi xin nói rằng ông ấy còn hơn cả một người cha, nếu có thể nói thế.)"}, {"e": "He seldom if ever travel abroad. (Anh ta chả mấy khi đi ra nước ngoài)"}, {"e": "Usually, if not always, we write “cannot” as one word (Thông thường, nhưng không phải là luôn luôn... )"}]}, {"c": "If + Adjective = although (cho dù là) Nghĩa không mạnh bằng although - Dùng để diễn đạt quan điểm riêng hoặc vấn đề gì đó không quan trọng.", "f": [], "e": [{"e": "His style, if simple, is pleasant to read. (Văn phong của ông ta, cho dù là đơn giản, thì đọc cũng thú vị)"}, {"e": "The profits, if little lower than last year’s, are still extremely wealthy (Lợi nhuận, cho dù là có thấp hơn năm qua một chút, thì vẫn là rất lớn.)"}]}, {"c": "Cấu trúc này có thể thay bằng may..., but", "f": [], "e": [{"e": "His style may be simple, but it is pleasant to read."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (77, '2', 'Cách sử dụng hope và wish.', '{"Câu điều kiện"}', 'Câu điều kiện, Cách sử dụng hope và wish.', '{72, 73, 74, 75, 76, 78, 79, 80}', '[{"sub_title": "", "content": [{"c": "Hai động từ này tuy cùng nghĩa nhưng khác nhau về cách sử dụng và ngữ pháp. Hope dùng để diễn đạt một hành động hoặc tình huống có thể sẽ xảy ra hoặc có thể đã xảy ra, còn wish dùng để diễn đạt một điều chắc chắn sẽ không xảy ra hoặc chắc chắn đã không xảy ra. Thì của mệnh đề sau hope (hi vọng rằng) có thể là bất kỳ thì nào. Thì của mệnh đề sau wish bắt buộc không được ở thì hiện tại.", "f": [], "e": [{"e": "We hope that they will come. (We don’t know if they are coming or not)"}, {"e": "We wish that they could come. (We know they can’t come)"}, {"e": "We hope that he came there yesterday. (We don’t know if he came there or not.)"}, {"e": "We wish that he had come there yesterday. (He didn’t come)"}]}]}, {"sub_title": "Wish ở tương lai", "content": [{"c": "", "f": ["S + wish + (that) + S + (could + verb/would + verb + were + verb-ing) + ..."], "e": []}, {"c": "That là tuỳ chọn (có hoặc không có). Hai chủ ngữ (S) có thể giống nhau hoặc khác nhau.", "f": [], "e": [{"e": "We wish that you could come to the party tonight. (We known you can''t come)"}]}]}, {"sub_title": "Wish ở hiện tại", "content": [{"c": "", "f": ["S + wish + (that) + S + simple past tense"], "e": []}, {"c": "Động từ ở mệnh đề sau wish sẽ chia ở Simple past, to be phải chia là were ở tất cả các ngôi.", "f": [], "e": [{"e": "I wish that I had enough time to finish my homework."}]}]}, {"sub_title": "Wish ở quá khứ", "content": [{"c": "", "f": ["S + wish + (that) + S + (past perfect/could heve + [verb in participle])"], "e": []}, {"c": "Động từ ở mệnh đề wish sẽ chia ở Past perfect hoặc could have + P2.", "f": [], "e": [{"e": "I wish that I had washed the clothes yesterday."}, {"e": "She wishes that she could have been there."}]}, {"c": "Lưu ý 1: Động từ ở mệnh đề sau wish bắt buộc phải ở dạng điều kiện không thể thực hiện được nhưng điều kiện ấy ở thì nào lại phụ thuộc vào chính thời gian của bản thân mệnh đề chứ không phụ thuộc vào thì của wish.", "f": [], "e": [{"e": "She wishes that she could have gone earlier yesterday.(Past)"}, {"e": "He wished that he would come to visit me next week.(Future)"}, {"e": "The photographer wished we stood clother than we are standing now. (Present)."}]}, {"c": "Lưu ý 2: Cần phân biệt wish (ước gì/ mong gì) với wish mang nghĩa \"chúc\" trong mẫu câu: to wish sb smt", "f": [], "e": [{"e": "I wish you a happy birthday."}]}, {"c": "Lưu ý 3: và phân biệt với wish mang nghĩa \"muốn\":", "f": ["wish to do smt (Muốn làm gì)"], "e": [{"e": "Why do you wish to see the manager I wish to make a complaint."}]}, {"c": "", "f": ["To wish smb to do smt (Muốn ai làm gì)"], "e": [{"e": "The government does not wish Dr.Jekyll Hyde to accept a professorship at a foreign university."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (78, '2', 'Cách sử dụng as if, as though (cứ như là, như thể là)', '{"Câu điều kiện"}', 'Câu điều kiện, Cách sử dụng as if, as though (cứ như là, như thể là)', '{72, 73, 74, 75, 76, 77, 79, 80}', '[{"sub_title": "", "content": [{"c": "Mệnh đề đằng sau hai thành ngữ này luôn ở dạng điều kiện không thể thực hiện được. Có hai trường hợp", "f": [], "e": []}]}, {"sub_title": "Ở thì hiện tại", "content": [{"c": "Nếu động từ ở mệnh đề trước chia ở thì hiện tại đơn giản thì động từ ở mệnh đề sau chia ở quá khứ đơn giản. To be phải chia là were ở tất cả các ngôi.", "f": [], "e": [{"e": "The old lady dresses as if it were winter even in the summer. (Bà cụ ăn mặc cứ như bây giờ là mùa đông) (It is not winter now)"}, {"e": "He acts as though he were rich. (Anh ta cứ làm như thể là anh ta giàu có lắm) (He is not rich infact)"}, {"e": "He talks as if he knew everything in the world."}]}]}, {"sub_title": "Thì quá khứ", "content": [{"c": "Nếu động từ ở mệnh đề trước chia ở quá khứ đơn giản thì động từ ở mệnh đề sau chia ở quá khứ hoàn thành.", "f": [], "e": [{"e": "Jeff looked as though he had seen a ghost. (Trông Jeff như thể anh ta vừa gặp ma) (He didn''t see a ghost)"}, {"e": "She talked about the contest as if she had won the grand prize."}]}, {"c": "Lưu ý: Mệnh đề sau as if, as though không phải lúc nào cũng tuân theo qui luật trên. Trong một số trường hợp, nếu điều kiện trong câu là có thật hoặc theo quan niệm của người nói, người viết là có thật thì hai công thức trên không được sử dụng. Động từ ở mệnh đề sau chúng diễn biến bình thường theo mối quan hệ với động từ ở mệnh đề chính.", "f": [], "e": [{"e": "He looks as if he has finished the test."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (79, '2', 'Cách sử dụng used to, (to be/get) used to', '{"Câu điều kiện"}', 'Câu điều kiện, Cách sử dụng used to, (to be/get) used to', '{72, 73, 74, 75, 76, 77, 78, 80}', '[{"sub_title": "Used to + Verb:", "content": [{"c": "Chỉ một thói quen, một hành động thường xuyên xảy ra trong quá khứ.", "f": ["S + used to + [verb in simple form] ..."], "e": [{"e": "When David was young, he used to swim once a day."}]}, {"c": "Nghi vấn: Did + S + use to + verb in simple form", "f": [], "e": [{"e": "Did David use to swim once a day when he was young?"}]}, {"c": "Phủ định: S + didn''t + use to + verb in simple form", "f": [], "e": [{"e": "David didn’t use to swim once a day when he was young."}]}]}, {"sub_title": "To be/ to get used to + V-ing/ Noun: Trở nên quen với.", "content": [{"c": "", "f": ["S + (be/get) + used to + [verb-ing] ..."], "e": [{"e": "He is used to swimming every day. He got used to American food."}]}, {"c": "Lưu ý 1: Used to luôn luôn ở dạng như vậy, không thay đổi theo số, theo ngôi của chủ ngữ. Không được thay thế nó bằng use to.", "f": [], "e": []}, {"c": "Lưu ý 2: Có sự khác nhau về nghĩa giữa used to, be used to và get used to.", "f": [], "e": []}, {"c": "used to: chỉ một thói quen, một hành động thường xuyên trong quá khứ (past time habit): ", "f": [], "e": [{"e": "The program director used to write his own letter."}]}, {"c": "be used to: quen với việc ... (be accustomed to) ", "f": [], "e": [{"e": "I am used to eating at 7:00 PM"}]}, {"c": "get used to: trở nên quen với việc ... (become accustomed to)", "f": [], "e": [{"e": "We got used to cooking our own food when we had to live alone."}]}, {"c": "Lưu ý 3: Có thể dùng would thay thế cho used to mà ý nghĩa và ngữ pháp không đổi.", "f": [], "e": [{"e": "When David was young, he would swim once a day."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (80, '2', 'Cách sử dụng would rather', '{"Câu điều kiện"}', 'Câu điều kiện, Cách sử dụng would rather', '{72, 73, 74, 75, 76, 77, 78, 79}', '[{"sub_title": "", "content": [{"c": "would rather .... than cũng có nghĩa giống như prefer\tto (thích hơn) nhưng ngữ pháp lại không giống.", "f": [], "e": []}, {"c": "Đằng sau would rather bắt buộc phải là một động từ nguyên thể không có to nhưng sau prefer là một V- ing hoặc một danh từ. Khi so sánh hai vế, would rather dùng với than còn prefer dùng với to.", "f": [], "e": [{"e": "We would rather die in freedom than live in slavery."}, {"e": "I would rather drink Coca than Pepsi."}, {"e": "I prefer drinking Coca to drinking Pepsi."}, {"e": "I prefer Coca to Pepsi."}]}, {"c": "Cách sử dụng would rather phụ thuộc vào số lượng chủ ngữ của câu cũng như thì của câu.", "f": [], "e": []}]}, {"sub_title": "Loại câu có một chủ ngữ", "content": [{"c": "Loại câu này dùng would rather ... (than) là loại câu diễn tả sự mong muốn hay ước muốn của một người và chia làm 2 thì:", "f": [], "e": []}, {"c": "Thì hiện tại:", "f": [], "e": []}, {"c": "Sau would rather là nguyên thể bỏ to. Nếu muốn thành lập thể phủ định đặt not trước nguyên thể và bỏ to.", "f": ["S + would rather + [verb in simple form] ..."], "e": [{"e": "Jim would rather go to class tomorrow than today."}, {"e": "Jim would rather not go to class tomorrow."}]}, {"c": "Thì quá khứ:", "f": [], "e": []}, {"c": "Động từ sau would rather phải là have + P2, nếu muốn thành lập thể phủ định đặt not trước have.", "f": ["S + would rather + have + [verb in past participle]"], "e": [{"e": "Jim would rather have gone to class yesterday than today."}, {"e": "Jim would rather not have gone to the class yesterday."}]}]}, {"sub_title": "Loại câu có hai chủ ngữ", "content": [{"c": "Loại câu này dùng would rather that (ước gì, mong gì) và dùng trong một số trường hợp sau:", "f": [], "e": []}, {"c": "Câu cầu kiến ở hiện tại (present subjunctive):", "f": [], "e": []}, {"c": "Là loại câu người thứ nhất muốn người thứ hai làm việc gì (nhưng làm hay không còn phụ thuộc vào người thứ hai). Xem thêm về câu cầu khiến ở phần sau. Trong trường hợp này động từ ở mệng đề hai để ở dạng nguyên thể bỏ to. Nếu muốn thành lập thể phủ định đặt not trước nguyên thể bỏ to.", "f": ["S1 + would rathar that + S2 + [verb in simple form] ..."], "e": [{"e": "I would rather that you call me tomorrow."}, {"e": "He would rather that I not take this train."}]}, {"c": "Ngữ pháp nói ngày nay đặc biệt là ngữ pháp Mỹ cho phép bỏ that trong cấu trúc này mà vẫn giữ nguyên hình thức giả định.", "f": [], "e": []}, {"c": "Câu giả định đối lập với thực tế ở hiện tại", "f": [], "e": []}, {"c": "Động từ sau chủ ngữ hai sẽ chia ở simple past, to be phải chia là were ở tất cả các ngôi.", "f": ["S1 + would rather that + S2 + [verb in simple past tense] ..."], "e": [{"e": "Henry would rather that his girlfriend worked in the same department as he does. (His girlfriend does not work in the same department)"}, {"e": "Jane would rather that it were winter now. (Infact, it is not winter now)"}]}, {"c": "Nếu muốn thành lập thể phủ định dùng didn''t + verb hoặc were not sau chủ ngữ hai.", "f": [], "e": [{"e": "Henry would rather that his girlfriend didn’t work in the same department as he does."}, {"e": "Jane would rather that it were not winter now."}]}, {"c": "Câu giả định trái ngược với thực tế ở quá khứ", "f": [], "e": []}, {"c": "Động từ sau chủ ngữ hai sẽ chia ở dạng past perfect. Nếu muốn thành lập thể phủ định dùng hadn''t + P2.", "f": ["S1 + would rather that + S2 + past perfect ..."], "e": [{"e": "Bob would rather that Jill had gone to class yesterday. (Jill did not go to class yesterday)"}, {"e": "Bill would rather that his wife hadn’t divorced him."}]}, {"c": "Lưu ý: Trong văn nói bình thường hàng ngày người ta dùng wish thay cho would rather that.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (81, '2', 'Cách sử dụng would + like', '{"Cách dùng một số trợ động từ hình thái ở thì hiện tại"}', 'Cách dùng một số trợ động từ hình thái ở thì hiện tại, Cách sử dụng would + like', '{82, 83, 84, 85}', '[{"sub_title": "", "content": [{"c": "Để diễn đạt một cách lịch sự lời mời hoặc ý muốn gì. Không dùng do you want khi mời người khác.", "f": [], "e": [{"e": "Would you like to dance with me?"}, {"e": "I would like to visit Japan."}]}, {"c": "Trong tiếng Anh của người Anh, nếu để diễn đạt sở thích thì sau like là một v-ing.", "f": [], "e": [{"e": "He likes reading novel. (enjoyment)"}]}, {"c": "Nhưng để diễn đạt sự lựa chọn hoặc thói quen thì sau like là một động từ nguyên thể.", "f": [], "e": [{"e": "Between soccer and tennis, I like to see the former. (choice)"}, {"e": "When making tea, he usually likes to put some sugar and a slice of lemon in first. (habit)"}]}, {"c": "Trong tiếng Anh Mỹ thường không có sự phân biệt này, tất cả sau like đều là động từ nguyên thể có to.", "f": [], "e": []}, {"c": "Wouldn''t like = không ưa, trong khi don''t want = không muốn.", "f": [], "e": [{"e": "Would you like somemore coffee ?"}, {"e": "No, thanks/ No, I don''t want any more. (polite)"}, {"e": "I wouldn''t like (thèm vào) (impolite)"}]}, {"c": "Lưu ý rằng khi like được dùng với nghĩa \"cho là đúng\" hoặc \"cho là hay/ khôn ngoan\" thì bao giờ theo sau cũng là một nguyên thể có to (infinitive)", "f": [], "e": [{"e": "She likes the children to play in the garden (She think they are safe playing there) (Cô ấy muốn bọn trẻ con chơi trong vườn cho chắc/ cho an toàn)"}, {"e": "I like to go to the dentist twice a year. (Tôi muốn đến bác sĩ nha khoa 2 lần/ năm cho chắc)"}]}, {"c": "Would like = would care /love/ hate/ prefer + to + verb: khi diễn đạt điều kiện cho một hành động cụ thể ở tương lai.", "f": [], "e": [{"e": "Would you (like/care) to come with me?"}, {"e": "I''d love to"}]}, {"c": "Would like/ would care for/ would enjoy + Verb-ing khi diễn đạt khẩu vị, ý thích nói chung của chủ ngữ.", "f": [], "e": [{"e": "She would like/ would enjoy riding if she could ride better."}, {"e": "I wonder if Tom would care for/ would enjoy hang-gliding."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (82, '2', 'Cách sử dụng could/may/might', '{"Cách dùng một số trợ động từ hình thái ở thì hiện tại"}', 'Cách dùng một số trợ động từ hình thái ở thì hiện tại, Cách sử dụng could/may/might', '{81, 83, 84, 85}', '[{"sub_title": "", "content": [{"c": "Chỉ khả năng có thể xảy ra ở hiện tại nhưng người nói không dám chắc. (Cả 3 trợ động từ đều có giá trị như nhau).", "f": [], "e": [{"e": "It might rain tomorrow."}, {"e": "It may rain tomorrow."}, {"e": "It could rain tomorrow."}, {"e": "It will possibly rain tomorrow Maybe it will rain tomorrow."}]}, {"c": "Chú ý maybe là sự kết hợp của cả may và be nhưng nó là một từ và không phải là trợ động từ. Nó là một phó từ, có nghĩa như perhap (có lẽ)", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (83, '2', 'Cách sử dụng should', '{"Cách dùng một số trợ động từ hình thái ở thì hiện tại"}', 'Cách dùng một số trợ động từ hình thái ở thì hiện tại, Cách sử dụng should', '{81, 82, 84, 85}', '[{"sub_title": "", "content": [{"c": "1)\tĐể diễn đạt một lời khuyên, sự gợi ý, sự bắt buộc (nhưng không mạnh).", "f": [], "e": [{"e": "He should  study tonight."}, {"e": "One should do exercise daily."}, {"e": "You should go on a diet."}, {"e": "She should see a doctor about her pain."}]}, {"c": "2)\tDiễn đạt người nói mong muốn điều gì sẽ xảy ra. ", "f": [], "e": [{"e": "(Expectation) It should rain tomorrow. (I expect it to rain tomorrow)"}, {"e": "My check should arrive next week. (I expect it to arrive next week)"}]}, {"c": "Các cụm từ had better, ought to, be supposed to đều có nghĩa tương đương và cách dùng tương tự với should trong cả hai trường hợp trên (với điều kiện động từ be trong be supposed to phải chia ở thì hiện tại).", "f": [], "e": [{"e": "John ought to study tonight."}, {"e": "John is supposed to study tonight."}, {"e": "John had better study tonight."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (84, '2', 'Cách sử dụng must', '{"Cách dùng một số trợ động từ hình thái ở thì hiện tại"}', 'Cách dùng một số trợ động từ hình thái ở thì hiện tại, Cách sử dụng must', '{81, 82, 83, 85}', '[{"sub_title": "", "content": [{"c": "(1) có nghĩa “phải”: mang nghĩa hoàn toàn bắt buộc (mạnh hơn should). Với should (nên), nhân vật có thể lựa chọn có thực hiện hành động hay không nhưng với must (phải), họ không có quyền lựa chọn.", "f": [], "e": [{"e": "George must call his insurance agent today."}, {"e": "A car must have gasoline to run."}, {"e": "A pharmacist must keep a record of the prescripton that are filled. (Dược sỹ phải giữ lại bản sao đơn thuốc đã được bác sỹ kê - khi bán thuốc cho bệnh nhân)"}, {"e": "An attorney must pass an examination before practicing law. (Luật sư phải qua một kỳ kiểm tra trước khi hành nghề)"}]}, {"c": "(2) có nghĩa “hẳn là”, “chắc đã”: chỉ một kết luận logic dựa trên những hiện tượng đã xảy ra. ", "f": [], "e": [{"e": "John''s lights are out. He must be asleep. (Đèn phòng John đã tắt. Anh ta chắc đã ngủ)"}, {"e": "The grass is wet. It must be raining."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (85, '2', 'Cách sử dụng have to', '{"Cách dùng một số trợ động từ hình thái ở thì hiện tại"}', 'Cách dùng một số trợ động từ hình thái ở thì hiện tại, Cách sử dụng have to', '{81, 82, 83, 84}', '[{"sub_title": "", "content": [{"c": "Người ta dùng have to thay cho must với nghĩa bắt buộc (nghĩa 1). Quá khứ của nó là had to, tương lai là will have to. Chú ý rằng have to không phải là một trợ động từ nhưng nó mang ý nghĩa giống như trợ động từ hình thái must.", "f": [], "e": [{"e": "We will have to take an exam next week."}, {"e": "George has to call his insurance agent today."}]}, {"c": "Để diễn đạt một nghĩa vụ trong quá khứ, phải dùng had to chứ không được dùng must.", "f": [], "e": [{"e": "George had to call his insurance agent yesterday."}, {"e": "Mrs.Kinsey had to pass an examination before she could practice law."}]}, {"c": "Trong câu hỏi, have to thường được dùng thay cho must để hỏi xem bản thân người hỏi bắt buộc phải làm gì hoặc để diễn đạt sự bắt buộc do khách quan đem lại.", "f": [], "e": [{"e": "Guest: Do I have to leave a deposit? (Tôi có phải đặt cọc không)"}, {"e": "Receptionist: No, you needn''t. But you have to leave your I.D card. I''m sorry but that''s the way it is."}]}, {"c": "Trong văn nói, người ta dùng have got to, cũng có nghĩa như have to.", "f": [], "e": [{"e": "He has got to go to the office tonight."}]}, {"c": "Đặc biệt, dùng để nhấn mạnh các trường hợp cá biệt:", "f": [], "e": [{"e": "I have to work everyday except Sunday. But I don''t have got to work a full day on Saturday."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (86, '2', 'Could, may, might + have + p2', '{"Dùng trợ động từ để diễn đạt tình huống quá khứ (modal + perfective)"}', 'Dùng trợ động từ để diễn đạt tình huống quá khứ (modal + perfective), Could, may, might + have + p2', '{87, 88}', '[{"sub_title": "có lẽ đã", "content": [{"c": "Diễn đạt một khả năng có thể đã xảy ra ở quá khứ song người nói không dám chắc.", "f": [], "e": [{"e": "It may have rained last night, but I''m not sure."}, {"e": "He could have forgotten the ticket for the concert last night."}, {"e": "I didn''t hear the telephone ring, I might have been sleeping at that time."}, {"e": "The cause of death could have been bacteria. John migh have gone to the movies yesterday."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (87, '2', 'Should have + p2', '{"Dùng trợ động từ để diễn đạt tình huống quá khứ (modal + perfective)"}', 'Dùng trợ động từ để diễn đạt tình huống quá khứ (modal + perfective), Should have + p2', '{86, 88}', '[{"sub_title": "Lẽ ra phải, lẽ ra nên", "content": [{"c": "Chỉ một việc lẽ ra đã phải xảy ra trong quá khứ nhưng vì lí do nào đó lại không xảy ra.", "f": [], "e": [{"e": "Maria shouldn''t have called John last night. (She did call him)"}, {"e": "John should have gone to the post office this morning."}, {"e": "The policeman should have made a report about the burglary."}]}, {"c": "Chúng ta cũng có thể dùng cụm từ was/were supposed to + [verb in simple form] để thay cho should + perfective.", "f": [], "e": [{"e": "John was supposed to go to the post office this morning."}, {"e": "The policeman was supposed to make a report about the burglary. (Anh ta đã không lập biên bản vụ trộm)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (88, '2', 'Must have + p2', '{"Dùng trợ động từ để diễn đạt tình huống quá khứ (modal + perfective)"}', 'Dùng trợ động từ để diễn đạt tình huống quá khứ (modal + perfective), Must have + p2', '{86, 87}', '[{"sub_title": "chắc là đã, hẳn là đã", "content": [{"c": "Chỉ một kết luận logic dựa trên những hiện tượng có đã xảy ra ở quá khứ (nghĩa 2 của must ở phần trên). Chú ý rằng không bao giờ dùng cấu trúc này với nghĩa 1. Chỉ được dùng had to hoặc should + perfective hoặc be supposed to khi diễn tả một trách nhiệm bắt buộc ở quá khứ.", "f": [], "e": [{"e": "The grass is wet. It must have rained last night. (It probably rained last night)"}, {"e": "Jane did very well on the exam. She must have studied hard."}, {"e": "I didn''t hear you knock, I must have been gardening behind the house"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (89, '2', 'Cách dùng should trong một số trường hợp cụ thể khác', '{}', 'Cách dùng should trong một số trường hợp cụ thể khác, Cách dùng should trong một số trường hợp cụ thể khác', '{}', '[{"sub_title": "", "content": [{"c": "Diễn đạt kết quả của một yếu tố tưởng tượng: Sẽ", "f": [], "e": [{"e": "If I was asked to work on Sunday I should resign."}]}, {"c": "Dùng trong mệnh đề có that sau những tính từ chỉ trạng thái như anxious/ sorry/ concerned/ happy/ delighted...: Lấy làm ... rằng/ lấy làm ...vì", "f": [], "e": [{"e": "I''m anxious that she should be well cared for. (Tôi lo lắng rằng liệu cô ấy có được săn sóc tốt không)"}, {"e": "We are sorry that you should feel uncomfortable (Chúng tôi lấy làm tiếc vì anh thấy không được thoải mái)"}, {"e": "That you should speak to him like that is quite astonishing (Cái cách mà anh nói với anh ấy như vậy quả là đáng ngạc nhiên)."}]}, {"c": "Dùng với if/in case để chỉ một điều khó có thể xảy ra, người ta đưa ra ý kiến chỉ đề phòng ngừa. If you should change your mind, please let me know.", "f": [], "e": [{"e": "In case he should have forgotten to go to the airport, nobody will be there to meet her. (Ngộ nhỡ ông ấy quên ra sân bay, thì sẽ không có ai ở đó đón cô ta mất)"}]}, {"c": "Dùng sau so that/ in order that để chỉ mục đích (Thay cho would/ could)", "f": [], "e": [{"e": "He put the cases in the car so that he should be able to make an early start."}, {"e": "She repeated the instructions slowly in order that he should understand."}]}, {"c": "Dùng trong lời yêu cầu lịch sự", "f": [], "e": [{"e": "I should like to make a phone call, if possible. (tôi xin phép gọi điện thoại nếu tôi có thể)"}]}, {"c": "Dùng với imagine/ say/ think... để đưa ra lời đề nghị: Thiết tưởng, cho là ", "f": [], "e": [{"e": "I should imagine it will take about 3 hours(Tôi thiết tưởng công việc sẽ tốn mất 3 giờ đồng hồ đấy)."}, {"e": "I should say she''s over 40 (Tôi cho là bà ta đã ngoài 40)"}]}, {"c": "Dùng trong câu hỏi để diễn tả sự nghi ngờ, thiếu chắc chắn ", "f": [], "e": [{"e": "How should I know? (Làm sao tôi biết được kia chứ)"}, {"e": "Why should he thinks that? (sao nó lại nghĩ như vậy chứ)"}]}, {"c": "Dùng với các đại từ nghi vấn như what/ where/ who để diễn tả sự ngạc nhiên, thường dùng với \"But\".", "f": [], "e": [{"e": "I was thinking of going to see John when who should appear but John himself. (Tôi đang tính là đến thăm John thì người xuất hiện lại chính là anh ấy)"}, {"e": "What should I find but an enormous spider (Cái mà tôi nhìn thấy lại chính là một con nhện khổng lồ)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (90, '2', 'Tính từ', '{"Tính từ và phó từ"}', 'Tính từ và phó từ, Tính từ', '{91, 92, 93, 94, 95, 96}', '[{"sub_title": "", "content": [{"c": "Tính từ là từ chỉ tính chất, mức độ, phạm vi, ... của một người hoặc vật. Nó bổ nghĩa cho danh từ, đại từ hoặc liên từ (linking verb). Nó trả lời cho câu hỏi What kind? Tính từ luôn đứng trước danh từ được bổ nghĩa:", "f": [], "e": [{"e": "That is my new red car."}]}, {"c": "Trừ trường hợp galore (nhiều, phong phú, dồi dào) và general trong tên các cơ quan, các chức vụ lớn là hai tính từ đứng sau danh từ được bổ nghĩa:", "f": [], "e": [{"e": "There were errors galore in your final test;"}, {"e": "UN Secretary General (Tổng thư ký Liên hợp quốc)."}]}, {"c": "Tính từ đứng sau các đại từ phiếm chỉ (something, anything, anybody...) ", "f": [], "e": [{"e": "It’s something strange."}, {"e": "He is somebody quite unknown."}]}, {"c": "Tính từ được chia làm 2 loại là tính từ mô tả (descriptive adjective) và tính từ giới hạn (limiting adjective). Tính từ mô tả là các tính từ chỉ màu sắc, kích cỡ, chất lượng của một vật hay người. Tính từ giới hạn là tính từ chỉ số lượng, khoảng cách, tính chất sở hữu, ... Tính từ không thay đổi theo ngôi và số của danh từ mà nó bổ nghĩa, trừ trường hợp của these và those.", "f": [], "e": []}]}, {"sub_title": "TÍNH TỪ MÔ TẢ", "content": [{"c": "beautiful, large", "f": [], "e": []}, {"c": "red, interesting, important, colorful", "f": [], "e": []}]}, {"sub_title": "TÍNH TỪ GIỚI HẠN", "content": [{"c": "one, two, first, second (số đếm, số thứ tự)", "f": [], "e": []}, {"c": "my, your, his, this, that, these, those, few, many, much (Tính chất, sở hữu, đại từ chỉ định, số lượng)", "f": [], "e": []}, {"c": "Khi tính từ mô tả bổ nghĩa cho một danh từ đếm được số ít, nó thường có mạo từ a, an hoặc the đứng trước.", "f": [], "e": [{"e": "a pretty girl, an interesting book, the red dress."}]}, {"c": "Tính từ đi trước danh từ nhưng đi sau liên từ (linking verb – xem phần sau). Tính từ chỉ bổ nghĩa cho danh từ, đại từ và liên từ.", "f": [], "e": []}, {"c": "Nếu trong một cụm từ có nhiều tính từ cùng bổ nghĩa cho một danh từ thì thứ tự các tính từ sẽ như sau: tính từ sở hữu hoặc mạo từ, opinion, size, age, shape, color, origin, material, purpose. Chú ý rằng tính từ sở hữu (my, your, our...) hoặc mạo từ (a, an, the) nếu có thì luôn đi trước các tính từ khác. Nếu đã dùng mạo từ thì không dùng tính từ sở hữu và ngược lại. Ví dụ về thứ tự sắp xếp các tính từ:", "f": [], "e": [{"e": "a silly young English man the huge round metal bowl my small red sleeping bag"}]}, {"c": "art/poss: a, the, my", "f": [], "e": []}, {"c": "opinion: silly", "f": [], "e": []}, {"c": "size: huge, small", "f": [], "e": []}, {"c": "age: young", "f": [], "e": []}, {"c": "shape: round", "f": [], "e": []}, {"c": "color: red", "f": [], "e": []}, {"c": "origin: English", "f": [], "e": []}, {"c": "material: metal", "f": [], "e": []}, {"c": "purpose: sleeping\t", "f": [], "e": []}, {"c": "noun: man, bowl, bag", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (91, '2', 'Phó từ', '{"Tính từ và phó từ"}', 'Tính từ và phó từ, Phó từ', '{90, 92, 93, 94, 95, 96}', '[{"sub_title": "", "content": [{"c": "Phó từ bổ nghĩa cho động từ, cho một tính từ hoặc cho một phó từ khác. Nó trả lời cho câu hỏi How?", "f": [], "e": [{"e": "Rita drank too much. (How much did Rita drink?)"}, {"e": "I don''t play tenis very well. (How well do I play?)"}, {"e": "He was driving carelessly. (How was he driving?)"}, {"e": "John is reading carefully. (How is John reading?)"}, {"e": "She speaks Spanish fluently. (How does she speak Spanish?)"}]}, {"c": "Thông thường, các tính từ mô tả có thể biến đổi thành phó từ bằng cách thêm đuôi -ly vào tính từ.", "f": [], "e": [{"e": "He is a careful driver. He always drives carefully."}]}]}, {"sub_title": "TÍNH TỪ", "content": [{"c": "bright, careful, quiet", "f": [], "e": []}]}, {"sub_title": "PHÓ TỪ", "content": [{"c": "brightly, carefully, quietly", "f": [], "e": []}, {"c": "Tuy nhiên, có một số tính từ không theo quy tắc trên:", "f": [], "e": []}, {"c": "1)\tMột số tính từ đặc biệt: good => well; hard => hard; fast => fast; ... She is a good singer. She sings very well.", "f": [], "e": []}, {"c": "2)\tMột số tính từ cũng có tận cùng là đuôi ly (lovely, friendly) nên để thay thế cho phó từ của các tính từ này, người ta dùng: in a + Adj + way/ manner", "f": [], "e": [{"e": "He is a friendly man. He behaved me in a friendly way."}]}, {"c": "Các từ sau cũng là phó từ: so, very, almost, soon, often, fast, rather, well, there, too. Các phó từ được chia làm 6 loại sau:", "f": [], "e": []}, {"c": "ADVERB OF", "f": [], "e": [{"e": "manner, degree, frequency, time, place, disjunctive"}]}, {"c": "EXAMPLE", "f": [], "e": [{"e": "happily, bitterly, totally, completely, nerver, often, recently, just, here, there, hopefully, frankly"}]}, {"c": "TELL US", "f": [], "e": [{"e": "how something happens"}, {"e": "how much ST happens, often go with an adjective"}, {"e": "how often ST happens"}, {"e": "when things happen"}, {"e": "where things happen"}, {"e": "opinion about things happen"}]}, {"c": "Các phó từ thường đóng vai trò trạng ngữ (modifier – xem phần 1) trong câu. Ngoài ra các ngữ giới từ cũng đóng vai trò trạng ngữ như phó từ, chúng bao gồm một giới từ mở đầu và danh từ đi sau để chỉ: địa điểm (at home), thời gian (at 5 pm), phương tiện (by train), tình huống, hành động (in a very difficult situation). Các cụm từ này đều có chức năng và cách sử dụng như phó từ, cũng có thể coi là phó từ.", "f": [], "e": []}, {"c": "Về thứ tự, các phó từ cùng bổ nghĩa cho một động từ thường xuất hiện theo thứ tự: manner, palce, time. Nói chung, phó từ chỉ cách thức của hành động (manner - cho biết hành động diễn ra như thế nào) thường liên hệ chặt chẽ hơn với động từ so với phó từ chỉ địa điểm (place) và phó từ chỉ địa điểm lại liên kết với động từ chặt chẽ hơn phó từ chỉ thời gian (time).", "f": [], "e": [{"e": "The old woman sits quietly by the fire for hours."}]}, {"c": "Về vị trí, các phó từ hầu như có thể xuất hiện bất cứ chỗ nào trong câu, nhưng cũng vì thế, nó là vấn đề khá phức tạp. Vị trí của phó từ có thể làm thay đổi phần nào ý nghĩa của câu. Chúng ta sẽ xem xét từng loại phó từ:", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (92, '2', 'Adverb of manner', '{"Tính từ và phó từ"}', 'Tính từ và phó từ, Adverb of manner', '{90, 91, 93, 94, 95, 96}', '[{"sub_title": "", "content": [{"c": "Các phó từ và cụm phó từ chỉ phương thức của hành động có thể đứng đầu câu, cuối câu hoặc giữa câu (thông dụng hơn). Ví dụ", "f": [], "e": [{"e": "He angrily slammed the door."}, {"e": "He slammed the door angrily."}, {"e": "Angrily he slammed the door."}]}, {"c": "Khi đứng giữa câu, phó từ đứng giữa chủ ngữ và động từ chính, nhưng đứng sau trợ động từ hoặc động từ be nếu có.", "f": [], "e": [{"e": "Ina had carefully placed the vase on the table."}, {"e": "Ina had been carefully arranging the flowers."}]}, {"c": "Vị trí của phó từ có thể làm thay đổi ý nghĩa của câu. Xét các câu sau: She answered the question immediately.", "f": [], "e": [{"e": "She immediately answered the question."}, {"e": "She answered the question foolishly."}, {"e": "She foolishly answered the question."}]}, {"c": "Trong hai câu đầu, ý nghĩa của câu không bị ảnh hưởng bởi vị trí của phó từ, nhưng ở hai câu sau thì có sự khác nhau. Câu 3 cho biết câu trả lời của cô ta là ngốc ngếch, còn câu 4 có nghĩa “Cô ấy thật ngốc ngếch khi trả lời câu hỏi”. Phó từ foolishly khi đứng ở cuối câu thì chỉ bổ nghĩa cho động từ answered the question, nhưng khi được đặt giữa câu lại có liên hệ với chủ ngữ nhiều hơn là với động từ. Hiện tượng này cũng xảy ra với các loại phó từ khác. Nghiên cứu thêm các ví dụ sau:", "f": [], "e": [{"e": "John was able to solve this problem without any help. (John có thể giải được bài toán này mà không cần sự trợ giúp nào)"}, {"e": "Even John was able to solve this problem without any help. (Ngay cả John cũng có thể giải được bài toán này mà ...)"}, {"e": "John was even able to solve this problem without any help. (John có thể thậm chí giải được bài toán này mà ...)"}, {"e": "John was able to solve even this problem without any help. (John có thể giải được ngay cả bài toán này mà không cần ...)"}, {"e": "John was able to solve this problem even without any help. (John có thể giải được bài toán này mà thậm chí không cần tới sự trợ giúp nào)"}, {"e": "He completely failed to agree with me. (Anh ta hoàn toàn không đồng ý với tôi)"}, {"e": "He failed to agree completely with me. (Anh ta không đồng ý hoàn toàn với tôi)"}]}, {"c": "Phó từ chỉ cách thức của hành động thường chỉ đứng giữa câu khi nó là từ đơn (với đuôi –ly). Phần lớn các phó từ thuộc dạng này. Nhưng cũng có một số phó từ là một cụm từ, thường bắt đầu bằng with. Khi đó phải đặt nó ở cuối câu:", "f": [], "e": [{"e": "She was looking through the notes with great interest."}]}, {"c": "Tương tự như vậy, khi hai phó từ ghép song song với nhau, chúng cũng phải ở cuối câu.", "f": [], "e": [{"e": "She angrily called him an idiot."}, {"e": "She called him an idiot angrily and loudly."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (93, '2', 'Adverb of place', '{"Tính từ và phó từ"}', 'Tính từ và phó từ, Adverb of place', '{90, 91, 92, 94, 95, 96}', '[{"sub_title": "", "content": [{"c": "Phó từ chỉ nơi chốn là loại dễ nhớ nhất. Nó luôn xuất hiện cuối câu.", "f": [], "e": [{"e": "I had lunch in the canteen."}]}, {"c": "Trong một số mẫu câu đảo vị trí của chủ ngữ và động từ, nó có thể đứng đầu câu", "f": [], "e": [{"e": "Here is your homework"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (94, '2', 'Adverb of time', '{"Tính từ và phó từ"}', 'Tính từ và phó từ, Adverb of time', '{90, 91, 92, 93, 95, 96}', '[{"sub_title": "", "content": [{"c": "Phó từ chỉ thời gian lại được phân làm 2 loại. Loại chỉ thời gian tuyệt đối (yesterday, tonight, on Sunday, in December...) và loại chỉ thời gian tương đối (recently, already,...). Đối với loại chỉ thời gian tương đối, vị trí của chúng giống như đối với phó từ chỉ cách thức của hành động. Ta chỉ nói đến ở đây loại chỉ thời gian tuyệt đối. Nó có liên kết yếu nhất với động từ trong câu nên có thể xuất hiện bất cứ vị trí nào trong câu, miễn sao không đứng giữa động từ và các trợ động từ. Tuy nhiên, nó thường đứng đầu câu hoặc cuối câu.", "f": [], "e": [{"e": "I didn''t go cycling yesterday."}, {"e": "In 1987 she was working for a bank in Manchester."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (95, '2', 'Adverb of frequency', '{"Tính từ và phó từ"}', 'Tính từ và phó từ, Adverb of frequency', '{90, 91, 92, 93, 94, 96}', '[{"sub_title": "", "content": [{"c": "Phó từ chỉ tần xuất cũng phân làm 2 loại: tần suất tuyệt đối (once a week, twice a month, ...) và tần xuất tương đối (always, nearly always, usually, often, quite often, sometimes, occasionally, hardly ever, never). Tần xuất tuyệt đối được dùng giống như phó từ chỉ thời gian, thường đứng đầu câu hoặc cuối câu. Tần xuất tương đối lại thường đứng giữa câu, sau chủ ngữ và trước động từ chính (nhưng sau động từ be)", "f": [], "e": [{"e": "Sally always gets here on time."}, {"e": "Fred is sometimes late for class."}]}, {"c": "Các phó từ chỉ tần xuất như: occasionally, sometimes, often... có thể đứng đầu câu hoặc cuối câu", "f": [], "e": [{"e": "Things get complicated sometimes."}, {"e": "Often I forget where I put things."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (96, '2', 'Disjunctive adverb', '{"Tính từ và phó từ"}', 'Tính từ và phó từ, Disjunctive adverb', '{90, 91, 92, 93, 94, 95}', '[{"sub_title": "", "content": [{"c": "Phó từ chỉ quan điểm, tình cảm của người nói thường xuất hiện ở đầu câu hoặc cuối câu sau dấu phảy.", "f": [], "e": [{"e": "Thankfully we still had some time to spare."}, {"e": "Clearly we will have to think about this again."}, {"e": "Frankly my dear, I don''t give a damn."}, {"e": "We still had some time to spare, thankfully."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (97, '2', 'Liên từ (linking verb)', '{}', 'Liên từ (linking verb), Liên từ (linking verb)', '{}', '[{"sub_title": "", "content": [{"c": "Có một nhóm các động từ đặc biệt có chức năng nối giữa chủ ngữ và vị ngữ tính từ. Không giống các động từ khác, chúng không thể hiện hành động. Do vậy chúng được bổ nghĩa bởi tính từ chứ không phải phó từ. Người ta gọi chúng là liên từ (linking verb).", "f": [], "e": [{"e": "be, become, remain, stay"}, {"e": "appear, seem, sound, taste"}, {"e": "feel, look, smell"}, {"e": "Mary feels bad about her test grade."}, {"e": "Children become tired quite easily."}, {"e": "Lucy looks radiant in her new dress."}, {"e": "They were sorry to see us leave."}, {"e": "The flower smell sweet."}, {"e": "The soup taste good."}]}]}, {"sub_title": "Liên từ có các tính chất", "content": [{"c": "Không diễn đạt hành động mà diễn đạt trạng thái hoặc bản chất sự việc.", "f": [], "e": []}, {"c": "Đằng sau chúng phải là tính từ chứ không phải là phó từ.", "f": [], "e": []}, {"c": "Không được chia ở thể tiếp diễn dù dưới bất cứ thì nào.", "f": [], "e": []}, {"c": "Be, become, remain còn có thể đứng trước một cụm danh từ chứ không chỉ tính từ: ", "f": [], "e": [{"e": "They remained sad even though I tried to cheer them up. (adjective)"}, {"e": "He remained chairman of the board despite the opposition. (noun)"}, {"e": "Children often become bored at the meetings. (adjective)"}, {"e": "She become class president after a long campaign. (noun)"}, {"e": "Mary will be happy when she hears the good news. (adjective)"}, {"e": "Ted will be a bridegroom this year. (noun)"}]}, {"c": "Feel, look, smell và taste cũng có thể là ngoại động từ khi nó có tân ngữ trự tiếp. Trong trường hợp đó, nó trở thành một động từ mô tả hành động thực sự chứ không còn là một linking verb và do đó nó được bổ nghĩa bởi phó từ chứ không phải tính từ. Chúng được phép chia ở thì tiếp diễn. Nghiên cứu các ví dụ sau:", "f": [], "e": [{"e": "The doctor felt the leg (object) carefully (adverb) to see if there were any broken bones"}, {"e": "Mike felt happy (adjective) after passing his law school exam"}, {"e": "Professor Bob looked at the experiment (object) happily (adverb)"}, {"e": "You do not look happy (adjective) today"}, {"e": "The lady is smelling the flowers (object) gingerly (adverb)"}, {"e": "After being closed up for so long, the house smells musty (adjective)"}, {"e": "The chef tasted the meat (object) cautiously (adverb) before prenting it to the king"}, {"e": "The cake tastes delicious (adjective)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (98, '2', 'So sánh ngang bằng', '{"Các dạng so sánh của tính từ và phó từ"}', 'Các dạng so sánh của tính từ và phó từ, So sánh ngang bằng', '{99, 100, 101, 102, 103, 104, 105, 106}', '[{"sub_title": "Cấu trúc sử dụng là as\tas", "content": [{"c": "", "f": ["S+ V + as + (adj/adv) + as + (noun/pronoun)"], "e": [{"e": "My book is as interesting as yours."}, {"e": "His car runs as fast as a race car."}, {"e": "John sings as well as his sister."}, {"e": "Their house is as big as that one."}, {"e": "His job is not as difficult as mine."}, {"e": "They are as lucky as we."}]}, {"c": "Nếu là câu phủ định, as thứ nhất có thể thay bằng so.", "f": [], "e": [{"e": "He is not as tall as his father."}, {"e": "He is not so tall as his father."}]}, {"c": "Sau as phải là một đại từ nhân xưng chủ ngữ, không được là một đại từ tân ngữ.", "f": [], "e": [{"e": "Peter is as tall as I. (ĐÚNG)"}, {"e": "Peter is as tall as me. (SAI)"}]}, {"c": "Danh từ cũng có thể được dùng để so sánh, nhưng nên nhớ trước khi so sánh phải đảm bảo rằng danh từ đó phải có các tính từ tương đương.", "f": [], "e": []}]}, {"sub_title": "adjectives", "content": [{"c": "heavy, light", "f": [], "e": []}, {"c": "wide, narrow", "f": [], "e": []}, {"c": "deep, shallow", "f": [], "e": []}, {"c": "long, short", "f": [], "e": []}, {"c": "big, small", "f": [], "e": []}, {"c": "weight", "f": [], "e": []}, {"c": "width", "f": [], "e": []}, {"c": "depth", "f": [], "e": []}, {"c": "length", "f": [], "e": []}, {"c": "size", "f": [], "e": []}]}, {"sub_title": "", "content": [{"c": "Khi so sánh bằng danh từ, sử dụng cấu trúc sau", "f": [], "e": [{"e": "My house is as high as his."}, {"e": "My house is the same height as his."}]}, {"c": "Chú ý rằng ngược nghĩa với the same...as là different from... Không bao giờ dùng different than. Sau đây là một số ví dụ khác về so sánh bằng danh từ.", "f": [], "e": [{"e": "These trees are the same as those."}, {"e": "He speaks the same language as she."}, {"e": "Her address is the same as Rita’s."}, {"e": "Their teacher is different from ours."}, {"e": "She takes the same course as her husband."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (99, '2', 'So sánh hơn kém', '{"Các dạng so sánh của tính từ và phó từ"}', 'Các dạng so sánh của tính từ và phó từ, So sánh hơn kém', '{98, 100, 101, 102, 103, 104, 105, 106}', '[{"sub_title": "", "content": [{"c": "Trong loại so sánh này người ta chia làm hai dạng: tính từ và phó từ ngắn (chỉ có một hoặc hai âm tiết khi phát âm) và tính từ, phó từ dài (3 âm tiết trở lên). Khi so sánh không ngang bằng", "f": [], "e": []}, {"c": "Đối với tính từ và phó từ ngắn chỉ cần cộng đuôi -er. (thick – thicker; cold-colder; quiet-quieter)", "f": [], "e": []}, {"c": "Đối với tính từ ngắn có một phụ âm tận cùng (trừ w,x,z) và trước đó là một nguyên âm, phải gấp đôi phụ âm cuối. (big-bigger; red-redder; hot-hotter)", "f": [], "e": []}, {"c": "Đối với tính từ, phó từ dài, thêm more hoặc less trước tính từ hoặc phó từ đó (more beautiful; more important; more believable).", "f": [], "e": []}, {"c": "Đối với tính từ tận cùng là phụ âm+y, phải đổi y thành -ier (happy-happier; dry-drier; pretty- prettier).", "f": [], "e": []}, {"c": "Đối với các tính từ có hậu tố -ed, -ful, -ing, -ish, -ous cũng biến đổi bằng cách thêm more cho dù chúng là tính từ dài hay ngắn (more useful, more boring, more cautious)", "f": [], "e": []}, {"c": "Trường hợp đặc biệt: strong-stronger; friendly-friendlier than = more friendly than.", "f": [], "e": []}, {"c": "Chú ý khi đã dùng more thì không dùng hậu tố -er và ngược lại. Các ví dụ sau là SAI: more prettier, more faster, more better", "f": ["S + V + {short adjective + er/short adverb + er*/more + long adjective|adverb/less + adjective|adverb} + than + {noun/pronoun}"], "e": []}]}, {"sub_title": "Chú ý:", "content": [{"c": "1. Chỉ một số phó từ là có đuôi –er, bao gồm: faster, quicker, sooner, latter.", "f": [], "e": []}, {"c": "2. Nhớ dùng dạng thức chủ ngữ của đại từ sau than, không được dùng dạng tân ngữ. Ví dụ về so sánh không ngang bằng:", "f": [], "e": [{"e": "John’s grades are higher than his sister’s."}, {"e": "Today is hotter than yesterday."}, {"e": "This chair is more comfortable than the other."}, {"e": "He speaks Spanish more fluently than I. (không dùng than me)"}, {"e": "He visits his family less frequently than she does."}, {"e": "This year’s exhibit is less impressive than last year’s."}]}, {"c": "So sánh không ngang bằng có thể được nhấn mạnh bằng cách thêm much hoặc far trước cụm từ so sánh.", "f": ["S + V + {far/much} + adjective + er + than + {noun/pronoun}"], "e": [{"e": "A waterlemon is much sweeter than a a lemon."}, {"e": "His car is far better than yours."}]}, {"c": "", "f": ["S + V + {far/much} + more + {adjective/adverb} + than + {noun/pronoun}"], "e": [{"e": "Henry’s watch is far more expensive than mine."}, {"e": "That movie we saw last night was much more interesting than the one on TV."}, {"e": "She dances much more artistically than her predecessor."}, {"e": "He speaks English much more rapidly than he does Spanish."}]}, {"c": "Danh từ cũng được dùng để diễn đạt phép so sánh ngang bằng hoặc hơn/kém. Chú ý dùng tính từ bổ nghĩa đúng với danh từ đếm được hoặc không đếm được.", "f": ["S + V + as + {many/much/little/few} + noun + as + {noun/pronoun}"], "e": [{"e": "He earns as much money as his brother."}, {"e": "They have as few classes as we."}, {"e": "Before payday, I have as little money as my brother."}]}, {"c": "", "f": ["S + V + {more/fewer/less} + noun + than + {noun/pronoun}"], "e": [{"e": "I have more books than she."}, {"e": "February has fewer days than March."}, {"e": "Their job allows them less fredom than ours does."}]}, {"c": "Khi so sánh một người/ một vật với tất cả những người hoặc vật khác phải thêm else sau anything/anybody...", "f": [], "e": [{"e": "He is smarter than anybody else in the class."}]}]}, {"sub_title": "Lưu ý:", "content": [{"c": "Đằng sau as và than của các mệnh đề so sánh có thể loại bỏ chủ ngữ nếu nó trùng hợp với chủ ngữ thứ nhất, đặc biệt là khi động từ sau than và as ở dạng bị động. Lúc này than và as còn có thêm chức năng của một đại từ quan hệ thay thế.", "f": [], "e": [{"e": "Their marriage was as stormy as had been expected (Incorrect: as it had been expected)."}, {"e": "He worries more than was good for him. (Incorrect: than it/what was good for him)."}]}, {"c": "Các tân ngữ cũng có thể bị loại bỏ sau các động từ ở mệnh đề sau THAN và AS:", "f": [], "e": [{"e": "Don’t lose your passport, as I did last year. (Incorrect: as I did it last year)."}, {"e": "They sent more than I had ordered. (Incorrect: than I had ordered it)."}, {"e": "She gets her meat from the same butcher as I go to. (Incorrect: as I go to him)."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (100, '2', 'Phép so sánh không hợp lý', '{"Các dạng so sánh của tính từ và phó từ"}', 'Các dạng so sánh của tính từ và phó từ, Phép so sánh không hợp lý', '{98, 99, 101, 102, 103, 104, 105, 106}', '[{"sub_title": "", "content": [{"c": "Khi dùng câu so sánh nên nhớ các đối tượng dùng để so sánh phải tương đương nhau: người với người, vật với vật. Các lỗi thường mắc phải khi sử dụng câu so sánh không hợp lý được chia làm 3 loại: sở hữu cách, that of và those of.", "f": [], "e": []}]}, {"sub_title": "Sở hữu cách:", "content": [{"c": "", "f": [], "e": [{"e": "Incorrect: His drawings are as perfect as his instructor. (Câu này so sánh các bức tranh với người chỉ dẫn)"}, {"e": "Correct: His drawings are as perfect as his instructor’s. (instructor''s = instructor''s drawings)"}]}]}, {"sub_title": "Dùng thêm that of cho danh từ số ít:", "content": [{"c": "", "f": [], "e": [{"e": "Incorrect: The salary of a professor is higher than a secretary. (Câu này so sánh salary với secretary)"}, {"e": "Correct: The salary of a professor is higher than that of a secretary. (that of = the salary of)"}]}]}, {"sub_title": "Dùng thêm those of cho các danh từ số nhiều:", "content": [{"c": "", "f": [], "e": []}, {"c": "Incorrect: The duties of a policeman are more dangerous than a teacher . (Câu này so sánh duties với teacher)", "f": [], "e": []}, {"c": "Correct: The duties of a policeman are more dangerous than those of a teacher (those of = the duties of)", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (101, '2', 'Các tính từ và phó từ đặc biệt', '{"Các dạng so sánh của tính từ và phó từ"}', 'Các dạng so sánh của tính từ và phó từ, Các tính từ và phó từ đặc biệt', '{98, 99, 100, 102, 103, 104, 105, 106}', '[{"sub_title": "", "content": [{"c": "Một số ít tính từ và phó từ có dạng thức so sánh đặc biệt. Chúng không theo các quy tắc nêu trên. Nghiên cứu ví dụ sau:", "f": [], "e": []}, {"c": "Tính từ hoặc trạng từ", "f": [], "e": [{"e": "far"}, {"e": "little, few"}, {"e": "much, many"}, {"e": "good, well"}, {"e": "bad, badly"}]}, {"c": "So sánh hơn kém", "f": [], "e": [{"e": "farther, further"}, {"e": "less"}, {"e": "more"}, {"e": "better"}, {"e": "worse"}]}, {"c": "So sánh nhất", "f": [], "e": [{"e": "farthest, furthest"}, {"e": "least"}, {"e": "most"}, {"e": "best"}, {"e": "worst"}]}, {"c": "", "f": [], "e": [{"e": "I feel much better today than I did last week."}, {"e": "He has less time now than he had before."}, {"e": "This magainze is better than that one."}, {"e": "He acts worse now than ever before."}]}]}, {"sub_title": "Lưu ý:", "content": [{"c": "farther dùng cho khoảng cách; further dùng cho thông tin hoặc các trường hợp trừu tượng khác.", "f": [], "e": [{"e": "The distance from your house to school is farther than that of mine."}, {"e": "If you want further information, please call to the agent."}, {"e": "Next year he will come to the U.S for his further education."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (102, '2', 'So sánh bội số', '{"Các dạng so sánh của tính từ và phó từ"}', 'Các dạng so sánh của tính từ và phó từ, So sánh bội số', '{98, 99, 100, 101, 103, 104, 105, 106}', '[{"sub_title": "", "content": [{"c": "So sánh bội số là so sánh: bằng nửa (half), gấp đôi (twice), gấp ba (three times),...", "f": ["S + V + number multiple + as + {much/many} + (noun) + as  + {noun/pronoun}"], "e": []}, {"c": "Không được sử dụng so sánh hơn kém mà sử dụng so sánh bằng, khi so sánh phải xác định danh từ là đếm được hay không đếm được, vì đằng trước chúng có many/much", "f": [], "e": [{"e": "This encyclopedy costs twice as much as the other one."}, {"e": "Jerome has half as many records now as I had last year."}, {"e": "At the clambake last week, Fred ate three times as many oysters as Bob."}]}, {"c": "Các lối nói: twice that many/twice that much = gấp đôi ngần ấy... chỉ được dùng trong khẩu ngữ, không được dùng trong văn viết.", "f": [], "e": [{"e": "We have expected 80 people at that rally, but twice that many showned up. (twice as many as that number)."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (103, '2', 'So sánh kép', '{"Các dạng so sánh của tính từ và phó từ"}', 'Các dạng so sánh của tính từ và phó từ, So sánh kép', '{98, 99, 100, 101, 102, 104, 105, 106}', '[{"sub_title": "", "content": [{"c": "Là loại so sánh với cấu trúc: Càng... càng... Các Adj/Adv so sánh phải đứng ở đầu câu, trước chúng phải có The. Dùng một trong hai mẫu câu sau đây để so sánh kép:", "f": ["The + comparative + S + V + the + comparative +  S + V"], "e": [{"e": "The hotter it is, the more miserable I feel. (Trời càng nóng, tôi càng thấy khó chịu)"}, {"e": "The sooner you take your medicince, the better you will feel. (Anh càng uống thuốc sớm bao nhiêu, anh càng cảm thấy dễ chịu bấy nhiêu)"}, {"e": "The bigger they are, the faster they fall. (Chúng càng to bao nhiêu thì càng rơi nhanh bấy nhiêu)"}]}, {"c": "", "f": ["The + more + S + V + the + comparative + S + V"], "e": [{"e": "The more you study, the smarter you will become. (Càng học, anh càng thông minh hơn)"}, {"e": "The more I look into your eyes, the more I love you. (Càng nhìn vào mắt em, anh càng yêu em hơn)"}]}, {"c": "Sau The more ở vế thứ nhất có thể thêm that nhưng không mẫu câu này không phổ biến.", "f": [], "e": [{"e": "The more (that) you study, the smarter you will become."}]}, {"c": "Trong trường hợp nếu cả hai vế đều có chủ ngữ giả it is thì có thể bỏ chúng đi The shorter (it is), the better (it is). Hoặc nếu cả hai vế đều dùng to be thì bỏ đi", "f": [], "e": [{"e": "The closer to the Earth’s pole (is), the greater the gravitational force (is)."}]}, {"c": "Các cách nói: all the better (càng tốt hơn), all the more (càng... hơn), not... any the more... (chẳng... hơn... tí nào), none the more... (chẳng chút nào) dùng để nhấn mạnh cho các tính từ hoặc phó từ được đem ra so sánh. Nó chỉ được dùng với các tính từ trừu tượng và dùng trong văn nói:", "f": [], "e": [{"e": "Sunday mornings were nice."}, {"e": "I enjoyed them all the more because Sue used to come round to breakfast."}, {"e": "He didn’t seem to be any the worse for his experience."}, {"e": "He explained it all carefully, but I was still none the wiser."}]}, {"c": "Cấu trúc này không dùng cho các tính từ cụ thể:", "f": [], "e": [{"e": "Those pills have made him all the slimmer. (SAI)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (104, '2', 'Cấu trúc no sooner... than (vừa mới ... thì đã...)', '{"Các dạng so sánh của tính từ và phó từ"}', 'Các dạng so sánh của tính từ và phó từ, Cấu trúc no sooner... than (vừa mới ... thì đã...)', '{98, 99, 100, 101, 102, 103, 105, 106}', '[{"sub_title": "", "content": [{"c": "Chỉ dùng cho thì quá khứ và thì hiện tại, không dùng cho thì tương lai. No sooner đứng ở đầu một mệnh đề, theo sau nó là một trợ động từ đảo lên trước chủ ngữ để nhấn mạnh, than đứng ở đầu mệnh đề còn lại:", "f": ["No sooner + auxiliary + S + V + than + S + V"], "e": [{"e": "No sooner had we started out for California than it started to rain."}]}, {"c": "Một ví dụ ở thì hiện tại (will được lấy sang dùng cho hiện tại) ", "f": [], "e": [{"e": "No sooner will he arrive than he will want to leave."}]}, {"c": "Lưu ý: No longer có nghĩa là not anymore. Không dùng cấu trúc not longer cho các câu có ý nghĩa không còn ... nữa.", "f": ["S + no longer + Positive Verb"], "e": [{"e": "He no longer studies at the university. (He does not study at the university anymore.)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (105, '2', 'So sánh hơn kém không dùng than (giữa 2 đối tượng)', '{"Các dạng so sánh của tính từ và phó từ"}', 'Các dạng so sánh của tính từ và phó từ, So sánh hơn kém không dùng than (giữa 2 đối tượng)', '{98, 99, 100, 101, 102, 103, 104, 106}', '[{"sub_title": "", "content": [{"c": "Khi so sánh hơn kém giữa 2 người hoặc 2 vật mà không dùng than, muốn nói đối tượng nào đó là tốt hơn, giỏi hơn, đẹp hơn,... thì trước adj và adv so sánh phải có the. Chú ý phân biệt trường hợp này với trường hợp so sánh bậc nhất dưới đây (khi có 3 đối tượng trở lên). Trong câu thường có cụm từ of the two + noun, nó có thể đứng đầu hoặc cuối câu.", "f": [], "e": [{"e": "Harvey is the smarter of the two boys. Of the two shirts, this one is the prettier."}, {"e": "Pealse give me the smaller of the two cakes."}, {"e": "Of the two books, this one is the more interesting."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (106, '2', 'So sánh bậc nhất (từ 3 đối tượng trở lên)', '{"Các dạng so sánh của tính từ và phó từ"}', 'Các dạng so sánh của tính từ và phó từ, So sánh bậc nhất (từ 3 đối tượng trở lên)', '{98, 99, 100, 101, 102, 103, 104, 105}', '[{"sub_title": "", "content": [{"c": "Dùng khi so sánh 3 người hoặc 3 vật trở lên, một trong số đó là ưu việt nhất so với các đối tượng còn lại về một mặt nào đó. Để biến tính từ và phó từ thành dạng so sánh bậc nhất, áp dụng quy tắc sau:", "f": [], "e": []}, {"c": "Đối với tính từ và phó từ ngắn: thêm đuôi -est.", "f": [], "e": []}, {"c": "Đối với tính từ và phó từ dài: dùng most hoặc least.", "f": [], "e": []}, {"c": "Trước tính từ hoặc phó từ so sánh phải có the.", "f": [], "e": []}, {"c": "Dùng giới từ in với danh từ số ít đếm được.", "f": [], "e": []}, {"c": "Dùng giới từ of với danh từ số nhiều đếm được.", "f": ["S + V + the + {adjective + est/most + adjective/least + adjective} + {in + singular count noun/of + plural count noun}"], "e": [{"e": "John is the tallest boy in the family."}, {"e": "Deana is the shortest of the three sisters."}, {"e": "These shoes are the least expensive of all."}, {"e": "Of the three shirts, this one is the prettiest."}]}, {"c": "Sau cụm từ One of the + so sánh bậc nhất + noun phải đảm bảo chắc chắn rằng noun phải là số nhiều, và động từ phải chia ở số ít.", "f": [], "e": [{"e": "One of the greatest tennis players in the world is Johnson."}, {"e": "Kuwait is one of the biggest oil producers in the world."}]}, {"c": "Các phó từ thường không có các hậu tố -er hoặc –est. Chúng được chuyển sang dạng so sánh tương đối bằng cách thêm more hoặc less; sang dạng so sánh tuyệt đối bằng cách thêm most hoặc least phía trước chúng.", "f": [], "e": [{"e": "Sal drove more cautiously than Bob."}, {"e": "Joe dances more gracefully than his partner."}, {"e": "That child behaves most carelessly of all."}]}, {"c": "Một số các tính từ hoặc phó từ mang tính tuyệt đối thì không được dùng so sánh bậc nhất, hạn chế dùng so sánh hơn kém, nếu buộc phải dùng thì bỏ more, chúng gồm: unique/ extreme/ perfect/ supreme/ top/ absolute/prime/ primary", "f": [], "e": [{"e": "His drawings are perfect than mine."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (107, '2', 'Danh từ dùng làm tính từ', '{}', 'Danh từ dùng làm tính từ, Danh từ dùng làm tính từ', '{108}', '[{"sub_title": "", "content": [{"c": "Trong tiếng Anh có nhiều trường hợp một danh từ đứng trước một danh từ khác làm nhiệm vụ của một  tính từ (a wool coat, a gold watch, a history teacher). Danh từ đi trước có vai trò của một tính từ, bổ nghĩa cho danh từ đi sau. Các danh từ đóng vai trò của tính từ luôn luôn ở dạng số ít, cho dù danh từ được chúng bổ nghĩa có thể ở dạng số nhiều (trừ một vài trường hợp cá biệt: a sports car, small-claims court, a no-frills store...). Các liên kết số đếm – danh từ (number-noun) luôn được ngăn cách bởi dấu gạch nối.", "f": [], "e": [{"e": "We took a five-week tour. (We took a tour that lasted five weeks)"}, {"e": "He has a two-year subscription to that magazine. (His subscription to that magazine is for two years)"}, {"e": "These are twenty-dollar shoes. (These shoes cost twenty dollars.)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (108, '2', 'Khi nào dùng danh từ làm tính từ, khi nào dùng tính từ của danh từ đó?', '{"Danh từ dùng làm tính từ"}', 'Danh từ dùng làm tính từ, Khi nào dùng danh từ làm tính từ, khi nào dùng tính từ của danh từ đó?', '{107}', '[{"sub_title": "", "content": [{"c": "Trong một số trường hợp nếu dùng danh từ làm tính từ thì nó mang nghĩa đen: ", "f": [], "e": [{"e": "A gold watch = a watch made of gold."}]}, {"c": "Nhưng nếu dùng tính từ thì nó mang nghĩa bóng:", "f": [], "e": [{"e": "A golden opportunity = a chance that it is very rare and precious (quí giá)."}]}, {"c": "Không được dùng tính từ để bổ nghĩa cho danh từ khi nó chỉ tính chất nghề nghiệp hoặc nội dung, khi đó phải dùng danh từ để đóng vai trò tính từ:", "f": [], "e": [{"e": "a mathematics teacher, a biology book"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (109, '2', 'Cách dùng enough', '{}', 'Cách dùng enough, Cách dùng enough', '{}', '[{"sub_title": "", "content": [{"c": "Enough thay đổi vị trí theo từ loại của từ mà nó bổ nghĩa. Nó luôn đứng sau tính từ và phó từ: ", "f": [], "e": [{"e": "Are those french fries good enough for you?"}, {"e": "She speak Spanish well enough to be an interpreter."}, {"e": "It is not cold enough now to wear a heavy jacket."}]}, {"c": "Nhưng lại đứng trước danh từ:", "f": [], "e": [{"e": "Do you have enough sugar for the cake?"}, {"e": "He does not have enough money to attend the concert."}]}, {"c": "Nếu danh từ đã được nhắc đến trước đó, thì ở dưới có thể dùng enough như một đại từ thay cho danh từ ", "f": [], "e": [{"e": "I forgot money. Do you have enough?"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (110, '2', 'Much & many', '{"Much, many, a lot of và lots of – trong một số trường hợp khác"}', 'Much, many, a lot of và lots of – trong một số trường hợp khác, Much & many', '{111, 112, 113}', '[{"sub_title": "", "content": [{"c": "Many hoặc much thường đứng trước danh từ. Many đi với danh từ đếm được và much đi với danh từ không đếm được:", "f": [], "e": [{"e": "She didn’t eat much breakfast. (Cô ta không ăn sáng nhiều)"}, {"e": "There aren’t many large glasses left. (Không còn lại nhiều cốc lớn)"}]}, {"c": "Tuy nhiên much of có thể đi với tên người và tên địa danh: ", "f": [], "e": [{"e": "I have seen too much of Howard recently. (Gần đây tôi hay gặp Howard)"}, {"e": "Not much of Denmark is hilly. (Đan mạch không có mấy đồi núi)"}]}, {"c": "", "f": ["Many/much of + determiner (a, the, this, my... ) + noun."], "e": [{"e": "You can’t see much of a country in a week. (Anh không thể biết nhiều về một nước trong vòng một tuần đâu.)"}, {"e": "I won’t pass the exam. I have missed many of my lessons. (Tôi sẽ không thoát được kỳ thi này mất, tôi bỏ quá nhiều bài.)"}]}, {"c": "Many và much dùng nhiều trong câu phủ định và câu nghi vấn, trong câu khẳng định có thể dùng các loại từ khác như plenty of, a lot, lots of... để thay thế.", "f": [], "e": [{"e": "How much money have you got? I’ve got plenty."}, {"e": "He got lots of men friends, but he doesn’t know many women."}]}, {"c": "Tuy vậy trong tiếng Anh, much và many vẫn được dùng bình thường trong câu khẳng định.", "f": [], "e": [{"e": "Much has been written about the causes of unemployment in the opinion of many economists."}]}, {"c": "Much dùng như một phó từ (much đứng trước động từ nhưng đứng sau very và sau cấu trúc phủ định của câu):", "f": [], "e": [{"e": "I don’t travel much these days. (much = very often)"}, {"e": "I much appreciate your help. (much=highly)"}, {"e": "We very much prefer the country to the town."}, {"e": "Janet much enjoyed her stay with your family."}]}, {"c": "Much too much / many so many (để nhấn mạnh) dùng độc lập sau mệnh đề chính, trước danh từ mà nó bổ ngữ.", "f": [], "e": [{"e": "The cost of a thirty-second commercial advertisement on a television show is $300,000, much too much for most business."}]}, {"c": "Many a + singular noun + singular verb: Biết bao nhiêu.", "f": [], "e": [{"e": "Many a strong man has disheartened before such a challenge. (Biết bao chàng trai tráng kiện đã nản lòng trước một thử thách như vậy)"}, {"e": "I have been to the top of the Effeil tower many a time."}]}, {"c": "", "f": ["Many’s the + {smt that / sbody who} + singular verb"], "e": [{"e": "Many’s the student who couldn’t follow the post-graduate courses at universities and colleges under the pressure of money. (Biết bao nhiêu sinh viên đã không thể theo học các trường ĐH và CĐ do thiếu tiền)"}, {"e": "Many’s the promise that has been broken. (Biết bao nhiêu lời hứa đã bị phản bội)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (111, '2', 'Phân biệt alot/ lots of/ plenty/ a great deal với many/ much', '{"Much, many, a lot of và lots of – trong một số trường hợp khác"}', 'Much, many, a lot of và lots of – trong một số trường hợp khác, Phân biệt alot/ lots of/ plenty/ a great deal với many/ much', '{110, 112, 113}', '[{"sub_title": "", "content": [{"c": "Các thành ngữ trên đều có nghĩa tương đương với much/ many (nhiều) và most (đa phần). ", "f": [], "e": [{"e": "A lot of/ lots of (informal) = a great deal/ a large number of/ much/ many (formal)."}]}, {"c": "Không có khác nhau gì mấy giữa a lot of và lots of. Chủ ngữ chính sau hai thành ngữ này sẽ quyết định việc chia động từ.", "f": ["a lot of lots of"], "e": []}, {"c": "uncountable noun + singular verb", "f": [], "e": []}, {"c": "plural noun + plural verb", "f": [], "e": []}, {"c": "", "f": [], "e": [{"e": "A lot of time is needed to learn a language."}, {"e": "Lots of us think it’s time for an election."}, {"e": "Plenty of được dùng phổ biến trong informal English."}, {"e": "Don’t rush, there’s plenty of time."}, {"e": "Plenty of shops take checks."}, {"e": "A large a mount of = a great deal of + non-count noun (formal English)"}, {"e": "I have thrown a large amount of old clothing."}, {"e": "Mr Lucas has spent a great deal of time in the Far East."}]}, {"c": "A lot và a great deal có thể được dùng làm phó từ và vị trí của nó là ở cuối câu. ", "f": [], "e": [{"e": "On holiday we walk and swim a lot."}, {"e": "The gorvernment seems to change its mind a great deal."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (112, '2', 'More & most', '{"Much, many, a lot of và lots of – trong một số trường hợp khác"}', 'Much, many, a lot of và lots of – trong một số trường hợp khác, More & most', '{110, 111, 113}', '[{"sub_title": "", "content": [{"c": "More + noun/noun phrase: dùng như một định ngữ We need more time.", "f": [], "e": [{"e": "More university students are having to borrow money these day."}]}, {"c": "", "f": ["More + of + personal/geographic names"], "e": [{"e": "It would be nice to see more of Ray and Barbara. (Rất hay khi được gặp Ray và Barbara thường xuyên hơn.)"}, {"e": "Five hundred years ago, much more of Britain was covered with trees. (500 năm trước đây, đa phần nước Anh phủ nhiều rừng hơn bây giờ nhiều)"}]}, {"c": "", "f": ["More of + determiner/pronoun (a/ the/ my/ this/ him/ them...)"], "e": [{"e": "He is more of a fool than I thought. (Nó là một thằng ngu hơn tôi tưởng nhiều)"}, {"e": "Three more of the missing climbers have been found."}, {"e": "Could I have some more of that smoked fish."}, {"e": "I don’t think any more of them want to come."}]}, {"c": "", "f": ["One more/ two more... + noun/ noun phrase"], "e": [{"e": "There is just one more river to cross."}]}, {"c": "Nhưng phải dùng cardinal number (số đếm) + noun + more (Five dollars more) nếu more mang nghĩa thêm nữa.", "f": [], "e": [{"e": "He has contributed $50, and now he wants to contribute $50 more."}]}, {"c": "Most + noun = Đa phần, đa số", "f": [], "e": [{"e": "Most cheese is made from cow’s milk."}, {"e": "Most Swiss people understand French."}]}, {"c": "Most + determiner/ pronoun (a, the, this, my, him...) = Hầu hết... ", "f": [], "e": [{"e": "He has eaten 2 pizzas and most of a cold chicken."}, {"e": "Most of my friends live abroad."}, {"e": "She has eaten most of that cake."}, {"e": "Most of us thought he was wrong."}]}, {"c": "Most cũng được dùng thay cho một danh từ, nếu ở trên, danh từ hoặc chủ ngữ đó đã được nhắc đến.", "f": [], "e": [{"e": "Some people had difficulty with the lecture, but most understood."}, {"e": "Ann and Robby found a lot of blackberries, but Susan found the most."}]}, {"c": "Most + adjective khi mang nghĩa very (không phổ biến) ", "f": [], "e": [{"e": "That is most kind of you."}, {"e": "Thank you for a most interesting afternoon."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (113, '2', 'Long & (for) a long time', '{"Much, many, a lot of và lots of – trong một số trường hợp khác"}', 'Much, many, a lot of và lots of – trong một số trường hợp khác, Long & (for) a long time', '{110, 111, 112}', '[{"sub_title": "", "content": [{"c": "Long được dùng trong câu hỏi và câu phủ định. ", "f": [], "e": [{"e": "Have you been waiting long?"}, {"e": "It does not take long to get to her house."}, {"e": "She seldom stays long."}]}, {"c": "(for) a long time dùng trong câu khẳng định", "f": [], "e": [{"e": "I waited for a long time, but she didn’t arrive."}, {"e": "It takes a long time to get to her house."}]}, {"c": "Long cũng được dùng trong câu khẳng định khi đi với: too, enough, as, so", "f": [], "e": [{"e": "The meeting went on much too long."}, {"e": "I have been working here long enough."}, {"e": "It’s time to get a new job."}, {"e": "You can stay as long as you want."}, {"e": "Sorry! I took so long."}]}, {"c": "Long before (lâu trước khi)/ long after (mãi sau khi)/ long ago (cách đây đã lâu) ", "f": [], "e": [{"e": "We used to live in Paris, but that was long before you were born."}, {"e": "Long after the accident he used to dream that he was dying."}, {"e": "Long ago, in a distant country, there lived a beautiful princess."}]}, {"c": "Long mang vai trò phó từ phải đặt sau trợ động từ, trước động từ chính: ", "f": [], "e": [{"e": "This is a problem that has long been recognized."}]}, {"c": "All (day/ night/ week/ year) long = suốt cả...", "f": [], "e": [{"e": "She sits dreaming all day long (Cô ta ngồi mơ mộng suốt cả ngày)."}]}, {"c": "Before + long = Ngay bây giờ, chẳng bao lâu nữa ", "f": [], "e": [{"e": "I will be back before long (Tôi sẽ quay lại ngay bây giờ)."}]}, {"c": "Trong câu phủ định for a long time (đã lâu, trong một thời gian dài) khác với for long (ngắn, không dài, chốc lát)", "f": [], "e": [{"e": "She didn’t speak for long. (She only spoke for a short time)"}, {"e": "She didn’t speak for a long time. (She was silent for a long time)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (114, '2', 'Because, because of', '{"Các cụm từ nối mang tính quan hệ nhân quả"}', 'Các cụm từ nối mang tính quan hệ nhân quả, Because, because of', '{115, 116, 117}', '[{"sub_title": "", "content": [{"c": "Sau because phải một mệnh đề hoàn chỉnh (có cả S và V) nhưng sau because of phải là một danh từ hoặc ngữ danh từ.", "f": [], "e": [{"e": "Jan was worried because it had started to rain."}, {"e": "Jan was worried because of the rain."}, {"e": "We arrived late because there was a traffic jam."}, {"e": "We arrived late because of a traffic jam."}]}, {"c": "Có thể dùng because of thay cho on account of và due to và ngược lại. Nhưng thường dùng due to sau động từ to be.", "f": [], "e": [{"e": "The accident was due to the heavy rain."}]}, {"c": "Dùng as a result of để nhấn mạnh hậu quả của hành động hoặc sự vật, sự việc.", "f": [], "e": [{"e": "He was blinded as a result of a terrible accident."}]}, {"c": "Có thể đưa mệnh đề nguyên nhân lên đầu câu:", "f": [], "e": [{"e": "Because of the rain, we have cancelled the party."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (115, '2', 'So that', '{"Các cụm từ nối mang tính quan hệ nhân quả"}', 'Các cụm từ nối mang tính quan hệ nhân quả, So that', '{114, 116, 117}', '[{"sub_title": "", "content": [{"c": "Trong cấu trúc này người ta dùng so that với nghĩa sao cho, để cho. Sau so that phải là một mệnh đề hoàn chỉnh có cả S và V. Động từ ở mệnh đề sau so that phải lùi một thì so với thì của động từ ở mệnh đề chính.", "f": [], "e": [{"e": "He studied very hard so that he could pass the test."}, {"e": "She is sending the package early so that it will arrive in the time for her sister’s birthday."}, {"e": "Susan drove to Miami instead of flying so that she could save money."}]}]}, {"sub_title": "Chú ý:", "content": [{"c": "Phải phân biệt so that trong cấu trúc này với so that mang nghĩa do đó (therefore). Cách phân biệt là ở thì của động từ đằng sau so that mang nghĩa “do đó” diễn biến bình thường so với thì của động từ ở mệnh đề đằng trước.", "f": [], "e": [{"e": "We heard nothing from him so that we wondered if he moved away."}]}, {"c": "Cũng cần phân biệt với cấu trúc so + adj/adv + that nêu ở phần sau.", "f": [], "e": []}, {"c": "Trong tiếng Anh nói có thể bỏ that trong cụm so that nhưng trong tiếng Anh viết không được bỏ that. Đây là bẫy mà bài thi TOEFL thường nêu ra.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (116, '2', 'So và such', '{"Các cụm từ nối mang tính quan hệ nhân quả"}', 'Các cụm từ nối mang tính quan hệ nhân quả, So và such', '{114, 115, 117}', '[{"sub_title": "", "content": [{"c": "Người ta dùng cấu trúc so/such\tthat (đến nỗi mà) theo nhiều cách sử dụng", "f": [], "e": []}]}, {"sub_title": "Dùng với tính từ và phó từ:", "content": [{"c": "", "f": ["S + V + so + {adjective/adverb} + that + S + V"], "e": [{"e": "Terry ran so fast that he broke the previous speed record."}, {"e": "Judy worked so diligently that she received an increase in salary."}, {"e": "She is so beautiful that anyone sees her once will never forget her."}, {"e": "The little boy looks so unhappy that we all feel sorry for him"}]}]}, {"sub_title": "Dùng với danh từ đếm được số nhiều:", "content": [{"c": "", "f": ["S+ V + so  {many/few} + plural count noun + that + S + V"], "e": []}, {"c": "Cấu trúc vẫn là so\tthat nhưng phải dùng many hoặc few trước danh từ đó.", "f": [], "e": [{"e": "I had so few job offers that it wasn''t difficult to select one."}, {"e": "The Smiths had so many children that they form their own baseball team."}]}]}, {"sub_title": "Dùng với danh từ đếm được số ít:", "content": [{"c": "", "f": ["S + V + such + a + adjective + singular count noun + that + S + V"], "e": []}, {"c": "HOẶC", "f": ["S + V + so + adjective + a + singular count noun + that + S + V"], "e": []}, {"c": "", "f": [], "e": [{"e": "It was such a hot day that we decided to stay indoors."}, {"e": "It was so hot a day that we decided to stay indoors."}, {"e": "It was such an interesting book that he couldn’t put it down."}, {"e": "It was so interesting a book that he couldn’t put it down."}]}]}, {"sub_title": "Dùng such trước tính từ + danh từ:", "content": [{"c": "", "f": ["S + V + such + adjective + {pular count noun/no-count noun} + that + S + v"], "e": [{"e": "They are such beautiful pictures that everybody will want one."}, {"e": "This is such difficult homework that I will never finish it."}]}, {"c": "Lưu ý: KHÔNG được dùng so trong trường hợp này.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (117, '3', 'Một số cụm từ nối khác', '{"Các cụm từ nối mang tính quan hệ nhân quả"}', 'Các cụm từ nối mang tính quan hệ nhân quả, Một số cụm từ nối khác', '{114, 115, 116}', '[{"sub_title": "Even if + negative verb", "content": [{"c": "cho dù", "f": [], "e": [{"e": "You must go tomorrow even if you aren''t ready."}]}]}, {"sub_title": "Whether or not + positive verb", "content": [{"c": "dù có hay không", "f": [], "e": [{"e": "You must go tomorrow whether or not you are ready."}]}]}, {"sub_title": "Các từ nghi vấn đứng đầu câu dùng làm từ nối", "content": [{"c": "Động từ theo sau mệnh đề mở đầu phải chia ở ngôi thứ 3 số ít. ", "f": [], "e": [{"e": "Whether he goes out tonight depends on his girldfriend."}, {"e": "Why these pupils can not solve this problems makes the teachers confused."}]}]}, {"sub_title": "Một số các từ nối có quy luật riêng", "content": [{"c": "And moreover, And in addtion", "f": [], "e": []}, {"c": "And thus, or otherwise\t", "f": [], "e": []}, {"c": "And furthermore, And therefore\t", "f": [], "e": []}, {"c": "But nevertheless, But ... anyway", "f": [], "e": []}, {"c": "Nếu nối giữa hai mệnh đề, đằng trước chúng phải có dấu phẩy", "f": [], "e": [{"e": "He was exhausted, and therefore his judgement was not very good."}]}, {"c": "Nhưng nếu nối giữa hai từ đơn thì không", "f": [], "e": [{"e": "The missing piece is small but nevertheless significant."}]}]}, {"sub_title": "Unless + positive = if ... not:", "content": [{"c": "Trừ phi, nếu không", "f": [], "e": [{"e": "You will be late unless you start at once."}]}]}, {"sub_title": "But for that + unreal condition:", "content": [{"c": "Nếu không thì", "f": [], "e": []}, {"c": "Mệnh đề sau nó phải lùi một thì.", "f": [], "e": []}, {"c": "Hiện tại", "f": [], "e": [{"e": "My father pays my fee, but for that I couldn''t be here."}]}, {"c": "Quá khứ", "f": [], "e": [{"e": "My car broke down, but for that we would have been here in time."}]}]}, {"sub_title": "Otherwise + real condition:", "content": [{"c": "Kẻo, nếu không thì", "f": [], "e": [{"e": "We must be back before midnight otherwise I will be locked out."}]}]}, {"sub_title": "Otherwise + unreal condition:", "content": [{"c": "Kẻo, nếu không thì", "f": [], "e": []}, {"c": "Sau nó phải là điều kiện không thể thực hiện được, mệnh đề sau nó lùi một thì.", "f": [], "e": []}, {"c": "Hiện tại", "f": [], "e": [{"e": "Her father supports her finance otherwise she couldn''t conduct these experiments (But she can with her father''s support)."}]}, {"c": "Quá khứ:", "f": [], "e": []}, {"c": "I used a computer, otherwise I would have taken longer with these calculations (But he didn''t take any longer).", "f": [], "e": []}, {"c": "Lưu ý: Trong tiếng Anh thông tục (colloquial English) người ta dùng or else để thay thế cho otherwise.", "f": [], "e": []}]}, {"sub_title": "Provided/Providing that:", "content": [{"c": "Với điều kiện là, miễn là ( = as long as)", "f": [], "e": [{"e": "You can camp here providing that you leave no mess."}]}]}, {"sub_title": "Suppose/Supposing = What ... if:", "content": [{"c": "Giả sử .... thì sao, Nếu\tthì sao.", "f": [], "e": [{"e": "Suppose the plane is late? = What (will happen) if the plane is late?"}]}, {"c": " Lưu ý: Suppose còn có thể được dùng để đưa ra lời gợi ý. ", "f": [], "e": [{"e": "Suppose you ask him = Why don''t you ask him."}]}, {"c": "Lưu ý thành ngữ: ", "f": [], "e": [{"e": "What if I am?: Tao thế thì đã sao nào? (mang tính thách thức)"}]}]}, {"sub_title": "If only + S + simple present/will + verb = hope that:", "content": [{"c": "hi vọng rằng", "f": [], "e": [{"e": "If only he comes in time."}, {"e": "If only he will learn harder for the test next month."}]}]}, {"sub_title": "If only + S + simple past/past perfect = wish that", "content": [{"c": "Câu điều kiện không thực = giá mà", "f": [], "e": [{"e": "If only he came in time now."}, {"e": "If only she had studied harder for that test"}]}]}, {"sub_title": "If only + S + would + V:", "content": [{"c": "Diễn đạt một hành động không thể xảy ra ở hiện tại", "f": [], "e": [{"e": "If only he would drive more slowly = we wish he drove more slowly."}]}, {"c": "Diễn đạt một ước muốn vô vọng về tương lai: ", "f": [], "e": [{"e": "If only it would stop raining."}]}]}, {"sub_title": "Immediately = as soon as = the moment (that) = the instant that = directly", "content": [{"c": "ngay lập tức/ ngay khi mà", "f": [], "e": [{"e": "Tell me immediatly (=as soon as) you have any news."}, {"e": "Phone me the moment that you get the results."}, {"e": "I love you the instant (that) I saw you."}, {"e": "Directly I walked in the door I smelt smoke."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (118, '3', 'Câu bị động (passive voice)', '{}', 'Câu bị động (passive voice), Câu bị động (passive voice)', '{}', '[{"sub_title": "", "content": [{"c": "Trong tiếng Anh, người ta rất hay dùng câu bị động. Khác với ở câu chủ động chủ ngữ thực hiện hành động, trong câu bị động chủ ngữ nhận tác động của hành động. Câu bị động được dùng khi muốn nhấn mạnh đến đối tượng chịu tác động của hành động hơn là bản thân hành động đó. Thì của động từ ở câu bị động phải tuân theo thì của động từ ở câu chủ động.", "f": [], "e": []}, {"c": "Nếu là loại động từ có 2 tân ngữ, muốn nhấn mạnh vào tân ngữ nào người ta đưa tân ngữ đó lên làm chủ ngữ nhưng thông thường chủ ngữ hợp lý của câu bị động là tân ngữ gián tiếp.", "f": [], "e": [{"e": "I gave him a book = I gave a book to him = He was given a book (by me)."}]}, {"c": "Đặt by + tân ngữ mới đằng sau tất cả các tân ngữ khác. Nếu sau by là một đại từ vô nhân xưng mang nghĩa người ta: by people, by sb thì bỏ hẳn nó đi.", "f": [], "e": []}]}, {"sub_title": "Hiện tại đơn hoặc Quá khứ đơn", "content": [{"c": "", "f": ["(am/is/are/was/were) + [verb in past participle]"], "e": [{"e": "Active: Hurricanes destroy a great deal of property each year."}, {"e": "Passive: A great deal of property is destroyed by hurricanes each year."}]}]}, {"sub_title": "Hiện tại tiếp diễn hoặc Quá khứ tiếp diễn", "content": [{"c": "", "f": ["(am/is/are/was/were) + being + [verb in past participle]"], "e": [{"e": "Active: The committee is considering several new proposals."}, {"e": "Passive: Several new proposals are being considered by the committee."}]}]}, {"sub_title": "Hiện tại hoàn thành hoặc Quá khứ hoàn thành", "content": [{"c": "", "f": ["(has/have/had)\t+ been + [verb in past participle]"], "e": [{"e": "Active: The company has ordered some new equipment."}, {"e": "Passive: Some new equipment has been ordered by the company."}]}]}, {"sub_title": "Trợ động từ", "content": [{"c": "", "f": ["modal + be + [verb in past participle]"], "e": [{"e": "Active: The manager should sign these contracts today."}, {"e": "Passive: These contracts should be signed by the manager today."}]}, {"c": "Các nội động từ (Động từ không yêu cầu 1 tân ngữ nào) không được dùng ở bị động.", "f": [], "e": [{"e": "My leg hurts."}]}, {"c": "Đặc biệt khi chủ ngữ chịu trách nhiệm chính của hành động cũng không được chuyển thành câu bị động.", "f": [], "e": [{"e": "The US takes charge: Nước Mỹ nhận lãnh trách nhiệm"}]}, {"c": "Nếu là người hoặc vật trực tiếp gây ra hành động thì dùng by nhưng nếu là vật gián tiếp gây ra hành động thì dùng with.", "f": [], "e": [{"e": "The bird was shot with the gun. The bird was shot by the hunter."}]}, {"c": "Trong một số trường hợp to be/to get + P2 hoàn toàn không mang nghĩa bị động mà mang 2 nghĩa:", "f": [], "e": []}, {"c": "Chỉ trạng thái, tình huống mà chủ ngữ đang gặp phải. Could you please check my mailbox while I am gone. He got lost in the maze of the town yesterday.", "f": [], "e": []}, {"c": "Chỉ việc chủ ngữ tự làm lấy", "f": [], "e": [{"e": "The little boy gets dressed very quickly."}, {"e": "Could I give you a hand with these tires."}, {"e": "No thanks, I will be done when I finish tightening these bolts."}]}, {"c": "Mọi sự biến đổi về thì và thể đều nhằm vào động từ to be, còn phân từ 2 giữ nguyên.", "f": [], "e": []}, {"c": "to be made of: Được làm bằng (Đề cập đến chất liệu làm nên vật)", "f": [], "e": [{"e": "This table is made of wood"}]}, {"c": "to be made from: Được làm ra từ (đề cập đến việc nguyên vật liệu bị biến đổi khỏi trạng thái ban đầu để làm nên vật)", "f": [], "e": [{"e": "Paper is made from wood"}]}, {"c": "to be made out of: Được làm bằng (đề cập đến quá trình làm ra vật) ", "f": [], "e": [{"e": "This cake was made out of flour, butter, sugar, eggs and milk."}]}, {"c": "to be made with: Được làm với (đề cập đến chỉ một trong số nhiều chất liệu làm nên vật) ", "f": [], "e": [{"e": "This soup tastes good because it was made with a lot of spices."}]}, {"c": "Phân biệt thêm về cách dùng marry và divorce trong 2 thể: chủ động và bị động. Khi không có tân ngữ thì người Anh ưa dùng get maried và get divorced trong dạng informal English.", "f": [], "e": [{"e": "Lulu and Joe got maried last week. (informal)"}, {"e": "Lulu and Joe married last week. (formal)"}, {"e": "After 3 very unhappy years they got divorced. (informal)"}, {"e": "After 3 very unhappy years they dovorced. (formal)"}]}, {"c": "Sau marry và divorce là một tân ngữ trực tiếp thì không có giới từ: To mary / divorce smb", "f": [], "e": [{"e": "She married a builder."}, {"e": "Andrew is going to divorce Carola"}]}, {"c": "To be/ get married/ to smb (giới từ “to” là bắt buộc) ", "f": [], "e": [{"e": "She got married to her childhood sweetheart."}, {"e": "He has been married to Louisa for 16 years and he still doesn’t understand her."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (119, '3', 'To have sb do sth', '{"Một số cấu trúc cầu khiến (causative)"}', 'Một số cấu trúc cầu khiến (causative), To have sb do sth', '{120, 121, 122, 123, 124}', '[{"sub_title": "to get sb to do sth", "content": [{"c": "Sai ai, khiến ai, bảo ai làm gì", "f": [], "e": [{"e": "I’ll have Peter fix my car."}, {"e": "I’ll get Peter to fix my car."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (120, '3', 'To have/to get sth done', '{"Một số cấu trúc cầu khiến (causative)"}', 'Một số cấu trúc cầu khiến (causative), To have/to get sth done', '{119, 121, 122, 123, 124}', '[{"sub_title": "", "content": [{"c": "làm một việc gì bằng cách thuê người khác", "f": [], "e": [{"e": "I have my hair cut. (Tôi đi cắt tóc - chứ không phải tôi tự cắt)"}, {"e": "I have my car washed. (Tôi mang xe đi rửa ngoài dịch vụ - không phải tự rửa)"}]}, {"c": "Theo khuynh hướng này động từ to want và would like cũng có thể dùng với mẫu câu như vậy: ", "f": ["To want/ would like Sth done. (Ít dùng)"], "e": [{"e": "I want/ would like my car washed."}]}, {"c": "Câu hỏi dùng cho loại mẫu câu này là: What do you want done to Sth?", "f": [], "e": [{"e": "What do you want done to your car?"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (121, '3', 'To make sb do sth', '{"Một số cấu trúc cầu khiến (causative)"}', 'Một số cấu trúc cầu khiến (causative), To make sb do sth', '{119, 120, 122, 123, 124}', '[{"sub_title": "to force sb to do sth", "content": [{"c": "Bắt buộc ai phải làm gì", "f": [], "e": [{"e": "The bank robbers made the manager give them all the money."}, {"e": "The bank robbers forced the manager to give them all the money."}]}, {"c": "Đằng sau tân ngữ của make còn có thể dùng 1 tính từ: To make sb/sth + adj", "f": [], "e": [{"e": "Wearing flowers made her more beautiful."}, {"e": "Chemical treatment will make this wood more durable"}]}]}, {"sub_title": "To make sb + P2", "content": [{"c": "làm cho ai bị làm sao", "f": [], "e": [{"e": "Working all night on Friday made me tired on Saturday."}]}]}, {"sub_title": "To cause sth + P2", "content": [{"c": "làm cho cái gì bị làm sao", "f": [], "e": [{"e": "The big thunder storm caused many waterfront houses damaged."}]}, {"c": "Nếu tân ngữ của make là một động từ nguyên thể thì phải đặt it giữa make và tính từ, đặt động từ ra phía đằng sau", "f": ["make it + adj + V as object"], "e": [{"e": "The wire service made it possible to collect and distribute news faster and cheaper."}]}, {"c": "Tuy nhiên nếu tân ngữ của make là 1 danh từ hay 1 ngữ danh từ thì không được đặt it giữa make và tính từ", "f": ["Make + adj + noun/ noun phrase."], "e": [{"e": "The wire service made possible much speedier collection and distribution of news."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (122, '3', 'To let sb do sth', '{"Một số cấu trúc cầu khiến (causative)"}', 'Một số cấu trúc cầu khiến (causative), To let sb do sth', '{119, 120, 121, 123, 124}', '[{"sub_title": "to permit/allow sb to do sth", "content": [{"c": "để cho ai, cho phép ai làm gì", "f": [], "e": [{"e": "I let me go."}, {"e": "At first, she didn’t allow me to kiss her but..."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (123, '3', 'To help sb to do sth/do sth', '{"Một số cấu trúc cầu khiến (causative)"}', 'Một số cấu trúc cầu khiến (causative), To help sb to do sth/do sth', '{119, 120, 121, 122, 124}', '[{"sub_title": "", "content": [{"c": "Giúp ai làm gì", "f": [], "e": [{"e": "Please help me to throw this table away."}, {"e": "She helps me open the door."}]}, {"c": "Nếu tân ngữ của help là một đại từ vô nhân xưng mang nghĩa người ta thì không cần phải nhắc đến tân ngữ đó và bỏ luôn cả to của động từ đằng sau.", "f": [], "e": [{"e": "This wonder drug will help (people to) recover more quickly."}]}, {"c": "Nếu tân ngữ của help và tân ngữ của động từ sau nó trùng hợp với nhau, người ta sẽ bỏ tân ngữ sau help và bỏ luôn cả to của động từ đằng sau.", "f": [], "e": [{"e": "The body fat of the bear will help (him to) keep him alive during hibernation."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (124, '3', 'Ba động từ đặc biệt: see, watch, hear', '{"Một số cấu trúc cầu khiến (causative)"}', 'Một số cấu trúc cầu khiến (causative), Ba động từ đặc biệt: see, watch, hear', '{119, 120, 121, 122, 123}', '[{"sub_title": "", "content": [{"c": "Đó là những động từ mà ngữ nghĩa của chúng sẽ thay đổi đôi chút khi động từ sau tân ngữ của chúng ở các dạng khác nhau.", "f": ["To see/to watch/ to hear sb/sth do sth (hành động được chứng kiến từ đầu đến cuối)"], "e": [{"e": "I heard the telephone ring and then John answered it."}]}, {"c": "", "f": ["To see/to watch/ to hear sb/sth doing sth (hành động không được chứng kiến trọn vẹn mà chỉ ở một thời điểm)"], "e": [{"e": "I heard her singing at the time I came home."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (125, '3', 'Câu phức hợp và đại từ quan hệ', '{}', 'Câu phức hợp và đại từ quan hệ, Câu phức hợp và đại từ quan hệ', '{126, 127, 128, 129, 130, 131, 132}', '[{"sub_title": "Tiếng Anh có 2 loại câu:", "content": [{"c": "Câu đơn giản: là câu có 1 thành phần và chỉ cần như vậy câu cũng đã đủ nghĩa.", "f": [], "e": []}, {"c": "Câu phức hợp: là loại câu có 2 thành phần (2 mệnh đề) chính/phụ nối với nhau bằng một từ gọi là đại từ quan hệ.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (126, '3', 'That và which làm chủ ngữ của mệnh đề phụ', '{"Câu phức hợp và đại từ quan hệ"}', 'Câu phức hợp và đại từ quan hệ, That và which làm chủ ngữ của mệnh đề phụ', '{125, 127, 128, 129, 130, 131, 132}', '[{"sub_title": "", "content": [{"c": "Nó đứng đầu câu phụ, thay thế cho danh từ bất động vật đứng trước nó và đóng vai trò chủ ngữ của câu phụ. Trong trường hợp này không thể lược bỏ that hoặc which:", "f": [], "e": [{"e": "We bought the stereo that had been advertised at a reduced price. (We bought the stereo. It had been advertised at a reduced price)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (127, '3', 'That và which làm tân ngữ của mệnh đề phụ', '{"Câu phức hợp và đại từ quan hệ"}', 'Câu phức hợp và đại từ quan hệ, That và which làm tân ngữ của mệnh đề phụ', '{125, 126, 128, 129, 130, 131, 132}', '[{"sub_title": "", "content": [{"c": "Nó thay thế cho danh từ bất động vật đứng trước nó, mở đầu câu phụ nhưng đóng vai trò tân ngữ trong câu phụ. Trong trường hợp này có thể lược bỏ that hoặc which:", "f": [], "e": [{"e": "George is going to buy the house (that) we have been thinking of buying. (George is going to buy a house. We have been thinking of buying it.)"}]}, {"c": "Người ta dùng that chứ không dùng which khi:", "f": [], "e": []}, {"c": "Đứng trước nó là một tính từ so sánh bậc nhất + danh từ", "f": [], "e": [{"e": "That is the best novel that has been written by this author."}]}, {"c": "Khi đằng trước nó là một số các đại từ phiếm chỉ như all, some, any, anything, everything, much, little, nothing v.v..", "f": [], "e": [{"e": "I want to see all that he possesses."}, {"e": "All the apples that fall are eaten by pig."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (128, '3', 'Who làm chủ ngữ của mệnh đề phụ', '{"Câu phức hợp và đại từ quan hệ"}', 'Câu phức hợp và đại từ quan hệ, Who làm chủ ngữ của mệnh đề phụ', '{125, 126, 127, 129, 130, 131, 132}', '[{"sub_title": "", "content": [{"c": "Nó thay thế cho danh từ chỉ người hoặc động vật đứng trước nó và đóng vai trò chủ ngữ của câu phụ. Trong trường hợp này không thể lược bỏ who:", "f": [], "e": [{"e": "The man who is in this room is angry."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (129, '3', 'Whom làm tân ngữ của mệnh đề phụ', '{"Câu phức hợp và đại từ quan hệ"}', 'Câu phức hợp và đại từ quan hệ, Whom làm tân ngữ của mệnh đề phụ', '{125, 126, 127, 128, 130, 131, 132}', '[{"sub_title": "", "content": [{"c": "Nó thay thế cho danh từ chỉ người hoặc động vật (động vật nuôi trong nhà) đứng trước nó và đóng trò tân ngữ của câu phụ; nó có thể bỏ đi được.", "f": [], "e": [{"e": "The men (whom) I don''t like are angry."}]}, {"c": "Nếu whom làm tân ngữ của một ngữ động từ bao gồm 1 Verb + 1 giới từ thì lối viết chuẩn nhất là đưa giới từ đó lên trước whom.", "f": [], "e": [{"e": "The man to whom you have just talked is the chairman of the company."}]}, {"c": "Tuy nhiên nếu whom là tân ngữ của một ngữ động từ bao gồm 1 động từ + 2 giới từ thì 2 giới từ đó vẫn phải đứng đằng sau động từ.", "f": [], "e": [{"e": "The man whom you are looking forward to is the chairman of the company."}]}, {"c": "Không được dùng who thay cho whom trong văn viết dù nó được chấp nhận trong văn nói.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (130, '3', 'Mệnh đề phụ bắt buộc và không bắt buộc', '{"Câu phức hợp và đại từ quan hệ"}', 'Câu phức hợp và đại từ quan hệ, Mệnh đề phụ bắt buộc và không bắt buộc', '{125, 126, 127, 128, 129, 131, 132}', '[{"sub_title": "Mệnh đề phụ bắt buộc.", "content": [{"c": "Là loại mệnh đề bắt buộc phải có mặt ở trong câu, nếu bỏ đi câu sẽ mất ý nghĩa ban đầu. Nên dùng that làm chủ ngữ cho loại câu này mặc dù which vẫn được chấp nhận. Câu phụ thường đứng xen vào giữa câu chính hoặc nối liền với câu chính và không tách rời khỏi nó bởi bất cứ dấu phảy nào. Trong ví dụ sau nếu bỏ đi mệnh đề phụ được gạch chân thì câu sẽ mang ý nghĩa hoàn toàn khác:", "f": [], "e": []}, {"c": "Weeds that float to the surface should be removed before they decay.", "f": [], "e": []}]}, {"sub_title": "Mệnh đề phụ không bắt buộc", "content": [{"c": "Là loại mệnh đề mang thông tin phụ trong câu, nếu bỏ nó đi câu không mất nghĩa ban đầu. Không được dùng that làm chủ ngữ mà phải dùng which, cho dù which có là tân ngữ của mệnh đề phụ cũng không được phép bỏ nó đi. Câu phụ thường đứng chen vào giữa câu chính và bắt buộc phải tách khỏi câu chính bằng 2 dấu phẩy. Xét hai ví dụ sau:", "f": [], "e": [{"e": "My car, which is very large, uses too much gasoline."}, {"e": "This rum, which I bought in the Virgin Islands, is very smooth."}]}]}, {"sub_title": "Tầm quan trọng của việc sử dụng dấu phẩy đối với mệnh đề phụ", "content": [{"c": "Ở những mệnh đề phụ bắt buộc, khi không dùng dấu phẩy tức là mệnh đề phụ xác định một giới hạn đối với danh từ đằng trước trong mệnh đề chính", "f": [], "e": [{"e": "The travelers who knew about the flood took another road. (Những người biết về trận lũ đều chọn đường khác)"}, {"e": "The wine that was stored in the cellar was ruined."}]}, {"c": "Đối với những mệnh đề phụ không bắt buộc, khi có dấu phẩy ngăn cách thì nó không xác định giới hạn đối với danh từ đứng trước nó.", "f": [], "e": [{"e": "The travelers, who knew about the flood, took another road. (Tất cả những người...)"}, {"e": "The wine, which was stored in the cellar, was ruined."}]}, {"c": "Note: Các nguyên tắc trên đây chỉ dùng trong văn viết, không dùng trong văn nói. Lỗi ngữ pháp của TOEFL không tính đến mệnh đề phụ bắt buộc nhưng sẽ trừ điểm nếu phạm phải lỗi mệnh đề phụ không bắt buộc.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (131, '3', 'Cách sử dụng all, both, some, several, most, few + of + whom/ which', '{"Câu phức hợp và đại từ quan hệ"}', 'Câu phức hợp và đại từ quan hệ, Cách sử dụng all, both, some, several, most, few + of + whom/ which', '{125, 126, 127, 128, 129, 130, 132}', '[{"sub_title": "", "content": [{"c": "", "f": [], "e": [{"e": "Her sons, both of whom are working abroad, call her every week. (không được nói both of them)"}, {"e": "The buses, most of which were full of passengers, began to pull out."}]}, {"c": "Tuyệt đối không được dùng đại từ nhân xưng tân ngữ: them, us trong trường hợp này.", "f": [], "e": []}, {"c": "What (the things that) có thể làm tân ngữ cho mệnh đề phụ cùng lúc làm chủ ngữ cho mệnh đề chính/ hoặc làm chủ ngữ của cả 2 mệnh đề chính, phụ:", "f": [], "e": [{"e": "What we have expected is the result of the test."}]}, {"c": "Whose (của người mà, của con mà) có thể thay thế cho danh từ chỉ người hoặc động vật ở trước nó và chỉ sự sở hữu của người hoặc động vật đó đối với danh từ đi sau.", "f": [], "e": [{"e": "James, whose father is the president of the company, has received a promotion."}]}, {"c": "Trong lối văn viết trang trọng nên dùng of which để thay thế cho danh từ bất động vật mặc dù whose vẫn được chấp nhận.", "f": [], "e": [{"e": "Savings account, of which interest rate is quite hight, is very common now. (of which = whose)"}]}, {"c": "Hoặc dùng with + noun/ noun phrase thay cho whose", "f": [], "e": [{"e": "A house whose walls were made of glass is easy to break = A house with glass walls is easyto break."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (132, '3', 'Cách loại bỏ đại từ quan hệ trong mệnh đề phụ', '{"Câu phức hợp và đại từ quan hệ"}', 'Câu phức hợp và đại từ quan hệ, Cách loại bỏ đại từ quan hệ trong mệnh đề phụ', '{125, 126, 127, 128, 129, 130, 131}', '[{"sub_title": "", "content": [{"c": "Đối với những mệnh đề phụ bắt buộc người ta có thể (không bắt buộc) loại bỏ đại từ quan hệ và động từ to be (cùng với các trợ động từ của nó) trong một số trường hợp sau:", "f": [], "e": []}, {"c": "Trước một mệnh đề phụ mà cấu trúc động từ ở thì bị động.", "f": [], "e": [{"e": "This is the value of X (which was) obtained from the areas under the normal curve."}]}, {"c": "Trước một mệnh đề phụ mà sau nó là một ngữ giới từ.", "f": [], "e": [{"e": "The beaker (that is) on the counter contains a solution."}]}, {"c": "Trước một cấu trúc động từ ở thể tiếp diễn.", "f": [], "e": [{"e": "The girl (who is) running down the street might be in trouble."}]}, {"c": "Ngoài ra trong một số trường hợp khi đại từ quan hệ trong mệnh đề phụ bắt buộc gắn liền với chủ ngữ đứng trước nó và mệnh đề phụ ấy diễn đạt qui luật của sự kiện, người ta có thể bỏ đại từ quan hệ và động từ chính để thay vào đó bằng một Verb-ing.", "f": [], "e": [{"e": "The travelers taking (who take) this bus on a daily basis buy their ticket in booking."}]}, {"c": "Trong những mệnh đề phụ không bắt buộc người ta cũng có thể loại bỏ đại từ quan hệ và động từ to be khi nó đứng trước một ngữ danh từ. Tuy nhiên phần ngữ danh từ còn lại vẫn phải đứng giữa 2 dấu phẩy.", "f": [], "e": [{"e": "Mr Jackson, (who is) a professor, is traveling in the Mideast this year."}]}, {"c": "Một trường hợp khác rất phổ biến là loại bỏ đại từ quan hệ và động từ chính, thay vào đó bằng một Verb-ing khi mệnh đề phụ này bổ nghĩa cho một tân ngữ.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (133, '3', 'Cách sử dụng một số cấu trúc p1', '{}', 'Cách sử dụng một số cấu trúc p1, Cách sử dụng một số cấu trúc p1', '{}', '[{"sub_title": "Have sb/sth + doing:", "content": [{"c": "làm cho ai làm gì.", "f": [], "e": [{"e": "John had us laughing all through the meal."}]}]}, {"sub_title": "S + won''t have sb + doing = S + won''t allow sb to do sth:", "content": [{"c": "không cho phép ai làm gì ", "f": [], "e": [{"e": "I won''t have him telling me what to do."}]}, {"c": "Các cụm phân từ: adding, pointing out, reminding, warning, reasoning that đều có thể mở đầu cho một mệnh đề phụ gián tiếp.", "f": [], "e": [{"e": "He told me to start early, reminding me that the road would be crowded."}, {"e": "Reasoning that he could only get to the lake, we followed that way."}]}]}, {"sub_title": "To catch sb doing sth:", "content": [{"c": "bắt gặp ai đang làm gì (hàm ý bị phật lòng). ", "f": [], "e": [{"e": "If she catches you reading her diary, she will be furious."}]}]}, {"sub_title": "To find sb/sth doing sth:", "content": [{"c": "Thấy ai/ cái gì đang làm gì", "f": [], "e": [{"e": "I found him standing at the doorway"}, {"e": "He found a tree lying across the road."}]}]}, {"sub_title": "To leave sb doing sth:", "content": [{"c": "Để ai làm gì", "f": [], "e": [{"e": "I left Bob talking to the director after the introduction."}]}]}, {"sub_title": "Go/come doing sth", "content": [{"c": "dùng cho thể thao và mua sắm", "f": [], "e": [{"e": "Go skiing/ go swimming/ go shopping/ come dancing"}]}]}, {"sub_title": "To spend time doing sth:", "content": [{"c": "Bỏ thời gian làm gì", "f": [], "e": [{"e": "He usually spends much time preparing his lessons."}]}]}, {"sub_title": "To waste time doing:", "content": [{"c": "hao phí thời gian làm gì", "f": [], "e": [{"e": "She wasted all the afternoon having small talks with her friends."}]}]}, {"sub_title": "To have a hard time/trouble doing sth:", "content": [{"c": "Gặp khó khăn khi làm gì", "f": [], "e": [{"e": "He has trouble listening to English."}, {"e": "I had a hard time doing my homework."}]}]}, {"sub_title": "To be worth doing sth:", "content": [{"c": "đáng để làm gì", "f": [], "e": [{"e": "This project is worth spending time and money on."}]}]}, {"sub_title": "To be busy doing something:", "content": [{"c": "bận làm gì ", "f": [], "e": [{"e": "She is busy packing now."}]}]}, {"sub_title": "Be no/ not much/ any/ some good doing smt:", "content": [{"c": "Không có ích, ít khi có ích (làm gì) ", "f": [], "e": [{"e": "It''s no good my talking to him (Nói chuyện với anh ta tôi chả thấy có ích gì)"}, {"e": "What good is it asking her (Hỏi cô ta thì có ích gì cơ chứ)"}]}, {"c": "P1 được sử dụng để rút ngắn những câu dài:", "f": [], "e": []}, {"c": "Hai hành động xảy ra song song cùng một lúc thì hành động thứ hai ở dạng V-ing, hai hành động không tách rời khỏi nhau bởi bất kì dấu phảy nào.", "f": [], "e": [{"e": "He drives away and whistles = He drives away whistling."}]}, {"c": "Khi hành động thứ hai hoặc các hành động tiếp sau theo nó là một phần trong quá trình diễn biến của hành động thứ nhất thì hành động thứ hai hoặc các hành động tiếp theo sau nó ở dạng V-ing. Giữa hai hành động có ngăn cách nhau bởi dấu phẩy.", "f": [], "e": [{"e": "She went out and slammed the door = She went out, slamming the door."}]}, {"c": "Khi hành động thứ 2 hoặc các hành động sau nó là kết quả của hành động thứ nhất thì hành động thứ 2 và các hành động tiếp theo sẽ ở dạng V-ing. Nó sẽ ngăn cách với hành động chính bằng một dấu phẩy.", "f": [], "e": [{"e": "He fired two shots, killling a robber and wounding the other."}]}, {"c": "Hành động thứ 2 không cần chung chủ ngữ với hành động thứ nhất mà chỉ cần là kết quả của hành động thứ nhất cũng có thể ở dạng V-ing.", "f": [], "e": [{"e": "The plane crashed, its bombs exploding when it hit the ground."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (134, '3', 'Cách sử dụng một số cấu trúc p2', '{}', 'Cách sử dụng một số cấu trúc p2, Cách sử dụng một số cấu trúc p2', '{}', '[{"sub_title": "Whould (should) like + to have + P2:", "content": [{"c": "Diễn đạt một ước muốn không thành.", "f": [], "e": [{"e": "He would like to have seen the photos = He would have liked to see the photos (But he couldn''t)."}]}]}, {"sub_title": "Dùng với một số động từ: to appear, to seem, to happen, to pretend", "content": [{"c": "Nên nhớ rằng hành động của nguyên mẫu hoàn thành xảy ra trước hành động của mệnh đề chính.", "f": [], "e": [{"e": "He seems to have passed the exam = It seems that he has passed the exam."}, {"e": "She pretended to have read the material = She pretended that she had read the material."}]}]}, {"sub_title": "Dùng với sorry, to be sorry + to have + P2:", "content": [{"c": "Hành động của nguyên mẫu hoàn thành xảy ra trước trạng thái sorry.", "f": [], "e": [{"e": "The girls were sorry to have missed the Rock Concert (The girls were sorry that they had missed the Rock Concert.)"}]}]}, {"sub_title": "Dùng với một số các động từ sau đây ở thể bị động:", "content": [{"c": "to ackowledge. believe, consider, find, know, report, say, suppose, think, understand. Hành động của nguyên mẫu hoàn thành xảy ra trước hành động của mệnh đề chính.", "f": [], "e": [{"e": "He was believed to have gone out of the country. (It was believed that he had gone out of the country.)"}]}]}, {"sub_title": "Dùng với một số các động từ khác như", "content": [{"c": "to claim, expect, hope, promise. Đặc biệt lưu ý rằng hành động của nguyên mẫu hoàn thành sẽ ở future perfect so với thì của động từ ở mệnh đề chính.", "f": [], "e": [{"e": "He expects to have graduated by June. (He expects that he will have graduated by June.)"}, {"e": "He promised to have told me the secret by the end of this week. (He promised that he would have told me the secret by the end of this week.)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (135, '3', 'That dùng với tư cách là một liên từ (rằng)', '{"Những cách sử dụng khác của that"}', 'Những cách sử dụng khác của that, That dùng với tư cách là một liên từ (rằng)', '{136}', '[{"sub_title": "", "content": [{"c": "Ngoài việc sử dụng trong mệnh đề quan hệ, that còn được dùng trong một số trường hợp khác như dùng làm liên từ, dùng trong mệnh đề that.", "f": [], "e": []}]}, {"sub_title": "Trường hợp không bắt buộc phải có that", "content": [{"c": "Đằng sau 4 động từ say, tell, think, believe thì that có thể bỏ đi được.", "f": [], "e": [{"e": "John said (that) he was leaving next week."}]}]}, {"sub_title": "Trường hợp bắt buộc phải có that.", "content": [{"c": "Đằng sau 4 động từ mention, declare, report, state thì that bắt buộc phải có mặt.", "f": [], "e": [{"e": "George mentioned that he was going to France next year."}]}, {"c": "That vẫn bắt buộc phải có mặt ở mệnh đề thứ 3 trong câu nếu mệnh đề chính sử dụng một trong 4 động từ trên.", "f": [], "e": [{"e": "The Major declared that on June the first he would announce the result of the search and that he would never overlook the crime punishment."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (136, '4', 'Mệnh đề that', '{"Những cách sử dụng khác của that"}', 'Những cách sử dụng khác của that, Mệnh đề that', '{135}', '[{"sub_title": "", "content": [{"c": "Là loại mệnh đề có chứa that.", "f": [], "e": []}]}, {"sub_title": "Dùng với chủ ngữ giả it và tính từ.", "content": [{"c": "", "f": ["It + to be + adj + That + S + V = That + S + V ... + to be + adj"], "e": [{"e": "It is well known that many residents of third world countries are dying."}, {"e": "That many residents of third world countries are dying is well known."}]}]}, {"sub_title": "Dùng với động từ tạo thành một mệnh đề độc lập.", "content": [{"c": "", "f": ["It + verb + complement + That + S + V = That + S + V + ... + V + complement"], "e": [{"e": "It surprises me that John would do such a thing That John would do such a thing surprises me."}]}, {"c": "Người ta dùng chủ ngữ giả it trong văn nói để dễ nhận biết và that làm chủ ngữ thật trong văn viết. Trong một số trường hợp người ta đảo ngữ giới từ lên đầu câu, liên từ hoặc động từ tĩnh đảo lên trên THAT và sau đó mới là chủ ngữ thật. Công thức sử dụng như sau:", "f": ["Prepositional phrase + link verb/stative verb + THAT + sentence as real subject, and THAT + sentence as real subject"], "e": [{"e": "Among the surprising discoveries were that T.rex was a far sleeker but more powerful carnivore than previously thought, perhaps weighing less than 6.5 tons - no more than a bull elephant, and that T.rex habitat was forest, not swamp or plain as previously believed. (Một trong số những phát hiện ngạc nhiên là ở chỗ loài khủng long bạo chúa là một loại ăn thịt có thân hình thon nhỏ hơn nhiều, song dũng mãnh hơn người ta đã từng nghĩ trước đây, có lẽ cân nặng chưa đến 6 tấn rưỡi - tức là không lớn hơn một con voi đực, và rằng môi trường sống của loài khủng long này là rừng chứ không phải là đầm lầy hay đồng bằng như người ta vẫn tưởng.)"}]}, {"c": "", "f": ["The fact that + sentence"], "e": []}, {"c": "", "f": ["The fact remains that + sentence"], "e": []}, {"c": "", "f": ["the fact of the matter is that + sentence"], "e": [{"e": "The fact that Simon had not been home for 3 days didn’t seem to worry anybody."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (137, '4', 'Câu giả định (subjunctive)', '{}', 'Câu giả định (subjunctive), Câu giả định (subjunctive)', '{138, 139, 140, 141}', '[{"sub_title": "", "content": [{"c": "Câu giả định hay còn gọi là câu cầu khiến là loại câu đối tượng thứ nhất muốn đối tượng thứ hai làm một việc gì. Câu giả định có tính chất cầu khiến chứ không mang tính ép buộc như câu mệnh lệnh. Trong câu giả định, người ta dùng dạng nguyên thể không có to của các động từ sau một số động từ chính mang tính cầu khiến. Thường có that trong câu giả định trừ một số trường hợp đặc biệt.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (138, '4', 'Dùng với would rather that', '{"Câu giả định (subjunctive)"}', 'Câu giả định (subjunctive), Dùng với would rather that', '{137, 139, 140, 141}', '[{"sub_title": "", "content": [{"c": "Ngữ pháp hiện đại ngày nay đặc biệt là ngữ pháp Mỹ cho phép dùng would rather mà không cần dùng that trong loại câu này.", "f": [], "e": [{"e": "We would rather (that) he not take this train."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (139, '4', 'Dùng với động từ', '{"Câu giả định (subjunctive)"}', 'Câu giả định (subjunctive), Dùng với động từ', '{137, 138, 140, 141}', '[{"sub_title": "", "content": [{"c": "Ví dụ sau là những động từ đòi hỏi mệnh đề sau nó phải ở dạng giả định và trong câu bắt buộc phải có that trong trường hợp đó.", "f": [], "e": [{"e": "advise, ask, command, decree"}, {"e": "demand, insist, move, order"}, {"e": "prefer, propose, recommend, request"}, {"e": "require, stipulate, suggest, urge"}]}, {"c": "Động từ sau chủ ngữ 2 ở dạng nguyên thể không chia bỏ to. Nếu muốn thành lập thể phủ định đặt not sau chủ ngữ 2 trước nguyên thể bỏ to.", "f": ["Subject + verb + that + subject + [verb in simple form] + ..."], "e": [{"e": "The doctor suggested that his patient stop smoking."}, {"e": "The doctor suggested that his patient not stop smoking."}]}, {"c": "Nếu bỏ that đi thì chủ ngữ 2 sẽ trở về dạng tân ngữ và động từ sau nó trở về dạng nguyên thể có to, câu mất tính chất giả định, trở thành một dạng mệnh lệnh thức gián tiếp.", "f": [], "e": [{"e": "The doctor suggested his patient to stop smoking."}, {"e": "The doctor suggested his patient not to stop smoking."}]}, {"c": "Trong ngữ pháp Anh-Anh trước động từ ở mệnh đề 2 thường có should, người Anh chỉ bỏ should khi sau nó là động từ to be nhưng ngữ pháp Anh-Mỹ không dùng should cho toàn bộ động từỏơ mệnh đề 2.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (140, '4', 'Dùng với tính từ', '{"Câu giả định (subjunctive)"}', 'Câu giả định (subjunctive), Dùng với tính từ', '{137, 138, 139, 141}', '[{"sub_title": "", "content": [{"c": "Ví dụ sau là một số các tính từ đòi hỏi mệnh đề sau nó phải ở dạng giả định, trong câu bắt buộc phải có that và động từ sau chủ ngữ 2 ở dạng nguyên thể bỏ to.", "f": [], "e": [{"e": "advised, necessary, recommended, urgent"}, {"e": "important, obligatory, required, imperative"}, {"e": "mandatory, proposed, suggested"}]}, {"c": "Lưu ý rằng trong bảng này có một số tính từ là phân từ 2 cấu tạo từ các động từ ở bảng trên. Nếu muốn cấu tạo phủ định đặt not sau chủ ngữ 2 trước nguyên thể bỏ to.", "f": ["It + be + adj + that + S + [verb in simple form]"], "e": [{"e": "It is necessary that he find the books."}, {"e": "It is necessary that he not find the books."}, {"e": "It has been proprosed that we change the topic."}, {"e": "It has been proprosed that we not change the topic."}]}, {"c": "Nếu bỏ that đi thì chủ ngữ 2 sẽ biến thành tân ngữ sau giới từ for, động từ trở về dạng nguyên thể có to và câu mất tính chất giả định trở thành dạng mệnh lệnh thức gián tiếp.", "f": [], "e": [{"e": "It is necessary for him to find the books."}, {"e": "It is necessary for him not to find the books."}, {"e": "It has been proprosed for us to change the topic."}, {"e": "It has been proprosed for us not to change the topic."}]}]}, {"sub_title": "Lưu ý:", "content": [{"c": "Câu giả định dùng với 2 loại trên thường được dùng trong các văn bản hoặc thư giao dịch diễn đạt lời đề nghị từ phía A -> B mà không có tính bắt buộc.", "f": [], "e": []}, {"c": "Không chỉ có động từ và tính từ mới được dùng theo dạng này, tất cả các danh từ xuất phát từ những động từ và tính từ trên đều buộc mệnh đề sau nó phải ở dạng giả định, nếu như nó diễn đạt các yếu tố trên.", "f": [], "e": [{"e": "There is suggestion from the doctor that the patient stop smoking."}, {"e": "It is recommendation that the vehicle owner be present at the court"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (141, '4', 'Thể giả định trong một số trường hợp khác', '{"Câu giả định (subjunctive)"}', 'Câu giả định (subjunctive), Thể giả định trong một số trường hợp khác', '{137, 138, 139, 140}', '[{"sub_title": "", "content": [{"c": "Dùng để diễn đạt một ước muốn hoặc một lời nguyền rủa, thường bao hàm các thế lực siêu nhiên. ", "f": [], "e": [{"e": "God be with you = Good bye."}, {"e": "Curse this toad (Chết tiệt cái con cóc này)"}]}]}, {"sub_title": "Dùng với động từ May trong một số trường hợp đặc biệt sau:", "content": [{"c": "Come what may = Dù thế nào chăng nữa, dù bất cứ truyện gì. ", "f": [], "e": [{"e": "Come what may we will stand by you."}]}, {"c": "May as well not do sth .... if .... = Có thể đừng\tnếu không.", "f": [], "e": [{"e": "You may as well not come if you can''t be on time. (Các anh có thể đừng đến nếu không đến được đúng giờ)"}]}, {"c": "May/Might (just) as well do smt = Chẳng mất gì mà lại không, mất gì của bọ. ", "f": [], "e": []}, {"c": "Since nobody wants that job, we might (just) as well let him have it (Vì không ai thích việc này, mình chẳng mất gì mà lại không cho hắn ta làm nó)", "f": [], "e": []}, {"c": "May diễn đạt một lời đề nghị mà người nói cho rằng chưa chắc nó đã được thực hiện", "f": [], "e": [{"e": "The sign on the lawn says clearly that people may not walk on the grass.  (không dùng should trong trường hợp này)."}]}, {"c": "May + S + link verb + adj hoặc May + S + verb + complement (Cầu chúc cho). ", "f": [], "e": [{"e": "May you both very happy (Chúc các bạn bách niên giai lão)"}, {"e": "May the conference be successful (Chúc hội nghị thành công rực rỡ)"}, {"e": "Long may she live to enjoy her good fortune (Chúc cho nàng sống lâu để hưởng vận may của nàng)"}]}, {"c": "If need be = If necessary = Nếu cần", "f": [], "e": [{"e": "If need be, we can take another road."}]}, {"c": "Dùng với động từ to be, bỏ to sau if để chỉ một sự giả định chưa được phân định rõ ràng là đúng hay sai.", "f": [], "e": [{"e": "If that be right, then it would be no defence for this man to say he''s innocent."}]}, {"c": "Let it be me: Giả sử đó là tôi, giá phải tay tôi.", "f": [], "e": [{"e": "Husband: Let it be me."}, {"e": "Wife (suddenly appearing): If that be you, what would you do."}]}, {"c": "Be that as it may... = whether that is true or not... = Cho dù là phải thế hay không. ", "f": [], "e": [{"e": "Be that as it may, you have to accept it."}]}, {"c": "Then so be it: Cứ phải vậy thôi.", "f": [], "e": [{"e": "If we have to pay $2,000, then so be it (thì cứ phải trả thôi)"}]}, {"c": "Câu giả định dùng với it + to be + time", "f": [], "e": []}, {"c": "It''s time (for sb) to do smt: đã đến lúc ai phải làm gì (Thời gian vừa vặn, không mang tính giả định).", "f": [], "e": [{"e": "It''s time for me to get to the airport (Đã đến lúc tôi phải ra sân bay)"}]}, {"c": "It''s time (hightime/about time) (for sb) did smt: đã đến lúc mà (thời gian đã trễ, mang tính giả định)", "f": [], "e": [{"e": "It''s time I got to the airport"}, {"e": "It''s hightime the city government did somthing to stop the traffic jam."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (142, '4', 'Lối nói bao hàm (inclusive)', '{}', 'Lối nói bao hàm (inclusive), Lối nói bao hàm (inclusive)', '{143, 144, 145}', '[{"sub_title": "", "content": [{"c": "Đó là lối nói gộp hai ý trong câu làm một thông qua một số các cụm từ. Hai thành phần trong câu phải tương đương nhau về mặt từ loại: danh từ với danh từ, tính từ với tính từ, ...", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (143, '4', 'Not only ..... but also (không những ... mà còn)', '{"Lối nói bao hàm (inclusive)"}', 'Lối nói bao hàm (inclusive), Not only ..... but also (không những ... mà còn)', '{142, 144, 145}', '[{"sub_title": "", "content": [{"c": "", "f": ["S + Verb + not only + {noun/adjective/adverb/Prepositional phrase} + but (also) + {noun/adjective/adverb/Prepositional phrase}"], "e": []}, {"c": "OR", "f": ["Subject + not only + verb + but (also) + verb"], "e": [{"e": "Robert is not only talented but also handsome. (adjective-adjective)"}, {"e": "He writes not only correctly but also neatly. (adverb-adverb)"}, {"e": "She can play not only the guitar but also the violin. (noun-noun)"}, {"e": "She not only plays the piano but also composes music. (verb-verb)"}]}, {"c": "Thành phần sau but also thường quyết định thành phần sau not only.", "f": [], "e": [{"e": "Incorrect: He is not only famous in Italy but also in Switzerland."}, {"e": "Correct: He is famous not only in Italy but also in Switzerland."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (144, '4', 'As well as (vừa ... vừa ...)', '{"Lối nói bao hàm (inclusive)"}', 'Lối nói bao hàm (inclusive), As well as (vừa ... vừa ...)', '{142, 143, 145}', '[{"sub_title": "", "content": [{"c": "Cũng giống như cấu trúc trên, các thành phần đằng trước và đằng sau cụm từ này phải tương đương với nhau.", "f": ["S + Verb + {noun/adjective/adverb/prepositional phrase} + as well as + {noun/adjective/adverb/prepositional phrase}"], "e": []}, {"c": "OR", "f": ["Subject + verb + as well as + verb"], "e": [{"e": "Robert is talented as well as handsome. (adjective-adjective)"}, {"e": "He writes correctly as well as neatly. (adverb-adverb)"}, {"e": "She plays the guitar as well as the violin. (noun-noun)"}, {"e": "Paul plays the piano as well as composes music. (verb-verb)"}]}, {"c": "Không được nhầm thành ngữ này với as well as của hiện tượng đồng chủ ngữ mang nghĩa cùng với.", "f": [], "e": [{"e": "The teacher, as well as her students, is going to the concert. My cousins, as well as Tim, have a test tomorrow."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (145, '4', 'Both ..... and... (vừa ... vừa)', '{"Lối nói bao hàm (inclusive)"}', 'Lối nói bao hàm (inclusive), Both ..... and... (vừa ... vừa)', '{142, 143, 144}', '[{"sub_title": "", "content": [{"c": "Công thức dùng giống hệt như Not only but also. Both chỉ được dùng với and, không được dùng với as well as.", "f": [], "e": [{"e": "Robert is both talented and handsome."}, {"e": "Paul both plays the piano and composes music."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (146, '4', 'To know, to know how.', '{}', 'To know, to know how., To know, to know how.', '{}', '[{"sub_title": "", "content": [{"c": "Know how thường được dùng để thể hiện một người có kỹ năng hoặc có khả năng để làm một việc gì đó. Do đó nó thường được dùng trước một động từ ở dạng nguyên thể (có to).", "f": ["S + know how + [verb in infinitive] ..."], "e": [{"e": "Bill know how to play tennis well."}, {"e": "Mai and her sisters know how to prepare Chinese food."}, {"e": "Do you know how to use this kind of ATM?"}]}, {"c": "Đằng sau to know how cũng có thể dùng một mệnh đề để diễn đạt khả năng hoặc sự bắt buộc.", "f": [], "e": [{"e": "At a glance, she knew how she could solve this math problem."}]}, {"c": "Know, ngược lại, thường được dùng trước một danh từ, một ngữ giới từ hoặc một mệnh đề.", "f": ["S + know + {noun/Prepositional phrase/Sentence}"], "e": [{"e": "Jason knew the answer to the teacher''s question. (a noun)"}, {"e": "No one know about his accepting the new position. (a prepositional phrase)"}, {"e": "I didn''t know that you were going to France. (a sentence)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (147, '4', 'Despite/inspite of', '{"Mệnh đề thể hiện sự nhượng bộ"}', 'Mệnh đề thể hiện sự nhượng bộ, Despite/inspite of', '{148, 149, 150}', '[{"sub_title": "", "content": [{"c": "bất chấp, cho dù, ...", "f": [], "e": []}, {"c": "Đó là loại mệnh đề diễn đạt hai ý trái ngược trong cùng một câu.", "f": [], "e": []}]}, {"sub_title": "", "content": [{"c": "Đằng sau hai cụm từ này phải dùng một danh từ hoặc ngữ danh từ, không được dùng một câu hoàn chỉnh.", "f": [], "e": [{"e": "Despite his physical handicap, he has become a successful businessman."}, {"e": "In spite of his physical handicap, he has become a successful businessman."}, {"e": "Jane will be admitted to the university in spite of her bad grades."}, {"e": "Jane will be admitted to the university despite her bad grades."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (148, '4', 'Although/even though/though', '{"Mệnh đề thể hiện sự nhượng bộ"}', 'Mệnh đề thể hiện sự nhượng bộ, Although/even though/though', '{147, 149, 150}', '[{"sub_title": "", "content": [{"c": "mặc dầu, mặc dù", "f": [], "e": []}, {"c": "Đằng sau 3 cụm từ này phải dùng một mệnh đề hoàn chỉnh (có cả chủ ngữ và động từ), không được dùng một danh từ.", "f": [], "e": [{"e": "Although he has a physical handicap, he has become a successful businessman."}, {"e": "Jane will be admitted to the university even though she has bad grades."}]}, {"c": "Nếu though đứng một mình, cuối câu, tách biệt khỏi câu chính bởi dấu phẩy thì nó mang nghĩa tuy nhiên.", "f": [], "e": [{"e": "He promised to call me, till now I haven''t received any call from him, though."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (149, '4', 'However + adj + s + linkverb', '{"Mệnh đề thể hiện sự nhượng bộ"}', 'Mệnh đề thể hiện sự nhượng bộ, However + adj + s + linkverb', '{147, 148, 150}', '[{"sub_title": "", "content": [{"c": "dù có .... đi chăng nữa thì ....", "f": [], "e": [{"e": "However strong you are, you can''t move this rock."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (150, '4', 'Although/ albeit + adjective/ adverb/ averbial modifier', '{"Mệnh đề thể hiện sự nhượng bộ"}', 'Mệnh đề thể hiện sự nhượng bộ, Although/ albeit + adjective/ adverb/ averbial modifier', '{147, 148, 149}', '[{"sub_title": "", "content": [{"c": "tuy rằng, dẫu sao", "f": [], "e": [{"e": "Her appointment was a significant, althought/ albeit temporary success (Việc bà ấy được bổ nhiệm là một thành công quan trọng, nhưng dẫu sao cũng chỉ là nhất thời)"}, {"e": "Her performed the task well, althought/ albeit slowly (Anh ta thực hiện nhiệm vụ đó khá tốt, tuy rằng chậm)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (151, '4', 'Những động từ dễ gây nhầm lẫn', '{}', 'Những động từ dễ gây nhầm lẫn, Những động từ dễ gây nhầm lẫn', '{}', '[{"sub_title": "", "content": [{"c": "Những động từ ở ví dụ sau rất dễ gây nhầm lẫn về mặt ngữ nghĩa, chính tả hoặc phát âm.", "f": [], "e": []}]}, {"sub_title": "Nội động từ", "content": [{"c": "Nguyên thể: rise, lie, sit", "f": [], "e": []}, {"c": "Quá khứ (P1): rose, lay, sat", "f": [], "e": []}, {"c": "Quá khứ phân từ (P2): risen, lain, sat", "f": [], "e": []}, {"c": "Verb-ing: rising, lying, sitting", "f": [], "e": []}]}, {"sub_title": "Ngoại động từ", "content": [{"c": "Nguyên thể: raise, lay, set", "f": [], "e": []}, {"c": "Quá khứ (P1): raised, laid, set", "f": [], "e": []}, {"c": "Quá khứ phân từ (P2): raised, laid, set", "f": [], "e": []}, {"c": "Verb-ing: raising, laying, setting", "f": [], "e": []}]}, {"sub_title": "To rise", "content": [{"c": "Dâng lên, tự nâng lên, tự tăng lên. ", "f": [], "e": [{"e": "The sun rises early in the summer."}, {"e": "Prices have risen more than 10% this year."}, {"e": "He rose early so that he could play golf before the others."}, {"e": "When oil and water mix, oil rises to the top."}]}]}, {"sub_title": "To raise smt/sb", "content": [{"c": "Nâng ai, cái gì lên.", "f": [], "e": [{"e": "The students raise their hands in class."}, {"e": "OPEC countries have raised the price of oil."}]}]}, {"sub_title": "To lie", "content": [{"c": "Nằm, ở, tại", "f": [], "e": []}]}, {"sub_title": "To lie in", "content": [{"c": "ở tại", "f": [], "e": []}]}, {"sub_title": "To lie down", "content": [{"c": "nằm xuống. ", "f": [], "e": []}]}, {"sub_title": "To lie on", "content": [{"c": "nằm trên.", "f": [], "e": [{"e": "The university lies in the western section of town."}, {"e": "Don''t disturb Mary, she has laid down for a rest."}]}, {"c": "Lưu ý: Cần phân biệt động từ này với to lie (nói dối) trong mẫu câu to lie to sb about smt (nói dối ai về cái gì):", "f": [], "e": [{"e": "He is lying to you about the test."}]}]}, {"sub_title": "To lay smt", "content": [{"c": "đặt, để cái gì vào vị trí nào đó ", "f": [], "e": []}]}, {"sub_title": "To lay smt on", "content": [{"c": "đặt trên", "f": [], "e": []}]}, {"sub_title": "To lay smt in", "content": [{"c": "đặt vào", "f": [], "e": []}]}, {"sub_title": "To lay smt down", "content": [{"c": "đặt xuống.", "f": [], "e": [{"e": "Don''t lay your clothes on the bed."}, {"e": "The enemy soldiers laid down their weapons and surrendered."}]}, {"c": "Lưu ý: Thì hiện tại của động từ này rất dễ ngây nhầm lẫn với quá khứ đơn giản của động từ to lie. Cần phải phân biệt bằng ngữ cảnh cụ thể.", "f": [], "e": []}]}, {"sub_title": "To sit", "content": [{"c": "ngồi", "f": [], "e": []}]}, {"sub_title": "To sit in", "content": [{"c": "ngồi tại, ngồi trong.", "f": [], "e": []}]}, {"sub_title": "To sit on", "content": [{"c": "ngồi trên (đã ngồi sẵn)", "f": [], "e": []}]}, {"sub_title": "To set = to put, to lay", "content": [{"c": "đặt, để.", "f": [], "e": [{"e": "We are going to sit in the fifth row at the opera."}, {"e": "After swimming, she sat on the beach to dty off."}, {"e": "Nobody has sat through as many boring lectures as Petes has."}]}, {"c": "Lưu ý: Phát âm hiện tại đơn giản của động từ này rất dễ lẫn với Simple past của to sit.", "f": [], "e": []}]}, {"sub_title": "To set smt", "content": [{"c": "đặt cái gì, bày cái gì, để cái gì", "f": [], "e": [{"e": "The little girl help her father set the table every night."}, {"e": "The botanist set his plants in the sun so that they would grow."}]}, {"c": "Một số các thành ngữ dùng với các động từ trên", "f": [], "e": []}, {"c": "to set the table for: bầy bàn để", "f": [], "e": [{"e": "My mother has set the table for the family dinner."}]}, {"c": "to lay off (workers. employees): cho thôi việc, giãn thợ.", "f": [], "e": [{"e": "The company had to lay off twenty-five employees because of a production slowdown."}]}, {"c": "to set (broken bone) in: bó những cái xương gẫy vào. ", "f": [], "e": [{"e": "Dr.Jacobs has set many broken bones in plaster casts."}]}, {"c": "to set one''s alarm for: Đặt đồng hồ báo thức vào lúc. ", "f": [], "e": [{"e": "John set his alarm for six o''clock."}]}, {"c": "to set fire to: làm cháy", "f": [], "e": [{"e": "While playing with matches, the children set fire to the sofa."}]}, {"c": "to raise (animals, plants) for a living: trồng cái gì, nuôi con gì bán để lấy tiền. ", "f": [], "e": [{"e": "That farmer raises chickens for a living."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (152, '4', 'Một số các động từ đặc biệt khác', '{}', 'Một số các động từ đặc biệt khác, Một số các động từ đặc biệt khác', '{}', '[{"sub_title": "", "content": [{"c": "Đó là những động từ ở trong các mẫu câu khác nhau sẽ có ý nghĩa khác nhau.", "f": [], "e": []}, {"c": "agree to do smt: đồng ý làm gì", "f": [], "e": [{"e": "He agreed to leave early tomorrow morning."}]}, {"c": "agree to one''s doing smt: đồng ý với việc ai làm gì.", "f": [], "e": [{"e": "He agreed to my leaving early tomorrow morning."}]}, {"c": "Mean to do smt: định làm gì.", "f": [], "e": [{"e": "I mean to get to the top of the hill before sunrise."}]}, {"c": "If it + mean + verb-ing: cho dù cả việc, bao hàm cả việc.", "f": [], "e": [{"e": "My neighbour was determined to get a ticket for Saturday''s game if it meant standing in line all night."}]}, {"c": "Propose to do smt: có ý định làm gì. ", "f": [], "e": [{"e": "I propose to start tomorrow."}]}, {"c": "Propose doing smt: Đề nghị làm gì", "f": [], "e": [{"e": "I propose waiting till the police came."}]}, {"c": "Go on doing smt/smt: tiếp tục làm gì, cái gì (một việc đang bị bỏ dở) ", "f": [], "e": [{"e": "He went on writing after a break"}]}, {"c": "Go on to do smt: Quay sang tiếp tục làm gì (vẫn về cùng một vấn đề nhưng chuyển sang khía cạnh khác)", "f": [], "e": [{"e": "He showed the island on the map then went on to tell about its climate."}]}, {"c": "Try to do smt: cố gắng làm gì", "f": [], "e": [{"e": "He try to solve this math problem."}]}, {"c": "Try doing smt: Thử làm gì", "f": [], "e": [{"e": "I try sending her flowers, writing her letter, giving her presents, but she still wouldn’t speak to me."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (153, '4', 'Sự phù hợp về thì giưa hai vế của một câu', '{}', 'Sự phù hợp về thì giưa hai vế của một câu, Sự phù hợp về thì giưa hai vế của một câu', '{}', '[{"sub_title": "", "content": [{"c": "Trong một câu tiếng Anh có 2 vế (hai mệnh đề) thì của động từ ở hai thành phần đó phải phù hợp với nhau. Thì của động từ ở mệnh đề chính sẽ quyết định thì của động từ ở mệnh đề phụ.", "f": [], "e": []}]}, {"sub_title": "Simple present <> Present progressive", "content": [{"c": "Hành động của mệnh đề phụ xảy ra cùng lúc với hành động của mệnh đề chính ", "f": [], "e": [{"e": "I see that Hung is writing a letter."}, {"e": "Do you know who is riding the horse?"}]}]}, {"sub_title": "Simple present <> Will/ Can/ May + Verb (hoặc Near future)", "content": [{"c": "Hành động của mệnh đề phụ xảy ra sau hành động của mệnh đề chính trong một tương lai không được xác định hoặc tương lai gần.", "f": [], "e": [{"e": "He says that he will look for a job."}, {"e": "I know that she is going to win that prize."}, {"e": "She says that she can play the piano."}]}]}, {"sub_title": "Simple present <> Simple past", "content": [{"c": "Hành động của mệnh đề phụ xảy ra trước hành động của mệnh đề chính trong một quá khứ được xác định cụ thể về mặt thời gian.", "f": [], "e": [{"e": "I hope he arrived safely."}, {"e": "They think he was here last night."}]}]}, {"sub_title": "Simple present <> Present perfect (progressive)", "content": [{"c": "Hành động của mệnh đề phụ xảy ra trước hành động của mệnh đề chính trong một quá khứ không được xác định cụ thể về mặt thời gian.", "f": [], "e": [{"e": "He tells us that he has been to Saigon before."}, {"e": "We know that you have spoken with Mike about the party."}]}]}, {"sub_title": "Simple past <> Past progressive/ Simple past", "content": [{"c": "Hành động của mệnh đề phụ xảy ra cùng lúc với hành động của mệnh đề chính trong quá khứ.", "f": [], "e": [{"e": "I gave the package to my sister when she visited us last week."}, {"e": "Mike visited us while he was staying in Hanoi."}]}]}, {"sub_title": "Simple past <> Would/ Could/ Might + Verb", "content": [{"c": "Hành động của mệnh đề phụ xảy ra sau hành động của mệnh đề chính trong một tương lai trong quá khứ.", "f": [], "e": [{"e": "He said that he would look for a job."}, {"e": "Mary said that she could play the piano."}]}]}, {"sub_title": "Simple past <> Past perfect", "content": [{"c": "Hành động của mệnh đề phụ xảy ra trước hành động của mệnh đề chính, lùi sâu về trong quá khứ.", "f": [], "e": [{"e": "I hoped he had arrived safely."}, {"e": "They thought he had been here last night."}]}, {"c": "Lưu ý: Những nguyên tắc trên đây chỉ được áp dụng khi các mệnh đề trong câu có liên hệ với nhau về mặt thời gian nhưng nếu các mệnh đề trong câu có thời gian riêng biệt thì động từ phải tuân theo thời gian của chính mệnh đề đó.", "f": [], "e": [{"e": "He promised to tell me, till now I haven''t received any call from him, though."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (154, '4', 'Cách sử dụng to say, to tell', '{}', 'Cách sử dụng to say, to tell, Cách sử dụng to say, to tell', '{}', '[{"sub_title": "", "content": [{"c": "Hai động từ này có nghĩa giống nhau nhưng cách dùng khác nhau. Nếu sau chủ ngữ không có một tân ngữ gián tiếp nào (tức là ta không đề cập đến đối tượng nào nghe trong câu) thì phải dùng to say, ngược lại thì dùng to tell.", "f": ["S + say + (that) + S + V ..."], "e": [{"e": "He says that he is busy today."}, {"e": "Henry says that he has already done his homework."}]}, {"c": "Nhưng nếu sau chủ ngữ có một tân ngữ gián tiếp (có đề cập đến người nghe) rồi mới đến liên từ that thì phải dùng to tell.", "f": ["S + tell + indirect object + (that) + S + V ..."], "e": [{"e": "He told us a story last night."}, {"e": "He tells me to stay here, waiting for him."}]}, {"c": "To tell cũng thường có tân ngữ trực tiếp. Luôn dùng to tell khi nói đến các danh từ sau đây, cho dù có tân ngữ gián tiếp hay không:", "f": ["Tell + (a story/a joke/a secret/a lie/the truth/(the) time)"], "e": [{"e": "Never tell a secret to a person who spreads gossip."}, {"e": "The little boy was punished because he told his mother a lie."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (155, '4', 'One', '{"One và you"}', 'One và you, One', '{156}', '[{"sub_title": "", "content": [{"c": "Cả hai đại từ này trong một số trường hợp đều mang nghĩa \"một người\" hoặc \"anh ta\", \"chị ta\", tuy nhiên ngữ pháp sử dụng có khác nhau.", "f": [], "e": []}]}, {"sub_title": "", "content": [{"c": "Nếu ở đầu câu đã sử dụng đại từ one thì các đại từ tương ứng tiếp theo ở mệnh đề dưới phải là one, one''s, he, his.", "f": [], "e": [{"e": "If one takes this exam without studying, one is likely to fail. (Nếu một người không học bài mà đi thi thì anh ta rất dễ bị trượt)"}, {"e": "If one takes this exam without studying, he is likely to fail."}, {"e": "One should always do one''s homework. One should always do his homework."}]}, {"c": "Lưu ý một số người do cẩn thận muốn tránh phân biệt nam/ nữ đã dùng he or she, his or her nhưng điều đó là không cần thiết. Các đại từ đó chỉ được sử dụng khi nào ở phía trên có những danh từ chung chung như the side, the party.", "f": [], "e": [{"e": "The judge will ask the defendant party (bên bị cáo- danh từ chung) if he or she admits the allegations."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (156, '4', 'You', '{"One và you"}', 'One và you, You', '{155}', '[{"sub_title": "", "content": [{"c": "Nếu đại từ ở đầu câu là you thì các đại từ tương ứng tiếp theo ở dưới sẽ là you hoặc your.", "f": [], "e": [{"e": "If you take this exam without studying, you are likely to fail. You should always do your homework."}]}, {"c": "Tuyệt đối không dùng lẫn one và you trong cùng một câu hoặc dùng they thay thế cho hai đại từ này.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (157, '4', 'Đi trước đại từ phải có một danh từ tương ứng với nó', '{}', 'Đi trước đại từ phải có một danh từ tương ứng với nó, Đi trước đại từ phải có một danh từ tương ứng với nó', '{}', '[{"sub_title": "", "content": [{"c": "Trong một câu tiếng Anh có 2 mệnh đề, nếu mệnh đề thứ hai có dùng đến đại từ nhân xưng thì ở mệnh đề đầu phải có một danh từ để đại từ đó đại diện. Danh từ đi trước, giới thiệu đại từ phải tương đương với đại từ đó về giống và số.", "f": [], "e": []}]}, {"sub_title": "Incorrect:", "content": [{"c": "Đại từ they không có danh từ nào đi trước nó để nó làm đại diện. \"graduate school\" là một đơn vị số ít nên không thể được đại diện bởi they.", "f": [], "e": [{"e": "Henry was denied admission to graduate school because they did not believe that he could handle the work load."}]}]}, {"sub_title": "Correct:", "content": [{"c": "", "f": [], "e": [{"e": "The members of the admissions committee denied Henry admission to graduate school because they did not believe that he could handle the work load."}, {"e": "Henry was denied admission to graduate school because the members of the admissions committee did not believe that he could handle the work load."}]}]}, {"sub_title": "Incorrect:", "content": [{"c": "George không thích chính trị bởi vì anh cho rằng họ tham nhũng. Đại từ they cũng không có danh từ tương ứng đi trước. Politics là một danh từ số ít nên không thể được đại diện bởi they", "f": [], "e": [{"e": "George dislikes politics because he believes that they are corrupt."}]}]}, {"sub_title": "Correct:", "content": [{"c": "", "f": [], "e": [{"e": "George dislikes politicians because he believes that they are corrupt. (các chính khách)"}, {"e": "George dislikes politics because he believes that politicians are corrupt."}]}, {"c": "Ngược lại với việc thiếu danh từ đi trước đại từ, nếu có quá nhiều (từ hai trở lên) danh từ cùng tương ứng với một đại từ thì cũng sẽ gây nhầm lẫn. Xem ví dụ sau:", "f": [], "e": []}, {"c": "Incorrect 1:", "f": [], "e": [{"e": "Mr. Brown told Mr. Adams that he would have to work all night in order to finish the report. (Không rõ đại từ he đại diện cho Mr. Brown hay Mr. Adams)"}]}, {"c": "Correct 1:", "f": [], "e": [{"e": "According to Mr. Brown, Mr. Adams will have to work all night in order to finish the report."}, {"e": "Mr. Brown said that, in order to finish the report, Mr. Adams would have to work all night."}]}, {"c": "Incorrect 2:", "f": [], "e": [{"e": "Janet visited her friend every day while she was on vacation. (Không rõ đại từ she đại diện cho Janet hay her friend)"}]}, {"c": "Correct 2:", "f": [], "e": [{"e": "While Janet was on vacation, she visited her friend every day."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (158, '4', 'Sử dụng verb-ing', '{"Sử dụng v-ing, to + verb để mở đầu một câu"}', 'Sử dụng v-ing, to + verb để mở đầu một câu, Sử dụng verb-ing', '{159}', '[{"sub_title": "", "content": [{"c": "Một V-ing có thể được dùng để nối hai câu có cùng chủ ngữ trở thành một câu bằng cách biến động từ của câu thứ nhất thành dạng V-ing, bỏ chủ ngữ và nối với câu thứ hai bằng dấu phảy. Ví dụ:", "f": [], "e": [{"e": "The man jumped out of the boat. He was bitten by a shark."}, {"e": "After jumping out of the boat, the man was bitten by a shark."}]}, {"c": "Khi sử dụng loại câu này cần hết sức lưu ý rằng chủ ngữ của mệnh đề chính bắt buộc phải là chủ ngữ hợp lý của mệnh đề phụ. Khi hai mệnh đề không có cùng chủ ngữ, người ta gọi đó là trường hợp chủ ngữ phân từ bất hợp lệ.", "f": [], "e": [{"e": "After jumping out of the boat, the shark bit the man. (SAI) (Chúng ta ngầm hiểu là chủ ngữ thật sử của hành động nhảy khỏi thuyền là the man chứ không phải the shark)"}]}, {"c": "Để đảm bảo không nhầm lẫn, ngay sau mệnh đề verb-ing ta nên đề cập ngay đến danh từ làm chủ ngữ cho cả hai mệnh đề.", "f": [], "e": []}, {"c": "Thông thường có 6 giới từ đứng trước một V-ing mở đầu cho mệnh đề phụ, đó là: By (bằng cách, bởi), upon, after (sau khi), before (trước khi), while (trong khi), when (khi).", "f": [], "e": [{"e": "By working a ten-hour day for four days, we can have a long weekend."}, {"e": "After preparing the dinner, Pat will read a book."}, {"e": "While reviewing for the test, Mary realized that she had forgotten to study the use of participial phrases."}]}, {"c": "Lưu ý rằng: on + động từ trạng thái hoặc in + động từ hành động thì có thể tương đương với when hoặc while:", "f": [], "e": [{"e": "On finding the door ajar, I aroused suspicion. (= when finding) (Khi thấy cửa hé mở, tôi nảy sinh mối nghi ngờ)"}, {"e": "In searching for underground deposits of oil, geologist often rely on magnometers. (= while searching) (Trong khi tìm các mỏ dầu trong lòng đất, các nhà địa chất thường dựa vào từ kế.)"}]}, {"c": "Nếu không có giới từ đi trước, chỉ có V-ing xuất hiện trong mệnh đề phụ thì thời của câu do thì của động từ ở mệnh đề chính quyết định; 2 hành động trong hai mệnh đề thường xảy ra song song cùng lúc:", "f": [], "e": []}]}, {"sub_title": "Present:", "content": [{"c": "", "f": [], "e": [{"e": "Practicing her swing every day, Trica hopes to get a job as a golf instructor."}]}]}, {"sub_title": "Past:", "content": [{"c": "", "f": [], "e": [{"e": "Having a terrible toothache, Henry called the dentist for an appointment."}]}]}, {"sub_title": "Future:", "content": [{"c": "", "f": [], "e": [{"e": "Finishing the letter later tonight, Sally will mail it tomorrow morning."}]}, {"c": "Dạng thức hoàn thành [having + P2] được dùng để diễn đạt trường hợp động từ của mệnh đề phụ xảy ra trước hành động của mệnh đề chính:", "f": [], "e": [{"e": "Having finished their supper, the boys went out to play. (After the boys had finished their supper...)"}, {"e": "Having written his composition, Louie handed it to his teacher. (After Louie had written ...)"}, {"e": "Not having read the book, she could not answer the question. (Because she had not read...)"}]}, {"c": "Dạng thức bị động [having been + P2] cũng thường được sử dụng để mở đầu một mệnh đề phụ: ", "f": [], "e": [{"e": "Having been notified by the court, Mary reported for jury duty. (After Mary had been notified ...)"}, {"e": "Having been delayed by the snowstorm, Jame and I missed our connecting flight. (After we had been delayed ...)"}, {"e": "Not having been notified of the change in the meeting time, George arrived late. (Because he had not been notified ...)"}]}, {"c": "Trong nhiều trường hợp, cụm từ being hoặc having been của thể bị động có thể được lược bỏ, khi đó chủ ngữ của mệnh đề chính vẫn phải phù hợp với chủ ngữ của mệnh đề phụ:", "f": [], "e": []}, {"c": "Incorrect: ", "f": [], "e": [{"e": "Found in Tanzania by Mary Leaky, some archeologists estimated that the three-million-year-old fossils were the oldest human remains that were discovered. (Being found ...)"}]}, {"c": "Correct: ", "f": [], "e": [{"e": "Found in Tanzania by Mary Leaky, the three-million-year-old fossils were estimated by some archeologists to be the oldest human remains that had ever been discovered."}]}, {"c": "Xét thêm các ví dụ sau về chủ ngữ phân từ bất hợp lệ:", "f": [], "e": [{"e": "Having apprehended the hijackers, they were whisked off to FBI headquarters by the security guards. (SAI)"}, {"e": "Having apprehended the hijackers, the security guards whisked them off to FBI headquarters. (ĐÚNG)"}, {"e": "Having been apprehended, the hijackers were whisked off to FBI headquarters by the security guards. (ĐÚNG)"}, {"e": "Before singing the school song, a poem was recited. (SAI)"}, {"e": "Before singing the school song, the students recited a poem. (ĐÚNG)"}, {"e": "Guiding us through the museum, a special explanation was given by the director. (SAI)"}, {"e": "Guiding us through the museum, the director gave us a special explanation. (ĐÚNG)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (159, '4', 'Động từ nguyên thể (to + verb) mở đầu câu', '{"Sử dụng v-ing, to + verb để mở đầu một câu"}', 'Sử dụng v-ing, to + verb để mở đầu một câu, Động từ nguyên thể (to + verb) mở đầu câu', '{158}', '[{"sub_title": "", "content": [{"c": "Động từ nguyên thể cũng được dùng để mở đầu một câu giống như trong trường hợp V-ing. Mệnh đề phụ đứng đầu câu sử dụng động từ nguyên thể thường diễn tả mục đích của mệnh đề chính.", "f": [], "e": [{"e": "To get up early, Jim never stay up late."}]}, {"c": "Cũng giống như trường hợp sử dụng V-ing nêu trên, chủ ngữ của mệnh đề thứ hai cũng phải là chủ ngữ hợp lý của mệnh đề phụ đứng trước nó.", "f": [], "e": [{"e": "To prevent cavities, dental floss should be used daily after brushing one''s teeth. (SAI)"}, {"e": "To prevent cavities, one should use dental floss daily after brushing one’s teeth. (ĐÚNG)"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (160, '4', 'Động từ (v-ing, v-ed) dùng làm tính từ', '{}', 'Động từ (v-ing, v-ed) dùng làm tính từ, Động từ (v-ing, v-ed) dùng làm tính từ', '{}', '[{"sub_title": "", "content": [{"c": "Thông thường, khi một động từ không có dạng thức tính từ tương ứng với nó thì phân từ 1 (V-ing) hoặc phân từ 2 (P2) của động từ đó được sử dụng làm tính từ. Đôi khi người học tiếng Anh không biết nên dùng tính từ ở dạng thức nào: V-ing hay Verb-ed hay Verb-en.", "f": [], "e": []}, {"c": "Tính từ dạng V-ing thường được dùng khi danh từ mà nó bổ nghĩa thực hiện hoặc chịu trách nhiệm về hành động. Động từ thường là nội động từ (không có tân ngữ) và thì của động từ là thì tiếp diễn:", "f": [], "e": [{"e": "The crying baby woke Mr.Binion. (The baby was crying)"}, {"e": "The blooming flowers in the meadow created a rainbow of colors. (The flowers were blooming)"}, {"e": "The purring kitten snuggled close to the fireplace. (The kitten was purring)"}]}, {"c": "Phân từ 2 (V-ed) được dùng làm tính từ khi danh từ mà nó bổ nghĩa là đối tượng nhận sự tác động của hành động. Câu có tính từ ở dạng P2 thường có nguồn gốc từ những câu bị động.", "f": [], "e": [{"e": "The sorted mail was delivered to the offices before noon. (The mail had been sorted)."}, {"e": "Frozen food is often easier to prepare than fresh food. (The food had been frozen)"}, {"e": "The imprisoned men were unhappy with their living conditions. (The men had been imprisoned)"}]}, {"c": "Lưu ý: Một số các động từ như to interest, to bore, to excite, to frighten khi sử dụng làm tính từ thường khó xác định nên dùng loại nào (phân từ 1 hay phân từ 2). Nguyên tắc áp dụng cũng giống như đã nêu trên: Nếu chủ ngữ gây ra hành động thì dùng P1, nếu chủ ngữ nhận tác động của hành động thì dùng P2. Xét thêm các ví dụ sau:", "f": [], "e": [{"e": "The boring professor put the students to sleep."}, {"e": "The boring lecture put the students to sleep."}, {"e": "The bored students went to sleep during the boring lecture."}, {"e": "The child saw a frightening movie."}, {"e": "The frightened child began to cry."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (161, '4', 'Thông tin thừa (redundancy)', '{}', 'Thông tin thừa (redundancy), Thông tin thừa (redundancy)', '{}', '[{"sub_title": "", "content": [{"c": "Khi thông tin trong câu bị lặp đi lặp lại không cần thiết thì nó bị gọi là thông tin thừa, cần phải loại bỏ phần thừa đó. Ví dụ: Người Anh không dùng the time when mà chỉ dùng một trong hai.", "f": [], "e": [{"e": "It is (the time/ when) I got home."}]}, {"c": "Người Anh không dùng the place where mà chỉ dùng một trong hai.", "f": [], "e": [{"e": "It is (the place/ where) I was born."}]}, {"c": "Không dùng song song cả 2 từ dưới đây trong cùng một câu:", "f": [], "e": []}, {"c": "advance, proceed, progress = forward: Cả 3 từ advance, proceed, progress đều có nghĩa tiến lên, tiến về phía trước: \"to move in a forward direction\". Vì vậy forward là thừa.", "f": [], "e": []}, {"c": "return, revert = back: Cả 2 từ return, revert đều có nghĩa: \"to go back, to send back\". Vì vậy back là thừa.", "f": [], "e": []}, {"c": "sufficient = enought: Hai từ này nghĩa như nhau", "f": [], "e": []}, {"c": "compete together: compete = đua tranh, cạnh tranh với nhau \"to take part in a contest against others\"", "f": [], "e": []}, {"c": "reason... because: Hai từ này có nghĩa như nhau. Mẫu đúng phải là \"reason... that\"", "f": [], "e": []}, {"c": "join together: join có nghĩa \"to bring together\", \"to put together\", \"to become a part or a member of...\"", "f": [], "e": []}, {"c": "repeat = again: repeat có nghĩa \"to say again\"", "f": [], "e": []}, {"c": "new innovation: innovation có nghĩa là một ý tưởng mới \"a new idea\"", "f": [], "e": []}, {"c": "matinee performance: matinee = buổi biểu diễn chiều", "f": [], "e": []}, {"c": "same = identical: hai từ này nghĩa giống nhau", "f": [], "e": []}, {"c": "two twins: twins = two brothers or sisters", "f": [], "e": []}, {"c": "the time / when: Hai từ này nghĩa giống nhau", "f": [], "e": []}, {"c": "the place / where: Hai từ này nghĩa giống nhau", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (162, '4', 'Cấu trúc song song trong câu', '{}', 'Cấu trúc song song trong câu, Cấu trúc song song trong câu', '{}', '[{"sub_title": "", "content": [{"c": "Khi thông tin trong một câu được đưa ra dưới dạng liệt kê thì các thành phần được liệt kê phải tương ứng với nhau về mặt từ loại (noun - noun, adjective - adjective, ...). Ví dụ:", "f": [], "e": [{"e": "Mr. Henry is a lawyer, a politician, and he teaches. (SAI)"}, {"e": "Mr. Henry is a lawyer, a politician, and a teacher. (ĐÚNG)"}, {"e": "Peter is rich, handsome, and many people like him. (SAI)"}, {"e": "Peter is rich, handsome, and popular. (ĐÚNG)"}, {"e": "The cat approached the mouse slowly and silent. (SAI)"}, {"e": "The cat approached the mouse slowly and silently. (ĐÚNG)"}, {"e": "She like to fish, swim and surfing.\tShe like to fish, to swim and to surf. (SAI)"}, {"e": "She like fishing, swimming and surfing. (ĐÚNG)"}, {"e": "When teenagers finish highschool, they have several choices: going to college, getting a job, or the army. (SAI)"}, {"e": "When teenagers finish highschool, they have several choices: going to college, getting a job, or joining the army. (ĐÚNG)"}, {"e": "Tim entered the room, sat down, and is opening his book. (SAI)"}, {"e": "Tim entered the room, sat down, and opened his book. (ĐÚNG)"}]}, {"c": "Tuy nhiên nếu thời gian trong các mệnh đề khác nhau của câu là khác nhau thì động từ cũng phải tuân theo qui luật thời gian. Lúc đó cấu trúc câu song song về thì của các động từ (như ví dụ ở dòng cuối phía trên) không được áp dụng. Ví dụ:", "f": [], "e": [{"e": "She is a senior, studies every day, and will graduate a semester early."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (163, '4', 'Chuyển đổi vị trí tân ngữ trực tiếp và gián tiếp', '{}', 'Chuyển đổi vị trí tân ngữ trực tiếp và gián tiếp, Chuyển đổi vị trí tân ngữ trực tiếp và gián tiếp', '{}', '[{"sub_title": "", "content": [{"c": "Có hai cách sử dụng tân ngữ của một ngoại động từ mà ý nghĩa của câu không thay đổi. Tân ngữ gián tiếp (indirect object) là tân ngữ chỉ đồ vật hoặc người mà hành động xảy ra đối với (hoặc dành cho) đồ vật hoặc người đó. Tân ngữ trực tiếp (direct object) là người hoặc vật đầu tiên nhận tác động của hành động. Tân ngữ gián tiếp có thể đứng sau tân ngữ trực tiếp, ngăn cách bởi một giới từ hoặc cũng có thể đứng trước tân ngữ trực tiếp (mà không có giới từ). Giới từ thường dùng ở đây là for và to. Ví dụ:", "f": [], "e": [{"e": "I gave the book to Jim."}]}, {"c": "The book là tân ngữ trực tiếp và hành động đầu tiên là hành động cầm lấy quyển sách trong tay và hành động thứ hai (gián tiếp) là đưa nó cho Jim.", "f": [], "e": []}, {"c": "Khi muốn đổi vị trí giữa tân ngữ trực tiếp và tân ngữ gián tiếp, ta sử dụng một trong hai công thức sau:", "f": ["Subject + verb + direct object + [for/to] + indirect object"], "e": [{"e": "I gave the book to Jim."}, {"e": "The little boy brought some flowers for his grandmother."}, {"e": "He lent his car to his brother"}]}, {"c": "", "f": ["Subject + verb + indirect object + direct object"], "e": [{"e": "I gave Jim the book."}, {"e": "The little boy brought his grandmother some flowers."}, {"e": "He lent his brother his car."}]}, {"c": "Không phải động từ nào cũng cho phép đổi vị trí tân ngữ trực tiếp và gián tiếp. Ví dụ sau là một số động từ cho phép đổi:", "f": [], "e": [{"e": "bring\tfind\tmake\tpromise\ttell"}, {"e": "build\tget\toffer\tread\twrite"}, {"e": "buy\tgive\town\tsell"}, {"e": "cut\thand\tpaint\tsend"}, {"e": "draw\tleave\tpass\tshow"}, {"e": "feed\tlend\tpay\tteach"}]}, {"c": "Một số trong các từ trên có thể dùng được cả với for và to, một số khác chỉ dùng với một trong 2 giới từ. Chú ý rằng trong công thức thứ hai nêu trên, khi tân ngữ gián tiếp đứng trước tân ngữ trực tiếp thì không dùng giới từ giữa chúng:", "f": [], "e": [{"e": "The director''s secretary sent them the menuscript last night. (ĐÚNG)"}, {"e": "The director''s secretary sent to them the menuscript last night. (SAI)"}]}, {"c": "Nếu cả tân ngữ trực tiếp và gián tiếp đều là đại từ thì chỉ được dùng công thức thứ nhất, không dùng công thức 2:", "f": [], "e": [{"e": "They gave it to us. (ĐÚNG)"}, {"e": "They gave us it. (SAI)"}]}, {"c": "Chú ý: Hai động từ introduce (giới thiệu ai với ai) và mention (đề cập đến cái gì trước mặt ai) luôn dùng cùng với giới từ to nên chúng không cho phép chuyển đổi vị trí hai tân ngữ trực tiếp và gián tiếp.", "f": [], "e": [{"e": "I introduce John to Dr. Tim."}, {"e": "I introduce Dr. Tim to John."}, {"e": "He mentions the party to me."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (164, '4', 'Câu trực tiếp và câu gián tiếp', '{}', 'Câu trực tiếp và câu gián tiếp, Câu trực tiếp và câu gián tiếp', '{}', '[{"sub_title": "", "content": [{"c": "Trong câu trực tiếp thông tin đi từ người thứ nhất đến thẳng người thứ hai (người thứ nhất nói trực tiếp với người thứ hai).", "f": [], "e": [{"e": "He said \"I bought a new motorbike for myself yesterday\"."}]}, {"c": "Trong câu gián tiếp thông tin đi từ người thứ nhất qua người thứ hai đến với người thứ ba. Khi đó câu có biến đổi về mặt ngữ pháp.", "f": [], "e": [{"e": "He said he had bought a new motorbike for himself the day before."}]}]}, {"sub_title": "Để biến đổi một câu trực tiếp sang câu gián tiếp cần:", "content": [{"c": "1. Đổi chủ ngữ và các đại từ nhân xưng khác trong câu trực tiếp theo chủ ngữ của thành phần thứ nhất.", "f": [], "e": []}, {"c": "2. Lùi động từ ở vế thứ 2 xuống một cấp so với lúc ban đầu.", "f": [], "e": []}, {"c": "3. Biến đổi các đại từ chỉ thị, phó từ chỉ thời gian và địa điểm theo bảng qui định.", "f": [], "e": []}]}, {"sub_title": "Bảng đổi động từ: Direct speech -> Indirect speech", "content": [{"c": "Simple present -> Simple past", "f": [], "e": []}, {"c": "Present progressive -> Past progressive", "f": [], "e": []}, {"c": "Present perfect (Progressive) -> Past perfect (Progressive)", "f": [], "e": []}, {"c": "Simple past -> Past perfect", "f": [], "e": []}, {"c": "Will/Shall\t-> Would/ Should", "f": [], "e": []}, {"c": "Can/May -> Could/ Might", "f": [], "e": []}]}, {"sub_title": "Bảng đổi các đại từ chỉ thị, phó từ chỉ địa điểm và thời gian", "content": [{"c": "Today -> That day", "f": [], "e": []}, {"c": "Yesterday -> The day before", "f": [], "e": []}, {"c": "The day before yesterday -> Two days before", "f": [], "e": []}, {"c": "Tomorrow -> The next/ the following day", "f": [], "e": []}, {"c": "The day after tomorrow -> In two days'' time", "f": [], "e": []}, {"c": "Next + Time -> The following + Time", "f": [], "e": []}, {"c": "Last + Time -> The previous + Time", "f": [], "e": []}, {"c": "Time + ago -> Time + before", "f": [], "e": []}, {"c": "This, these -> That, those", "f": [], "e": []}, {"c": "Here, Overhere -> There, Overthere", "f": [], "e": []}, {"c": "Nếu lời nói và hành động xảy ra cùng ngày thì không cần phải đổi thời gian.", "f": [], "e": [{"e": "At breakfast this morning he said \"I will be busy today\"."}, {"e": "At breakfast this morning he said he would be busy today."}]}, {"c": "Các suy luận logic về mặt thời gian tất nhiên là cần thiết khi lời nói được thuật lại sau đó một hoặc hai ngày.", "f": [], "e": [{"e": "(On Monday) He said \"I''ll be leaving on Wednesday\""}, {"e": "(On Tuesday) He said he would be leaving tomorrow."}, {"e": "(On Wednesday) He said he would be leaving today."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (165, '4', 'Khi phó từ đứng đầu câu để nhấn mạnh, phải đảo cấu trúc câu', '{}', 'Khi phó từ đứng đầu câu để nhấn mạnh, phải đảo cấu trúc câu, Khi phó từ đứng đầu câu để nhấn mạnh, phải đảo cấu trúc câu', '{}', '[{"sub_title": "", "content": [{"c": "Trong một số trường hợp các phó từ không đứng ở vị trí bình thường của nó mà đảo lên đứng ở đầu câu nhằm nhấn mạnh vào hành động của chủ ngữ. Trong trường hợp đó ngữ pháp sẽ thay đổi, đằng sau phó từ đứng đầu câu là trợ động từ rồi mới đến chủ ngữ và động từ chính:", "f": [], "e": [{"e": "John hardly remembers the accident that took his sister''s life."}, {"e": "Hardly does John remember the accident that took his sister''s life."}]}, {"c": "", "f": ["{hardly/rarely/seldom/never/only...} + auxiliary + subject + verb ..."], "e": [{"e": "Never have so many people been unemployed as today. (So many people have never been unemployed as today)"}, {"e": "Hardly had he fallen asleep when he began to dream of far-way lands. (He had hardly fallen asleep when he began to dream of far-way lands.)"}, {"e": "Rarely have we seen such an effective actor as he has proven himself to be. (We have rarely seen such an effective actor as he has proven himself to be.)"}, {"e": "Seldom does class let out early. (Class seldom lets out early.)"}, {"e": "Only by hard work will we be able to accomplish this great task.  (We will be able to accomplish this great task only by hard work.)"}]}]}, {"sub_title": "Một số các trạng từ đặc biệt khác cũng thường đứng ở đầu câu, phải đảo cấu trúc câu như trên:", "content": [{"c": "In/ Under no circumstances: Dù trong bất cứ hoàn cảnh nào cũng không. ", "f": [], "e": [{"e": "Under no circumstances should you lend him the money."}]}, {"c": "On no account: Dù bất cứ lý do gì cũng không ", "f": [], "e": [{"e": "On no accout must this switch be touched."}]}, {"c": "Only in this way: Chỉ bằng cách này", "f": [], "e": [{"e": "Only in this way could the problem be solved"}]}, {"c": "In no way: Không sao có thể", "f": [], "e": [{"e": "In no way could I agree with you."}]}, {"c": "By no means: Hoàn toàn không", "f": [], "e": [{"e": "By no means does he intend to criticize your idea."}]}, {"c": "Negative ..., nor + auxiliary + S + V", "f": [], "e": [{"e": "He had no money, nor did he know anybody from whom he could borrow."}]}, {"c": "Khi một mệnh đề mở đầu bằng các thành ngữ chỉ nơi chốn hoặc trật tự thì động từ chính có thể đảo lên chủ ngữ nhưng tuyệt đối không được sử dụng trợ động từ trong trường hợp này.", "f": ["Clause of place/ order + main verb + S (no auxiliary) In front of the museum is a statue."], "e": [{"e": "First came the ambulance, then came the police. (Thoạt đầu là xe cứu thương chạy đến, tiếp sau là cảnh sát.)"}]}, {"c": "Khi một ngữ giới từ làm phó từ chỉ địa điểm hoặc phương hướng đứng ở đầu câu, các nội động từ đặt lên trước chủ ngữ nhưng tuyệt đối không được sử dụng trợ động từ trong loại câu này. Nó rất phổ biến trong văn mô tả khi muốn diễn đạt một chủ ngữ không xác định:", "f": [], "e": [{"e": "Under the tree was lying one of the biggest men I had ever seen."}, {"e": "Directly in front of them stood a great castle."}, {"e": "On the grass sat an enormous frog."}, {"e": "Along the road came a strange procession."}]}, {"c": "Tính từ cũng có thể đảo lên trên đầu câu để nhấn mạnh và sau đó là liên từ nhưng tuyệt đối không được sử dụng trợ động từ.", "f": [], "e": [{"e": "So determined was she to take the university course that she taught school and gave music lesson for her tuition fees."}]}, {"c": "Trong một số trường hợp người ta cũng có thể đảo toàn bộ động từ chính lên trên chủ ngữ để nhấn mạnh, những động từ được đảo lên trên trong trường hợp này phần lớn đều mang sắc thái bị động nhưng hoàn toàn không có nghĩa bị động.", "f": [], "e": [{"e": "Lost, however, are the secrets of the Mayan astronomers and the Inca builders as well as many medicinal practices. (Mất đi vĩnh viễn, tuy vậy, lại là những bí mật của những nhà thiên văn thuộc nền văn minh Mayan, ...)"}]}, {"c": "Các phó từ away ( = off ), down, in, off, out, over, round, up... có thể theo sau là một động từ chuyển động và sau đó là một danh từ làm chủ ngữ:", "f": ["Away/down/in/off/out/over/round/up...+ motion verb + noun/noun phrase as a subject."], "e": [{"e": "Away went the runners/ Down fell a dozen of apples..."}]}, {"c": "Nhưng nếu một đại từ nhân xưng làm chủ ngữ thì động từ phải để sau chủ ngữ: Away they went/ Round and round it flew.", "f": [], "e": []}, {"c": "Trong tiếng Anh viết (written English) các ngữ giới từ mở đầu bằng các giới từ down, from, in, on, over, out of, round, up... có thể được theo sau ngay bởi động từ chỉ vị trí (crouch, hang, lie, sit, stand...) hoặc các dạng động từ chỉ chuyển động, các động từ như be born/ die/ live và một số dạng động từ khác.", "f": [], "e": [{"e": "From the rafters hung strings of onions."}, {"e": "In the doorway stood a man with a gun."}, {"e": "On a perch beside him sat a blue parrot."}, {"e": "Over the wall came a shower of stones."}]}]}, {"sub_title": "Lưu ý:", "content": [{"c": "3 ví dụ đầu của các ví dụ trên có thể diễn đạt bằng một VERB-ING mở đầu cho câu và động từ BE đảo lên trên chủ ngữ:", "f": [], "e": [{"e": "Hanging from the rafters were strings of onion."}, {"e": "Standing in the doorway was a man with a gun."}, {"e": "Sitting on a perch beside him was a blue parrot."}]}, {"c": "Hiện tượng này còn xảy ra khi chủ ngữ sau các phó từ so sánh as/than quá dài: ", "f": [], "e": [{"e": "She was very religious, as were most of her friends."}, {"e": "City dwellers have a higher death rate than do country people."}]}, {"c": "Tân ngữ mang tính nhấn mạnh cũng có thể đảo lên đầu câu: ", "f": [], "e": [{"e": "Not a single word did he say."}]}, {"c": "Here/There hoặc một số các phó từ đi kết hợp với động từ đứng đầu câu cũng phải đảo động từ lên trên chủ ngữ là một danh từ, nhưng nếu chủ ngữ là một đại từ thì không được đảo động từ:", "f": [], "e": [{"e": "Here comes Freddy."}, {"e": "Here comes he (Incorrect)"}, {"e": "Off we go"}, {"e": "Off go we (Incorrect)"}, {"e": "There goes your brother"}, {"e": "I stopped the car, and up walked a policeman"}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (166, '4', 'Cách làm bài trắc nghiệm ngữ pháp', '{}', 'Cách làm bài trắc nghiệm ngữ pháp, Cách làm bài trắc nghiệm ngữ pháp', '{}', '[{"sub_title": "", "content": [{"c": "Thường thì trong bài thi trắc nghiệm ngữ pháp, bạn sẽ không thể trả lời ngay bằng cách chỉ áp dụng một quy tắc ngữ pháp nhất định nào đó mà phải kết hợp hai hay nhiều quy tắc khác nhau để loại trừ phương án sai. Áp dụng lần lượt các bước sau:", "f": [], "e": []}]}, {"sub_title": "Kiểm tra các lỗi ngữ pháp cơ bản bao gồm", "content": [{"c": "a)\tSự hoà hợp giữa chủ ngữ và vị ngữ", "f": [], "e": []}, {"c": "b)\tCách sử dụng Adj và Adv", "f": [], "e": []}, {"c": "c)\tVị trí và thứ tự của các Adv.", "f": [], "e": []}, {"c": "d)\tSự phù hợp giữa các thì động từ.", "f": [], "e": []}, {"c": "e)\tĐại từ có danh từ duy nhất để nó đại diện hay không.", "f": [], "e": []}, {"c": "f)\tCấu trúc câu song song.", "f": [], "e": []}]}, {"sub_title": "Loại bỏ những câu trả lời rườm rà, câu càng ngắn gọn và dễ hiểu càng tốt:", "content": [{"c": "a)\tTránh các đáp án dài dòng như:", "f": [], "e": [{"e": "John read the letter in a thoughtful manner. (can be replaced by thoughtfully)"}]}, {"c": "b)\tTránh các đáp án có 2 từ nghĩa giống nhau.", "f": [], "e": []}]}, {"sub_title": "Loại bỏ những đáp án có từ vựng không rõ nghĩa:", "content": [{"c": "a)\tPhải chắc chắn rằng tất cả các từ đều có nghĩa trong câu.", "f": [], "e": []}, {"c": "b)\tPhải đảm bảo những động từ có 2 từ phải được nối với nhau bằng một giới từ phù hợp (xem phần sau)", "f": [], "e": []}]}, {"sub_title": "Tránh dùng các từ lóng, tiếng lóng. Từ lóng chỉ dùng trong văn nói, không dùng trong văn viết. Ví dụ", "content": [{"c": "really khi dùng với nghĩa very bunch khi dùng với nghĩa many", "f": [], "e": []}, {"c": "any noun + wise khi dùng với nghĩa in relation to ...", "f": [], "e": []}]}, {"sub_title": "Ví dụ:", "content": [{"c": "Before we can decide on the future uses of this drug,  \t", "f": [], "e": [{"e": "A)\tmany more informations must be reviewed."}, {"e": "B)\tis necessary to review more information."}, {"e": "C)\twe must review much more information."}, {"e": "D)\tanother information must to be reviewed."}]}]}, {"sub_title": "Phân tích:", "content": [{"c": "A)\tcó 2 chỗ sai: không dùng many với information là danh từ không đếm được và không được viết là informations.", "f": [], "e": []}, {"c": "B)\tcó 1 chỗ sai vì thiếu chủ ngữ.", "f": [], "e": []}, {"c": "C)\tđúng", "f": [], "e": []}, {"c": "D)\tcó 2 chỗ sai: không dùng another với danh từ không đếm được và sau trợ động từ must là động từ nguyên thể không có to.", "f": [], "e": []}]}, {"sub_title": "In this country, a growing concern about the possible hazardous effects ofchemical wastes", "content": [{"c": "A)\thave resulted in a bunch of new laws.", "f": [], "e": []}, {"c": "B)\thas resulted in several new laws.", "f": [], "e": []}, {"c": "C)\tis causing the results of numerous new laws.", "f": [], "e": []}, {"c": "D)\tresult in news laws.", "f": [], "e": []}]}, {"sub_title": "Phân tích:", "content": [{"c": "A)\tcó 2 chỗ sai: không dùng động từ ở số nhiều have với chủ ngữ số ít a growing concern và không được dùng từ lóng (slang) bunch of.", "f": [], "e": []}, {"c": "B)\tđúng.", "f": [], "e": []}, {"c": "C)\trườm rà. Causing the result of là quá dài dòng.", "f": [], "e": []}, {"c": "D)\tcó 2 chỗ sai: không dùng result (động từ số nhiều) với chủ ngữ là danh từ số ít và không được dùng dạng thức số nhiều của tính từ (viết đúng phải là new laws).", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (167, '4', 'Những từ dễ gây nhầm lẫn', '{}', 'Những từ dễ gây nhầm lẫn, Những từ dễ gây nhầm lẫn', '{}', '[{"sub_title": "Đó là những từ rất dễ gây nhầm lẫn về cách viết, ngữ nghĩa, chính tả hoặc phát âm:", "content": [{"c": "angel (N) = thiên thần", "f": [], "e": []}, {"c": "angle (N) = góc (trong hình học)", "f": [], "e": []}, {"c": "cite (V) = trích dẫn", "f": [], "e": []}, {"c": "site (N) = địa điểm, khu đất (để xây dựng).", "f": [], "e": []}, {"c": "sight (N) = khe ngắm, tầm ngắm; (V) = quang cảnh, cảnh tượng; (V) = quan sát, nhìn thấy", "f": [], "e": []}, {"c": "dessert (N) = món tráng miệng", "f": [], "e": []}, {"c": "desert (N) = sa mạc; desert (V) = bỏ, bỏ mặc, đào ngũ", "f": [], "e": []}, {"c": "later (ADV) = sau đó, rồi thì (thường dùng với động từ thì tương lai)", "f": [], "e": []}, {"c": "atter (ADJ) = cái thứ 2, người thứ 2, cái sau, người sau. >< the former = cái trước, người trước.", "f": [], "e": []}, {"c": "principal (N) = hiệu trưởng (trường phổ thông); (Adj) = chính, chủ yếu.", "f": [], "e": []}, {"c": "principle (N) = nguyên tắc, luật lệ", "f": [], "e": []}, {"c": "affect (V) = tác động đến", "f": [], "e": []}, {"c": "effect (N) = ảnh hưởng, hiệu quả; (V) = thực hiện, đem lại", "f": [], "e": []}, {"c": "already (Adv) = đã", "f": [], "e": []}, {"c": "all ready = tất cả đã sẵn sàng.", "f": [], "e": []}, {"c": "among (prep) trong số (dùng cho 3 người, 3 vật trở lên)", "f": [], "e": []}, {"c": "between...and giữa...và (chỉ dùng cho 2 người/vật)", "f": [], "e": []}]}, {"sub_title": "Lưu ý: between...and cũng còn được dùng để chỉ vị trí chính xác của một quốc gia nằm giữa những quốc gia khác cho dù là > 2", "content": [{"c": "", "f": [], "e": [{"e": "Vietnam lies between China, Laos and Cambodia."}]}, {"c": "Between còn được dùng cho các quãng cách giữa các vật và các giới hạn về mặt thời gian. Difference + between (not among)", "f": [], "e": [{"e": "What are the differences between crows, rooks, and jackdaws."}]}, {"c": "Between each + noun (-and the next) (more formal) ", "f": [], "e": [{"e": "We need 2 meters between each window."}, {"e": "There seems to be less and less time between each birthday (and the next)."}]}, {"c": "Devide + between (not among)", "f": [], "e": [{"e": "He devided his money between his wife, his daughter, and his sister."}]}, {"c": "Share + between/among", "f": [], "e": [{"e": "He shared the food between/among all my friend."}]}]}, {"sub_title": "", "content": [{"c": "consecutive (Adj) liên tục (không có tính đứt quãng)", "f": [], "e": []}, {"c": "successive (Adj) liên tục (có tính cách quãng)", "f": [], "e": []}, {"c": "emigrant (N) người di cư, (V) -> emigrate from", "f": [], "e": []}, {"c": "immigrant (N) người nhập cư; (V) immigrate into", "f": [], "e": []}, {"c": "formerly (Adv) trước kia", "f": [], "e": []}, {"c": "formally (Adv) chỉnh tề (ăn mặc); (Adv) chính thức", "f": [], "e": []}, {"c": "historic (Adj) nổi tiếng, quan trọng trong lịch sử", "f": [], "e": [{"e": "The historic spot on which the early English settlers landed in North America (Adj mang tính lịch sử)."}]}, {"c": "historic times", "f": [], "e": []}, {"c": "historical (Adj) thuộc về lịch sử", "f": [], "e": []}, {"c": "Historical reseach, historical magazine (Adj) có thật trong lịch sử", "f": [], "e": []}, {"c": "Historical people, historical events", "f": [], "e": []}, {"c": "hepless (Adj) vô vọng, tuyệt vọng", "f": [], "e": []}, {"c": "useless (Adj) vô dụng", "f": [], "e": []}, {"c": "imaginary = (Adj) không có thật, tưởng tượng", "f": [], "e": []}, {"c": "imaginative = (Adj) phong phú, bay bổng về trí tưởng tượng", "f": [], "e": []}]}, {"sub_title": "", "content": [{"c": "Classic (adj) chất lượng cao: a classic novel (một cuốn tiểu thuyết hay); a classic football match (một trận bóng đá hay). đặc thù/đặc trưng/tiêu biểu: a classic example (một ví dụ tiêu biểu, điển hình).", "f": [], "e": []}, {"c": "Classic (noun): văn nghệ sĩ, tác phẩm lưu danh. This novel may well become a classic (Tác phẩm này có thể được lưu danh).", "f": [], "e": []}, {"c": "Classical: cổ điển, kinh điển.", "f": [], "e": []}, {"c": "Politic: nhận thức đúng/ khôn ngoan/ trang trọng.", "f": [], "e": [{"e": "I don’t think it would be politic to ask for loan just now. (Tôi cho rằng sẽ không là khôn ngoan nếu hỏi vay một khoản ngay lúc này.)"}]}, {"c": "Political: thuộc về chính trị.", "f": [], "e": [{"e": "A political career (một sự nghiệp chính trị)."}]}, {"c": "Continual: liên tục lặp đi lặp lại (hành động có tính cách quãng)", "f": [], "e": [{"e": "Please stop your continual questions (Xin hãy thôi hỏi lặp đi lặp lại mãi như thế)."}]}, {"c": "Continous: liên miên/suốt (hành động không có tính cách quãng)", "f": [], "e": [{"e": "A continous flow of traffic (Dòng xe cộ chạy liên miên bất tận)."}]}, {"c": "As (liên từ) = Như + Subject + verb.", "f": [], "e": [{"e": "When in Roma, do as Romans do (Nhập gia tùy tục)."}]}, {"c": "Like (tính từ dùng như một giới từ) + noun/noun phrase", "f": [], "e": [{"e": "He fought like a mad man (Anh ta chiến đấu như điên như dại)."}]}, {"c": "Alike (adj): giống nhau, tương tự", "f": [], "e": [{"e": "Although they are brother, they don’t look alike."}]}, {"c": "Alike (adverb): như nhau", "f": [], "e": [{"e": "The climate here is always hot, summer and winter alike."}]}, {"c": "As: như/ với tư cách là (dùng trong so sánh khi bản thân chủ ngữ có chức năng như vật/người được so sánh)", "f": [], "e": [{"e": "Let me speak to you as a father (Hãy để tôi nói với cậu như một người cha)"}]}, {"c": "Like: như là (dùng trong so sánh khi bản thân chủ ngữ và cái/người so sánh không phải là một hoặc không có chức năng đồng nhất)", "f": [], "e": [{"e": "Let me speak to you like a man above (Hãy để tôi nói với anh như một người bề trên)."}]}, {"c": "Before: trước đây/trước đó (dùng khi so sánh một thứ với tất cả các thứ khác cùng loại)", "f": [], "e": [{"e": "She has never seen such a beautiful picture before (Cô ta chưa bao giờ nhìn thấy một bức tranh đẹp như thế trước đây)."}]}]}, {"sub_title": "", "content": [{"c": "Before: Trước (chỉ một sự việc xảy ra trước một sự việc khác trong quá khứ, thường dùng với Past Perfect)", "f": [], "e": [{"e": "He lived in France in 1912, he had lived in England 4 years before."}]}, {"c": "Ago: trước (tính từ hiện tại trở ngược về quá khứ, thường dùng với Simple Past) ", "f": [], "e": [{"e": "I went to England 3 years ago."}]}, {"c": "Certain: chắc chắn (biết sự thực)", "f": [], "e": [{"e": "Certainly/ I’m certain that he didn’t steal it (Tôi chắc chắn rằng hắn ta không lấy cái đó)."}]}, {"c": "Sure: tin rằng (không biết chắc, nói theo cảm nhận, nghĩa là yếu hơn certain) ", "f": [], "e": [{"e": "Surely/ I am sure that he did not steal it (Tôi tin rằng hắn không lấy thứ đó)."}]}, {"c": "Indeed: Very+indeed (sau một tính từ hoặc một phó từ) ", "f": [], "e": [{"e": "Thank you very much indeed."}, {"e": "I was very pleased indeed to hear from you."}]}, {"c": "Indeed dùng sau to be hoặc một trợ động từ nhằm xác nhận hoặc nhấn mạnh cho sự đồng ý (thường dùng trong câu trả lời ngắn).", "f": [], "e": [{"e": "It is cold / - It is indeed."}, {"e": "Henny made a fool of himself / - He did indeed."}]}, {"c": "Ill (British English) = Sick (American English) = ốm", "f": [], "e": [{"e": "George didn’t come in last week because he was ill (=he was sick)"}]}, {"c": "Sick + Noun = ốm yếu/ bệnh tật", "f": [], "e": [{"e": "He spent 20 years looking after his sick father (Người cha bệnh tật)"}]}, {"c": "Be sick = Fell sick = Nôn/ buồn nôn/ say (tàu, xe...)", "f": [], "e": [{"e": "I was sick 3 times in the night  (tôi nôn 3 lần trong đêm)"}, {"e": "I feel sick. Where’s the bath room? (tôi thấy buồn nôn, phòng tắm ở đâu?) She is never sea-sick (Cô ấy chẳng bao giờ say sóng cả)"}]}, {"c": "Welcome (adjective) = được mong đợi/ được chờ đợi từ lâu/ thú vị A welcome guest (Khách quí/ khách bấy lâu mong đợi)", "f": [], "e": [{"e": "A welcome gift (Món quà thú vị được chờ đợi từ lâu)"}]}, {"c": "Welcome to + noun = Có quyền, được phép sử dụng.", "f": [], "e": [{"e": "You are welcome to any book in my library (Anh có quyền lấy bất kỳ quyển sách nào trong thư viện của tôi)"}]}, {"c": "Welcoming (phân từ 1 cấu tạo từ động từ welcome dùng làm tính từ) Chào đón/ đón tiếp ân cần This country have given me a welcoming feeling. (Xứ sở này đã dành cho tôi một tình cảm chào đón ân cần) Hoan nghênh/ Tán đồng (ý kiến)", "f": [], "e": []}, {"c": "To show a welcoming idea (Bộc lộ một ý kiến tán đồng)", "f": [], "e": []}, {"c": "Be certain/ sure of + verb-ing: chắc chắn là (đề cập đến tình cảm của người đang được nói đến)", "f": [], "e": [{"e": "Before the game she felt certain of winning, but after a few minutes she realized it wasn’t going to be easy."}, {"e": "You seem very sure of passing the exam, I hope you are right."}]}, {"c": "Be certain/ sure + to + verb: chắc chắn sẽ phải (đề cập đến tình cảm của chính người nói hoặc viết câu đó):", "f": [], "e": [{"e": "The repairs are certain to cost more than you think."}, {"e": "Elaine is sure to win-the other girl hasn’t got a chance."}]}, {"c": "Be interested + to + verb: Thấy thích khi...:", "f": [], "e": [{"e": "I’m interested to read in the paper that scientists have found out how to talk to whales. (Tôi thấy thích/ thú vị khi...)"}]}, {"c": "Be interested in + verb-ing/ Be interested + to + verb:Muốn biết/ muốn phát hiện ra/ muốn tìm ra...: ", "f": [], "e": [{"e": "I’m interested in finding out/ to find out what she did with all that money. (Tôi muốn biết cô ta đã làm gì với ngần ấy tiền)."}]}, {"c": "Be interested in + verb-ing: Thấy thích/ thích/ muốn...", "f": [], "e": [{"e": "I’m interested in learning higher education in the U.S."}]}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (168, '4', 'Cách sử dụng giới từ', '{}', 'Cách sử dụng giới từ, Cách sử dụng giới từ', '{}', '[{"sub_title": "During = trong suốt (hoạt động diễn ra liên tục)", "content": []}, {"sub_title": "From = từ >< to = đến", "content": [{"c": "From ... to ...= từ ... đến... (dùng cho thời gian và nơi chốn) From time to time = đôi khi, thỉnh thoảng", "f": [], "e": []}]}, {"sub_title": "Out of=ra khỏi><into=vào trong Out of + noun = hết, không còn Out of town = đi vắng", "content": [{"c": "Out of date=cũ, lạc hậu >< up to date = mới, cập nhật Out of work = thất nghiệp, mất việc", "f": [], "e": []}, {"c": "Out of the question = không thể", "f": [], "e": []}, {"c": "Out of order = hỏng, không hoạt động", "f": [], "e": []}]}, {"sub_title": "By:", "content": [{"c": "động từ chỉ chuyển động + by = đi ngang qua (walk by the library)", "f": [], "e": []}, {"c": "động từ tĩnh + by = ở gần (your books are by the window)", "f": [], "e": []}, {"c": "by + thời gian cụ thể = trước lúc, cho đến lúc (hành động cho đến lúc đó phải xảy ra) by + phương tiện giao thông = đi bằng", "f": [], "e": []}, {"c": "by then = cho đến lúc đó (dùng cho cả QK và TL) by way of= theo đường... = via", "f": [], "e": []}, {"c": "by the way = một cách tình cờ, ngẫu nhiên by the way = by the by = nhân đây, nhân tiện", "f": [], "e": []}, {"c": "by far + so sánh (thường là so sánh bậc nhất)=>dùng để nhấn mạnh by accident = by mistake = tình cờ, ngẫu nhiên >< on purose", "f": [], "e": []}]}, {"sub_title": "In = bên trong In + month/year", "content": [{"c": "In time for = In good time for = Đúng giờ (thường kịp làm gì, hơi sớm hơn giờ đã định một chút) In the street = dưới lòng đường", "f": [], "e": []}, {"c": "In the morning/ afternoon/ evening", "f": [], "e": []}, {"c": "In the past/future = trước kia, trong quá khứ/ trong tương lai In future = from now on = từ nay trở đi", "f": [], "e": []}, {"c": "In the begining/ end = at first/ last = thoạt đầu/ rốt cuộc In the way = đỗ ngang lối, chắn lối", "f": [], "e": []}, {"c": "Once in a while = đôi khi, thỉnh thoảng", "f": [], "e": []}, {"c": "In no time at all = trong nháy mắt, một thoáng In the mean time = meanwhile = cùng lúc", "f": [], "e": []}, {"c": "In the middle of (địa điểm)= ở giữa In the army/ airforce/ navy", "f": [], "e": []}, {"c": "In + the + STT + row = hàng thứ...", "f": [], "e": []}, {"c": "In the event that = trong trường hợp mà In case = để phòng khi, ngộ nhỡ", "f": [], "e": []}, {"c": "Get/ be in touch/ contact with Sb = liên lạc, tiếp xúc với ai", "f": [], "e": []}]}, {"sub_title": "On = trên bề mặt:", "content": [{"c": "On + thứ trong tuần/ ngày trong tháng", "f": [], "e": []}, {"c": "On + a/the + phương tiện giao thông = trên chuyến/ đã lên chuyến... On + phố = địa chỉ... (như B.E : in + phố)", "f": [], "e": []}, {"c": "On the + STT + floor = ở tầng thứ...", "f": [], "e": []}, {"c": "On time = vừa đúng giờ (bất chấp điều kiện bên ngoài, nghĩa mạnh hơn in time) On the corner of = ở góc phố (giữa hai phố)", "f": [], "e": []}]}, {"sub_title": "Chú ý:", "content": [{"c": "In the corner = ở góc trong", "f": [], "e": []}, {"c": "At the corner = ở góc ngoài/ tại góc phố On the sidewalk = pavement = trên vỉa hè Chú ý:", "f": [], "e": []}, {"c": "On the pavement (A.E.)= trên mặt đường nhựa (Don’t brake quickly on the pavement or you can slice into another car) ", "f": [], "e": []}, {"c": "On the way to: trên đường đến >< on the way back to: trên đường trở về", "f": [], "e": []}, {"c": "On the right/left", "f": [], "e": []}, {"c": "On T.V./ on the radio", "f": [], "e": []}, {"c": "On the phone/ telephone = gọi điện thoại, nói chuyện điện thoại On the phone = nhà có mắc điện thoại (Are you on the phone?) On the whole= nói chung, về đại thể", "f": [], "e": []}, {"c": "On the other hand = tuy nhiên= however Chú ý:", "f": [], "e": []}, {"c": "On the one hand = một mặt thì on the other hand = mặt khác thì (On the one hand, we must learn the basic grammar, and on the other hand, we must combine it with listening comprehension)", "f": [], "e": []}, {"c": "on sale = for sale = có bán, để bán", "f": [], "e": []}, {"c": "on sale (A.E.)= bán hạ giá = at a discount (B.E) on foot = đi bộ", "f": [], "e": []}]}, {"sub_title": "At = ở tại At + số nhà", "content": [{"c": "At + thời gian cụ thể At home/ school/ work", "f": [], "e": []}, {"c": "At night/noon (A.E : at noon = at twelve = giữa trưa (she was invited to the party at noon, but she was 15 minutes late))", "f": [], "e": []}, {"c": "At least = chí ít, tối thiểu >< at most = tối đa At once =ngay lập tức", "f": [], "e": []}, {"c": "At present/ the moment = now", "f": [], "e": []}]}, {"sub_title": "Chú ý: 2 thành ngữ trên tương đương với presently nhưng presently se khác nhau về nghĩa nếu nó đứng ở các vị trí khác nhau trong câu:", "content": [{"c": "Sentence + presently (= soon): ngay tức thì ( She will be here presently/soon)", "f": [], "e": []}, {"c": "Presently + sentence (= Afterward/ and then) : ngay sau đó (Presently, I heard her leave the room)  S + to be + presently + Ving = at present/ at the moment ( He is presently working toward his Ph.D. degree)", "f": [], "e": []}, {"c": "At times = đôi khi, thỉnh thoảng", "f": [], "e": []}, {"c": "At first = thoạt đầu >< at last = cuối cùng", "f": [], "e": []}, {"c": "At the beginning of / at the end of... = ở đầu/ ở cuối (dùng cho thời gian và địa điểm). At + tên các ngày lễ : at Christmas, at Thanks Giving...", "f": [], "e": []}, {"c": "Nhưng on + tên các ngày lễ + day = on Christmas day ...", "f": [], "e": []}, {"c": "Trong dạng informal E., on trước các thứ trong tuần đôi khi bị lược bỏ: She is going to see her boss (on) Sun. morning.", "f": [], "e": []}, {"c": "At/in/on thường được không dùng trong các thành ngữ chỉ thời gian khi có mặt: next, last, this, that, one, any, each, every, some, all", "f": [], "e": []}, {"c": "At + địa điểm : at the center of the building", "f": [], "e": []}, {"c": "At + những địa điểm lớn (khi xem nó như một nơi trung chuyển hoặc gặp gỡ): The plane stopped 1 hour at Washington D.C. before continuing on to Atlanta.", "f": [], "e": []}, {"c": "At + tên các toà nhà lớn (khi xem như 1 hành động sẽ xảy ra ở đó chứ không đề cập đến toà nhà) : There is a good movie at the Center Theater.", "f": [], "e": []}, {"c": "At + tên riêng các tổ chức: She works at Legal & General Insurence.", "f": [], "e": []}, {"c": "At + tên riêng nơi các trường sở hoặc khu vực đại học: She is studying at the London school of Economics.", "f": [], "e": []}, {"c": "At + tên các hoạt động qui tụ thành nhóm: at a party/ lecture...", "f": [], "e": []}]}, {"sub_title": "Một số các thành ngữ dùng với giới từ On the beach: trên bờ biển", "content": [{"c": "Along the beach: dọc theo bờ biển", "f": [], "e": []}, {"c": "In place of = Instead of: thay cho, thay vì.", "f": [], "e": []}, {"c": "For the most part: chính là, chủ yếu là = mainly.", "f": [], "e": []}, {"c": "In hope of + V-ing = Hoping to + V = Hoping that + sentence = với hi vọng là. off and on: dai dẳng, tái hồi", "f": [], "e": []}, {"c": "all of a sudden= suddenly = bỗng nhiên for good = forever: vĩnh viễn, mãi mãi.", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (169, '4', 'Một số ngữ động từ thường gặp', '{}', 'Một số ngữ động từ thường gặp, Một số ngữ động từ thường gặp', '{}', '[{"sub_title": "Đó là những động từ kết hợp với 1, 2 hoặc đôi khi 3 giới từ, khi kết hợp ở dạng như vậy ngữ nghĩa của chúng thay đổi hẳn so với nghĩa ban đầu.", "content": [{"c": "To break off: chấm dứt, cắt đứt, đoạn tuyệt.", "f": [], "e": []}, {"c": "To bring up: nêu ra, đưa lên một vấn đề", "f": [], "e": []}, {"c": "To call on: yêu cầu / đến thăm", "f": [], "e": []}, {"c": "To care for: thích / trông nom, săn sóc (look after)", "f": [], "e": []}, {"c": "To check out (of/from) a library: mượn sách ở thư viện về", "f": [], "e": []}, {"c": "To check out: điều tra, xem xét.", "f": [], "e": []}, {"c": "To check out (of): làm thủ tục để ra (khách sạn, sân bay) <> check in.", "f": [], "e": []}, {"c": "To check (up) on: điều tra, xem xét.", "f": [], "e": []}, {"c": "To close in (on): tiến lại gần, chạy lại gần", "f": [], "e": []}, {"c": "To come along with: đi cùng với", "f": [], "e": []}, {"c": "To count on = depend on = rely on", "f": [], "e": []}, {"c": "To come down with: mắc phải một căn bệnh", "f": [], "e": []}, {"c": "Do away with = get rid of: tống khứ, loại bỏ, trừ khử", "f": [], "e": []}, {"c": "To daw up = to draft: soạn thảo (một kế hoạch, một hợp đồng)", "f": [], "e": []}, {"c": "To drop out of = to withdraw from: bỏ (đặc biệt là bỏ học giữa chừng)", "f": [], "e": []}, {"c": "To figure out: Hình dung ra được, hiểu được.", "f": [], "e": []}, {"c": "To find out: khám phá ra, phát hiện ra.", "f": [], "e": []}, {"c": "To get by: Lần hồi qua ngày, sống sót qua được", "f": [], "e": []}, {"c": "To get through with: kết thúc", "f": [], "e": []}, {"c": "To get through to: thông tin được cho ai, gọi được cho (điện thoại), tìm cách làm cho hiểu", "f": [], "e": []}, {"c": "To get up: dậy/ tổ chức.", "f": [], "e": []}, {"c": "To give up: bỏ, từ bỏ", "f": [], "e": []}, {"c": "To go along with: đồng ý với", "f": [], "e": []}, {"c": "To hold on to: vẫn giữ vững, duy trì", "f": [], "e": []}, {"c": "To hold up: cướp / vẫn giữ vững, vẫn duy trì, vẫn sống bình thường, vẫn dùng được (bất chấp sức ép bên ngoài hoặc sử dụng lâu)", "f": [], "e": []}, {"c": "To keep on doing smt: vẫn tiếp tục không ngừng làm gì", "f": [], "e": []}, {"c": "To look after: trông nom, săn sóc", "f": [], "e": []}, {"c": "To look into: điều tra, xem xét", "f": [], "e": []}, {"c": "To pass out = to faint: ngất (nội động từ, không dùng bị động)", "f": [], "e": []}, {"c": "To pick out: chọn ra, lựa ra, nhặt ra", "f": [], "e": []}, {"c": "To point out: chỉ ra, vạch ra", "f": [], "e": []}, {"c": "To put off: trì hoãn, đình hoãn", "f": [], "e": []}, {"c": "To run across: khám phá, phát hiện ra (tình cờ)", "f": [], "e": []}, {"c": "To run into sb: gặp ai bất ngờ", "f": [], "e": []}, {"c": "To see about to: lo lắng, săn sóc, chạy vạy", "f": [], "e": []}, {"c": "To take off: cất cánh <> to land", "f": [], "e": []}, {"c": "To take over for: thay thế cho", "f": [], "e": []}, {"c": "to talk over: bàn soạn, thảo luận về", "f": [], "e": []}, {"c": "to try out: thử nghiệm, dùng thử (sản phẩm)", "f": [], "e": []}, {"c": "to try out for: thử vai, thử giọng (1 vở kịch, buổi biểu diễn)", "f": [], "e": []}, {"c": "To turn in: giao nộp, đệ trình / đi ngủ", "f": [], "e": []}, {"c": "To watch out for: cảnh giác, để mắt, trông chừng (cả nghĩa đen lẫn nghĩa bóng)", "f": [], "e": []}]}]');
INSERT INTO grammars (id, level, title, tag, grammar_key, related, contents) VALUES (170, '4', 'List of irregular verbs in english', '{"Parts of speech, verbs"}', 'Parts of speech, verbs, List of irregular verbs in english', '{}', '[{"sub_title": "Verb -> Simple past -> Past Participle", "content": [{"c": "", "f": [], "e": [{"e": "abide -> abode/abided -> abode/abided/(rare) abidden"}, {"e": "alight -> alit/alighted -> alit/alighted"}, {"e": "arise -> arose -> arisen"}, {"e": "awake -> awoke -> awoken"}, {"e": "be -> was/were -> been"}, {"e": "bear -> bore -> born"}, {"e": "beat -> beat -> beat/beaten"}, {"e": "become -> became -> become"}, {"e": "begin -> began -> begun"}, {"e": "behold -> beheld -> beheld"}, {"e": "bend -> bent -> bent"}, {"e": "beset -> beset -> beset"}, {"e": "bet -> bet -> bet"}, {"e": "bid -> bid/bade -> bid/bidden"}, {"e": "bind -> bound -> bound"}, {"e": "bite -> bit -> bitten"}, {"e": "bleed -> bled -> bled"}, {"e": "blow -> blew -> blown"}, {"e": "break -> broke -> broken"}, {"e": "breed -> bred -> bred"}, {"e": "bring -> brought -> brought"}, {"e": "broadcast -> broadcast -> broadcast"}, {"e": "build -> built -> built"}, {"e": "burn -> burnt/burned -> burnt/burned"}, {"e": "burst -> burst -> burst"}, {"e": "buy -> bought -> bought"}, {"e": "cast -> cast -> cast"}, {"e": "catch -> caught -> caught"}, {"e": "choose -> chose -> chosen"}, {"e": "cling -> clung -> clung"}, {"e": "clothe -> clad/clothed -> clad/clothed"}, {"e": "come -> came -> come"}, {"e": "cost -> cost -> cost"}, {"e": "creep -> crept -> crept"}, {"e": "cut -> cut -> cut"}, {"e": "deal -> dealt -> dealt"}, {"e": "dig -> dug -> dug"}, {"e": "dive -> dove/dived -> dove/dived"}, {"e": "do -> did -> done"}, {"e": "draw -> drew -> drawn"}, {"e": "dream -> dreamt/dreamed -> dreamt/dreamed"}, {"e": "drink -> drank -> drunk"}, {"e": "drive -> drove -> driven"}, {"e": "dwell -> dwelt/dwelled -> dwelt/dwelled"}, {"e": "eat -> ate -> eaten"}, {"e": "fall -> fell -> fallen"}, {"e": "fee