INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (18, 'According to the speaker, why do some people dislike a construction project?', 'https://migiitoeic.eupgroup.net/uploads/audio_ETS/bc58c16c2bb3a3585df9e7f407773248.mp3', NULL, '{"Because it caused a power outage","Because it costs too much","Because roads have been closed","Because of the loud noise"}', 'Because it costs too much', 'Theo người nói, tại sao một số người không thích dự án xây dựng?
