INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (18, 'What does the speaker thank the listeners for?', 'https://migiitoeic.eupgroup.net/uploads/audio_ETS/f31c61ef61ab611c9f4e62eb9c55da8a.mp3', NULL, '{"Reorganizing some files","Cleaning a work area","Working on a Saturday","Attending a training"}', 'Working on a Saturday', 'Người nói cảm ơn người nghe vì điều gì?
