INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (18, 'In which division do the listeners most likely work?', 'https://migiitoeic.eupgroup.net/uploads/audio_ETS/f31c61ef61ab611c9f4e62eb9c55da8a.mp3', NULL, '{"Shipping and Receiving","Maintenance","Sales and Marketing","Accounting"}', 'Shipping and Receiving', 'Trong bộ phận nào thì người nghe có khả năng làm việc nhất?
