INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (18, 'What event is being described?', 'https://migiitoeic.eupgroup.net/uploads/audio_ETS/5a62ca6aa5b70d62656e449137fecf4a.mp3', 'https://migiitoeic.eupgroup.net/uploads/image_ETS/e59704a24578cd6afcc3f58c8487ed4a.png', '{"A sports competition","A government ceremony","A music festival","A cooking contest"}', 'A cooking contest', 'Sự kiện nào đang được mô tả?
