INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (18, 'According to the speaker, what can the listeners find on a Web site?', 'https://migiitoeic.eupgroup.net/uploads/audio_ETS/5a62ca6aa5b70d62656e449137fecf4a.mp3', 'https://migiitoeic.eupgroup.net/uploads/image_ETS/e59704a24578cd6afcc3f58c8487ed4a.png', '{"A city map","A list of vendors","A demonstration video","An entry form"}', 'An entry form', 'Theo diễn giả, người nghe có thể tìm thấy gì trên trang web?
