INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (18, 'What is the purpose of the call ?', 'https://migiitoeic.eupgroup.net/uploads/audio_ETS/43ff399af37a38ff11daf519f0fe5c1f.mp3', 'https://migiitoeic.eupgroup.net/uploads/image_ETS/272d6de8b609dbfeb6c7b4ee7714876b.png', '{"To confirm a deadline","To explain a company policy","To make a job offer","To discuss a new product"}', 'To make a job offer', 'Mục đích của cuộc gọi là gì?
