INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (18, 'Look at the graphic. Who is the speaker calling?', 'https://migiitoeic.eupgroup.net/uploads/audio_ETS/43ff399af37a38ff11daf519f0fe5c1f.mp3', 'https://migiitoeic.eupgroup.net/uploads/image_ETS/272d6de8b609dbfeb6c7b4ee7714876b.png', '{"Carla Wynn","Jae-Ho Kim","Kaori Aoki","Alex Lehmann"}', 'Carla Wynn', 'Nhìn vào hình ảnh. Người nói đang gọi cho ai?
