INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (18, 'What does the speaker ask the listener to do?', 'https://migiitoeic.eupgroup.net/uploads/audio_ETS/43ff399af37a38ff11daf519f0fe5c1f.mp3', 'https://migiitoeic.eupgroup.net/uploads/image_ETS/272d6de8b609dbfeb6c7b4ee7714876b.png', '{"Check a catalog","Send fee information","Submit a travel itinerary","Update a conference schedule"}', 'Send fee information', 'Người nói yêu cầu người nghe làm gì?
