INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (19, 'Lasner Electronics'' staff have extensive ____ of current hardware systems', NULL, NULL, '{"know","known","knowledge","knowledgeable"}', 'knowledge', 'Nhân viên của Lasner Electronics có kiến thức sâu rộng về các hệ thống phần cứng hiện tại.
