INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (19, 'Ms. Pham requested a refund ____ the coffeemaker she received was damaged', NULL, NULL, '{"despite","why","concerning","because"}', 'because', 'Cô Phạm yêu cầu hoàn tiền vì máy pha cà phê cô nhận được bị hỏng
