INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (19, 'The Physical Therapy Association is committed to keeping costs ____ for its certification programs', NULL, NULL, '{"affordable","permitted","cutting","necessary"}', 'affordable', 'Hiệp hội Vật lý trị liệu cam kết giữ chi phí hợp lý cho các chương trình chứng nhận của mình
