INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (19, 'Mr. Brennel ____ positions in various areas of the company before he became president', NULL, NULL, '{"occupation","occupational","occupying","occupied"}', 'occupied', 'Ông Brennel đã đảm nhiệm các vị trí trong nhiều lĩnh vực khác nhau của công ty trước khi trở thành chủ tịch
