INSERT INTO questions (content_id, title, media_url, image_url, possible_answers, true_answer, explanation, keywords) VALUES (32, 'Look at the graphic. Which day is the event being held?', 'https://migiitoeic.eupgroup.net/uploads/audio_ETS/5a62ca6aa5b70d62656e449137fecf4a.mp3', 'https://migiitoeic.eupgroup.net/uploads/image_ETS/e59704a24578cd6afcc3f58c8487ed4a.png', '{"Saturday","Sunday","Monday","Tuesday"}', 'Sunday', 'Nhìn vào hình ảnh. Ngày nào sự kiện được tổ chức?
